  1→"use client";
  2→
  3→import { useRef, useMemo, useEffect, useState } from "react";
  4→import { Canvas, useFrame } from "@react-three/fiber";
  5→import {
  6→  Float,
  7→  Line,
  8→  Environment,
  9→  MeshTransmissionMaterial,
 10→  RoundedBox,
 11→  AccumulativeShadows,
 12→  RandomizedLight,
 13→} from "@react-three/drei";
 14→import * as THREE from "three";
 15→
 16→// ── Reduced motion detection ────────────────────────────────────────────────
 17→function useReducedMotion() {
 18→  const [reduced, setReduced] = useState(false);
 19→
 20→  useEffect(() => {
 21→    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
 22→    setReduced(mq.matches);
 23→    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
 24→    mq.addEventListener("change", handler);
 25→    return () => mq.removeEventListener("change", handler);
 26→  }, []);
 27→
 28→  return reduced;
 29→}
 30→
 31→// ── Canvas2D page texture generator ─────────────────────────────────────────
 32→// Page content per variant — title, subtitle, body paragraphs
 33→const PAGE_CONTENT: { title: string; subtitle: string; body: string[] }[] = [
 34→  {
 35→    title: "Introduction",
 36→    subtitle: "Background and Rationale",
 37→    body: [
 38→      "Chronic kidney disease represents a growing public health concern in the Indian subcontinent, with prevalence estimates ranging from 8.2% to 17.4% across various population-based studies.",
 39→      "The early detection of renal impairment through biomarker screening has demonstrated significant potential for improving patient outcomes and reducing long-term morbidity.",
 40→      "This study investigates the diagnostic accuracy of novel urinary biomarkers in comparison with established serum creatinine-based estimation methods.",
 41→    ],
 42→  },
 43→  {
 44→    title: "Materials and Methods",
 45→    subtitle: "Study Design and Protocol",
 46→    body: [
 47→      "A prospective observational study was conducted at the Department of Nephrology, Institute of Post-Graduate Medical Education, Kolkata, over a period of eighteen months from January 2024 to June 2025.",
 48→      "Consecutive patients presenting with clinical features suggestive of early renal impairment were enrolled following informed written consent. The study protocol was approved by the Institutional Ethics Committee.",
 49→      "Serum and urine samples were collected under standardised conditions and analysed using automated biochemistry analysers with appropriate internal quality controls.",
 50→    ],
 51→  },
 52→  {
 53→    title: "Review of Literature",
 54→    subtitle: "Current Evidence and Gaps",
 55→    body: [
 56→      "A comprehensive literature search was performed across PubMed, Scopus, and the Cochrane Library databases. Studies published between 2015 and 2025 were included following PRISMA guidelines.",
 57→      "Kumar et al. (2023) reported a sensitivity of 89.4% for cystatin C in detecting early glomerular filtration rate decline, significantly superior to conventional markers.",
 58→      "Despite promising results, heterogeneity in study populations and methodologies necessitates further investigation in the Indian clinical context with standardised protocols.",
 59→    ],
 60→  },
 61→];
 62→
 63→function wrapText(ctx: CanvasRenderingContext2D, text: string, maxWidth: number): string[] {
 64→  const words = text.split(" ");
 65→  const lines: string[] = [];
 66→  let currentLine = words[0];
 67→
 68→  for (let i = 1; i < words.length; i++) {
 69→    const testLine = currentLine + " " + words[i];
 70→    if (ctx.measureText(testLine).width <= maxWidth) {
 71→      currentLine = testLine;
 72→    } else {
 73→      lines.push(currentLine);
 74→      currentLine = words[i];
 75→    }
 76→  }
 77→  lines.push(currentLine);
 78→  return lines;
 79→}
 80→
 81→function createPageTexture(variant: 0 | 1 | 2): THREE.CanvasTexture {
 82→  const w = 1024;
 83→  const h = 1440;
 84→  const canvas = document.createElement("canvas");
 85→  canvas.width = w;
 86→  canvas.height = h;
 87→  const ctx = canvas.getContext("2d")!;
 88→
 89→  // Clean white paper background
 90→  ctx.fillStyle = "#FEFEFE";
 91→  ctx.fillRect(0, 0, w, h);
 92→
 93→  // Subtle grain noise
 94→  for (let i = 0; i < 800; i++) {
 95→    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
 96→    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
 97→    ctx.fillStyle = `rgba(0,0,0,${0.008 + (i % 3) * 0.004})`;
 98→    ctx.fillRect(gx, gy, 2, 2);
 99→  }
100→
101→  const margin = 88;
102→  const contentW = w - margin * 2;
103→  const content = PAGE_CONTENT[variant];
104→
105→  // Chapter title — large serif
106→  ctx.fillStyle = "#2A2A2A";
107→  ctx.font = "bold 42px Georgia, 'Times New Roman', serif";
108→  ctx.fillText(content.title, margin, 120);
109→
110→  // Subtitle
111→  ctx.fillStyle = "#4A4A4A";
112→  ctx.font = "italic 28px Georgia, 'Times New Roman', serif";
113→  ctx.fillText(content.subtitle, margin, 165);
114→
115→  // Separator line
116→  ctx.fillStyle = "#C0C0C0";
117→  ctx.fillRect(margin, 185, contentW * 0.3, 1.5);
118→
119→  // Body paragraphs — sans-serif
120→  ctx.font = "22px 'Helvetica Neue', Helvetica, Arial, sans-serif";
121→  ctx.fillStyle = "#505050";
122→  const lineHeight = 32;
123→  let y = 230;
124→
125→  for (const paragraph of content.body) {
126→    const wrappedLines = wrapText(ctx, paragraph, contentW);
127→    for (const line of wrappedLines) {
128→      ctx.fillText(line, margin, y);
129→      y += lineHeight;
130→    }
131→    y += 18; // paragraph gap
132→  }
133→
134→  const texture = new THREE.CanvasTexture(canvas);
135→  texture.minFilter = THREE.LinearFilter;
136→  texture.magFilter = THREE.LinearFilter;
137→  texture.colorSpace = THREE.SRGBColorSpace;
138→  return texture;
139→}
140→
141→function roundRect(
142→  ctx: CanvasRenderingContext2D,
143→  x: number,
144→  y: number,
145→  w: number,
146→  h: number,
147→  r: number,
148→) {
149→  ctx.beginPath();
150→  ctx.roundRect(x, y, w, h, r);
151→  ctx.fill();
152→}
153→
154→// ── Thesis Page mesh ────────────────────────────────────────────────────────
155→function ThesisPageMesh({ variant }: { variant: 0 | 1 | 2 }) {
156→  const texture = useMemo(() => createPageTexture(variant), [variant]);
157→
158→  useEffect(() => {
159→    return () => { texture.dispose(); };
160→  }, [texture]);
161→
162→  return (
163→    <group>
164→      {/* Paper body with rounded edges — plain white, gives thickness */}
165→      <RoundedBox args={[2.1, 2.97, 0.015]} radius={0.004} smoothness={4} castShadow>
166→        <meshStandardMaterial
167→          color="#FEFEFE"
168→          roughness={0.88}
169→          metalness={0}
170→        />
171→      </RoundedBox>
172→      {/* Front face — flat plane with texture, sits on front surface */}
173→      <mesh position={[0, 0, 0.0076]} castShadow>
174→        <planeGeometry args={[2.1, 2.97]} />
175→        <meshStandardMaterial
176→          map={texture}
177→          color="#FFFFFF"
178→          roughness={0.88}
179→          metalness={0}
180→          emissive="#FFFFFF"
181→          emissiveIntensity={0.015}
182→        />
183→      </mesh>
184→    </group>
185→  );
186→}
187→
188→// ── Thesis pages group — visible fan, 3 edges showing ───────────────────────
189→function ThesisPagesGroup({ reducedMotion }: { reducedMotion: boolean }) {
190→  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;
191→
192→  return (
193→    <group position={[-0.6, 0, 0]}>
194→      {/* Page A — back-left, peeks above and left */}
195→      <Wrapper speed={1.1} floatIntensity={0.15} rotationIntensity={0.03}>
196→        <group
197→          rotation={[0.04, -0.12, -0.06]}
198→          position={[-0.5, 0.35, -0.6]}
199→        >
200→          <ThesisPageMesh variant={0} />
201→        </group>
202→      </Wrapper>
203→
204→      {/* Page B — front-centre, dominant */}
205→      <Wrapper speed={0.9} floatIntensity={0.12} rotationIntensity={0.02}>
206→        <group
207→          rotation={[0.02, -0.03, 0.02]}
208→          position={[0, 0.05, 0]}
209→        >
210→          <ThesisPageMesh variant={1} />
211→        </group>
212→      </Wrapper>
213→
214→      {/* Page C — back-right, peeks right and slightly down */}
215→      <Wrapper speed={0.7} floatIntensity={0.12} rotationIntensity={0.03}>
216→        <group
217→          rotation={[-0.03, 0.14, 0.06]}
218→          position={[0.55, -0.25, -0.35]}
219→        >
220→          <ThesisPageMesh variant={2} />
221→        </group>
222→      </Wrapper>
223→    </group>
224→  );
225→}
226→
227→// ── Float / Static wrapper ──────────────────────────────────────────────────
228→function FloatWrapper({
229→  children,
230→  speed,
231→  floatIntensity,
232→  rotationIntensity,
233→}: {
234→  children: React.ReactNode;
235→  speed: number;
236→  floatIntensity: number;
237→  rotationIntensity: number;
238→}) {
239→  return (
240→    <Float speed={speed} floatIntensity={floatIntensity} rotationIntensity={rotationIntensity}>
241→      {children}
242→    </Float>
243→  );
244→}
245→
246→function StaticGroup({
247→  children,
248→}: {
249→  children: React.ReactNode;
250→  speed?: number;
251→  floatIntensity?: number;
252→  rotationIntensity?: number;
253→}) {
254→  return <group>{children}</group>;
255→}
256→
257→// ── Scatter data — 18 points with varying sizes ─────────────────────────────
258→const SCATTER_COLORS = ["#2D8C5A", "#D4644A", "#3A82B8", "#D4A030"];
259→
260→// Breathe config per colour group — frequency, phase offset, amplitude
261→const GROUP_BREATHE: Record<string, { freq: number; phase: number; amp: number }> = {
262→  [SCATTER_COLORS[0]]: { freq: 0.7, phase: 0, amp: 0.06 },
263→  [SCATTER_COLORS[1]]: { freq: 1.1, phase: 1.2, amp: 0.08 },
264→  [SCATTER_COLORS[2]]: { freq: 0.9, phase: 2.5, amp: 0.05 },
265→  [SCATTER_COLORS[3]]: { freq: 1.4, phase: 0.8, amp: 0.07 },
266→};
267→
268→const SCATTER_POINTS: {
269→  x: number;
270→  y: number;
271→  z: number;
272→  radius: number;
273→  color: string;
274→}[] = [
275→  // Large spheres — spread across Z depth
276→  { x: 0.15, y: 0.22, z: 0.85, radius: 0.12, color: SCATTER_COLORS[0] },
277→  { x: 1.65, y: 1.50, z: 0.20, radius: 0.11, color: SCATTER_COLORS[1] },
278→  { x: 0.90, y: 0.85, z: 1.10, radius: 0.10, color: SCATTER_COLORS[2] },
279→  // Medium-large
280→  { x: 0.50, y: 0.52, z: 0.10, radius: 0.09, color: SCATTER_COLORS[3] },
281→  { x: 1.30, y: 1.20, z: 0.75, radius: 0.09, color: SCATTER_COLORS[0] },
282→  { x: 1.10, y: 0.78, z: 0.40, radius: 0.085, color: SCATTER_COLORS[1] },
283→  // Medium
284→  { x: 0.35, y: 0.55, z: 1.20, radius: 0.075, color: SCATTER_COLORS[2] },
285→  { x: 0.75, y: 0.60, z: 0.05, radius: 0.07, color: SCATTER_COLORS[3] },
286→  { x: 1.45, y: 1.35, z: 0.55, radius: 0.07, color: SCATTER_COLORS[0] },
287→  { x: 1.80, y: 1.58, z: 0.90, radius: 0.075, color: SCATTER_COLORS[1] },
288→  // Small
289→  { x: 0.60, y: 0.75, z: 1.30, radius: 0.055, color: SCATTER_COLORS[2] },
290→  { x: 1.20, y: 0.92, z: 0.15, radius: 0.05, color: SCATTER_COLORS[3] },
291→  { x: 0.25, y: 0.10, z: 0.60, radius: 0.05, color: SCATTER_COLORS[0] },
292→  { x: 1.55, y: 1.10, z: 1.05, radius: 0.055, color: SCATTER_COLORS[1] },
293→  // Tiny
294→  { x: 0.45, y: 0.30, z: 0.95, radius: 0.04, color: SCATTER_COLORS[2] },
295→  { x: 0.85, y: 1.05, z: 1.25, radius: 0.04, color: SCATTER_COLORS[3] },
296→  { x: 1.70, y: 1.42, z: 0.30, radius: 0.035, color: SCATTER_COLORS[0] },
297→  { x: 1.00, y: 0.65, z: 0.70, radius: 0.038, color: SCATTER_COLORS[1] },
298→];
299→
300→// ── Scatter sphere — per-group breathe animation ────────────────────────────
301→function ScatterSphere({
302→  position,
303→  color,
304→  radius,
305→  reducedMotion,
306→}: {
307→  position: [number, number, number];
308→  color: string;
309→  radius: number;
310→  reducedMotion: boolean;
311→}) {
312→  const meshRef = useRef<THREE.Mesh>(null);
313→  const geometry = useMemo(() => new THREE.SphereGeometry(radius, 32, 32), [radius]);
314→  const breathe = GROUP_BREATHE[color] ?? { freq: 0.8, phase: 0, amp: 0.05 };
315→
316→  useFrame(({ clock }) => {
317→    if (reducedMotion || !meshRef.current) return;
318→    const t = clock.getElapsedTime();
319→    const s = 1 + Math.sin(t * breathe.freq + breathe.phase) * breathe.amp;
320→    meshRef.current.scale.setScalar(s);
321→  });
322→
323→  return (
324→    <mesh ref={meshRef} geometry={geometry} position={position} castShadow>
325→      <MeshTransmissionMaterial
326→        transmissionSampler
327→        transmission={1}
328→        thickness={radius * 5}
329→        roughness={0.01}
330→        chromaticAberration={0.04}
331→        anisotropicBlur={0.08}
332→        distortion={0.15}
333→        distortionScale={0.4}
334→        temporalDistortion={0.02}
335→        backside
336→        backsideThickness={radius * 2}
337→        color={color}
338→        toneMapped={false}
339→        opacity={0.65}
340→        transparent
341→      />
342→    </mesh>
343→  );
344→}
345→
346→// ── Axes with tick marks and arrowheads ──────────────────────────────────────
347→const AXIS_COLOR = "#C0C0C0";
348→const AXIS_LENGTH = 2.2;
349→const TICK_SIZE = 0.025;
350→const TICK_INTERVAL = 0.5;
351→const ARROW_HEIGHT = 0.1;
352→const ARROW_RADIUS = 0.025;
353→
354→function AxisArrow({
355→  position,
356→  rotation,
357→}: {
358→  position: [number, number, number];
359→  rotation: [number, number, number];
360→}) {
361→  return (
362→    <mesh position={position} rotation={rotation}>
363→      <coneGeometry args={[ARROW_RADIUS, ARROW_HEIGHT, 8]} />
364→      <meshBasicMaterial color={AXIS_COLOR} />
365→    </mesh>
366→  );
367→}
368→
369→function ScatterAxes({ reducedMotion }: { reducedMotion: boolean }) {
370→  const groupRef = useRef<THREE.Group>(null);
371→
372→  const ticks = useMemo(() => {
373→    const result: { start: [number, number, number]; end: [number, number, number] }[] = [];
374→
375→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
376→      result.push({ start: [t, -TICK_SIZE, 0], end: [t, TICK_SIZE, 0] });
377→    }
378→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
379→      result.push({ start: [-TICK_SIZE, t, 0], end: [TICK_SIZE, t, 0] });
380→    }
381→    for (let t = TICK_INTERVAL; t <= 0.6; t += TICK_INTERVAL) {
382→      result.push({ start: [0, -TICK_SIZE, t], end: [0, TICK_SIZE, t] });
383→    }
384→
385→    return result;
386→  }, []);
387→
388→  useFrame(({ clock }) => {
389→    if (reducedMotion || !groupRef.current) return;
390→    const t = clock.getElapsedTime();
391→    const s = 1 + Math.sin(t * 0.35) * 0.008;
392→    groupRef.current.scale.setScalar(s);
393→  });
394→
395→  return (
396→    <group ref={groupRef}>
397→      {/* Axis lines */}
398→      <Line points={[[0, 0, 0], [AXIS_LENGTH, 0, 0]]} color={AXIS_COLOR} lineWidth={1} />
399→      <Line points={[[0, 0, 0], [0, AXIS_LENGTH, 0]]} color={AXIS_COLOR} lineWidth={1} />
400→      <Line points={[[0, 0, 0], [0, 0, 0.6]]} color={AXIS_COLOR} lineWidth={1} />
401→
402→      {/* Arrowheads — cones at axis tips */}
403→      <AxisArrow position={[AXIS_LENGTH, 0, 0]} rotation={[0, 0, -Math.PI / 2]} />
404→      <AxisArrow position={[0, AXIS_LENGTH, 0]} rotation={[0, 0, 0]} />
405→      <AxisArrow position={[0, 0, 0.6]} rotation={[Math.PI / 2, 0, 0]} />
406→
407→      {/* Tick marks */}
408→      {ticks.map((tick, i) => (
409→        <Line key={i} points={[tick.start, tick.end]} color={AXIS_COLOR} lineWidth={1} />
410→      ))}
411→    </group>
412→  );
413→}
414→
415→// ── Scatter plot — overlaps right edge of pages ─────────────────────────────
416→function ScatterPlotGroup({ reducedMotion }: { reducedMotion: boolean }) {
417→  const groupRef = useRef<THREE.Group>(null);
418→
419→  useFrame(({ clock }) => {
420→    if (reducedMotion || !groupRef.current) return;
421→    const t = clock.getElapsedTime();
422→    groupRef.current.position.y = -0.8 + Math.sin(t * 0.6) * 0.1;
423→  });
424→
425→  return (
426→    <group
427→      ref={groupRef}
428→      position={[0.0, -0.8, 1.5]}
429→      rotation={[0.1, -45 * (Math.PI / 180), 0.15]}
430→    >
431→      <ScatterAxes reducedMotion={reducedMotion} />
432→      {SCATTER_POINTS.map((pt, i) => (
433→        <ScatterSphere
434→          key={i}
435→          position={[pt.x, pt.y, pt.z]}
436→          color={pt.color}
437→          radius={pt.radius}
438→          reducedMotion={reducedMotion}
439→        />
440→      ))}
441→    </group>
442→  );
443→}
444→
445→// ── Procedural environment (no HDR download) ────────────────────────────────
446→function StudioEnvironment() {
447→  return (
448→    <Environment frames={1} resolution={128} background={false}>
449→      <mesh position={[0, 6, 0]} scale={30}>
450→        <planeGeometry />
451→        <meshBasicMaterial color="#FFFEF8" />
452→      </mesh>
453→      <mesh position={[0, -6, 0]} scale={30} rotation={[Math.PI, 0, 0]}>
454→        <planeGeometry />
455→        <meshBasicMaterial color="#E8E0D0" />
456→      </mesh>
457→      <mesh position={[-8, 0, 0]} scale={20} rotation={[0, Math.PI / 2, 0]}>
458→        <planeGeometry />
459→        <meshBasicMaterial color="#FFF5E8" />
460→      </mesh>
461→      <mesh position={[8, 0, 0]} scale={20} rotation={[0, -Math.PI / 2, 0]}>
462→        <planeGeometry />
463→        <meshBasicMaterial color="#E8ECF0" />
464→      </mesh>
465→      <mesh position={[0, 2, 8]} scale={15} rotation={[0, Math.PI, 0]}>
466→        <planeGeometry />
467→        <meshBasicMaterial color="#FFFFFF" />
468→      </mesh>
469→    </Environment>
470→  );
471→}
472→
473→// ── Main scene ──────────────────────────────────────────────────────────────
474→function Scene() {
475→  const reducedMotion = useReducedMotion();
476→
477→  return (
478→    <>
479→      <StudioEnvironment />
480→
481→      {/* Lighting — key/fill/rim for depth */}
482→      <ambientLight intensity={0.5} />
483→      <directionalLight position={[4, 8, 3]} intensity={1.8} />
484→      <pointLight position={[-4, 4, 3]} intensity={0.6} color="#FFF5E6" />
485→      <pointLight position={[2, -2, 5]} intensity={0.3} color="#F5F0E8" />
486→      <directionalLight position={[-3, 2, -4]} intensity={0.8} color="#E8ECF0" />
487→
488→      {/* Soft accumulated shadows — 100+ random light samples from top-right */}
489→      <AccumulativeShadows
490→        temporal
491→        frames={120}
492→        scale={18}
493→        position={[0, -2.2, 0]}
494→        opacity={0.75}
495→        color="#2A2520"
496→        alphaTest={0.3}
497→      >
498→        <RandomizedLight
499→          amount={120}
500→          radius={8}
501→          position={[4, 8, 3]}
502→          bias={0.001}
503→          mapSize={1024}
504→        />
505→        <RandomizedLight
506→          amount={40}
507→          radius={5}
508→          position={[-3, 6, 4]}
509→          bias={0.001}
510→          mapSize={512}
511→        />
512→      </AccumulativeShadows>
513→
514→      <ThesisPagesGroup reducedMotion={reducedMotion} />
515→      <ScatterPlotGroup reducedMotion={reducedMotion} />
516→    </>
517→  );
518→}
519→
520→// ── Exported component ──────────────────────────────────────────────────────
521→export function Hero3DScene() {
522→  return (
523→    <Canvas
524→      shadows
525→      camera={{ position: [0, 0.5, 7], fov: 38, near: 0.1, far: 50 }}
526→      dpr={[1, 1.5]}
527→      gl={{ antialias: true, alpha: true, powerPreference: "default" }}
528→      style={{ background: "transparent" }}
529→    >
530→      <Scene />
531→    </Canvas>
532→  );
533→}
