                      key={billing}
                      initial={{ opacity: 0, y: -10 }}
                      animate={{ opacity: 1, y: 0 }}
                      exit={{ opacity: 0, y: 10 }}
                      className="text-4xl font-bold text-[#2F2F2F]"
                    >
                      {billing === "monthly"
                        ? plan.monthlyPrice
                        : (plan.onetimePrice ?? plan.monthlyPrice)}
                    </motion.span>
