  1→import { NextRequest, NextResponse } from "next/server";
  2→import { readFile } from "fs/promises";
  3→import path from "path";
  4→import { getAuthenticatedUser } from "@/lib/api/auth";
  5→import {
  6→  unauthorised,
  7→  notFound,
  8→  internalError,
  9→  conflict,
 10→  queueFull,
 11→} from "@/lib/api/errors";
 12→import { tryAcquire, release } from "@/lib/compute/semaphore";
 13→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 14→import { generateTex } from "@/lib/latex/generate-tex";
 15→import { compileTex } from "@/lib/latex/compile";
 16→import type { Project, Compilation } from "@/lib/types/database";
 17→
 18→export async function POST(
 19→  _request: NextRequest,
 20→  { params }: { params: Promise<{ id: string }> }
 21→) {
 22→  try {
 23→    const authResult = await getAuthenticatedUser();
 24→    if (!authResult) return unauthorised();
 25→
 26→    const { id } = await params;
 27→    const supabase = createAdminSupabaseClient();
 28→
 29→    // Fetch project
 30→    const { data: project, error: projectError } = await supabase
 31→      .from("projects")
 32→      .select("*")
 33→      .eq("id", id)
 34→      .eq("user_id", authResult.user.id)
 35→      .single();
 36→
 37→    if (projectError || !project) {
 38→      return notFound("Project not found");
 39→    }
 40→
 41→    const typedProject = project as Project;
 42→
 43→    // Check for existing running compilation
 44→    const { data: runningCompilation } = await supabase
 45→      .from("compilations")
 46→      .select("id")
 47→      .eq("project_id", id)
 48→      .eq("status", "running")
 49→      .single();
 50→
 51→    if (runningCompilation) {
 52→      return conflict("A compilation is already in progress");
 53→    }
 54→
 55→    // Admission control — acquire compute slot
 56→    const slot = tryAcquire("compile", id);
 57→    if (!slot.acquired) {
 58→      if (slot.position !== undefined) {
 59→        return queueFull(Math.ceil((slot.estimatedWaitMs ?? 30000) / 1000));
 60→      }
 61→      return queueFull();
 62→    }
 63→
 64→    try {
 65→      // Create compilation record
 66→      const { data: compilation, error: compilationError } = await supabase
 67→        .from("compilations")
 68→        .insert({
 69→          project_id: id,
 70→          trigger: "manual",
 71→          status: "running",
 72→          warnings: [],
 73→          errors: [],
 74→        })
 75→        .select("*")
 76→        .single();
 77→
 78→      if (compilationError || !compilation) {
 79→        console.error("Failed to create compilation record:", compilationError);
 80→        return internalError("Failed to start compilation");
 81→      }
 82→
 83→      // Read the template
 84→      let template: string;
 85→      try {
 86→        const templatesDir = path.resolve(process.cwd(), "../../templates");
 87→        template = await readFile(path.join(templatesDir, "main.tex"), "utf-8");
 88→      } catch {
 89→        await updateCompilation(supabase, compilation.id, {
 90→          status: "failed",
 91→          errors: ["Template file not found"],
 92→        });
 93→        return internalError("LaTeX template not found");
 94→      }
 95→
 96→      // Generate populated TeX
 97→      const { tex, warnings: texWarnings } = generateTex(template, typedProject);
 98→
 99→      // Compile
100→      const isWatermark = typedProject.status === "sandbox";
101→      const result = await compileTex(tex, {
102→        projectId: id,
103→        watermark: isWatermark,
104→      });
105→
106→      // Update compilation record
107→      const allWarnings = [...texWarnings, ...result.log.warnings.map(String)];
108→
109→      if (result.success && result.pdfPath) {
110→        // Read PDF and store URL (in production this would upload to R2)
111→        // For now, store the local path reference
112→        await updateCompilation(supabase, compilation.id, {
113→          status: "completed",
