export type UniversityType = "wbuhs" | "ssuhs" | "generic";

export interface ProjectMetadata {
  candidate_name?: string;
  guide_name?: string;
  hod_name?: string;
  department?: string;
  degree?: string;
  speciality?: string;
  registration_no?: string;
