}

export type GuidelineType =
  | "CONSORT"
  | "STROBE"
  | "PRISMA"
  | "STARD"
  | "CARE";

export interface ComplianceCheck {
  id: string;
  project_id: string;
  guideline_type: GuidelineType;
  checklist_json: {
    item_id: string;
    description: string;
    status: string;
    section_ref: string | null;
