import { generateTex } from "./generate-tex";
import type { Project, Section, Citation } from "@/lib/types/database";

// ── Phase → label mapping (from main.tex template) ─────────────────────────

const PHASE_LABEL_MAP: Record<number, string> = {
  2: "chap:introduction",
  3: "chap:aims",
  4: "chap:literature",
  5: "chap:methodology",
  6: "chap:results",
  7: "chap:discussion",
  8: "chap:conclusion",
};

// ── BibTeX splitter ─────────────────────────────────────────────────────────

const BIBTEX_SEPARATOR = "---BIBTEX---";

export interface SplitResult {
  body: string;
  bib: string;
}

/**
 * Split section latex_content at the `---BIBTEX---` marker.
 * Returns the body (before marker) and bib entries (after marker).
 */
export function splitBibtex(latexContent: string): SplitResult {
  const idx = latexContent.indexOf(BIBTEX_SEPARATOR);
  if (idx === -1) {
    return { body: latexContent.trim(), bib: "" };
  }
  return {
    body: latexContent.slice(0, idx).trim(),
    bib: latexContent.slice(idx + BIBTEX_SEPARATOR.length).trim(),
  };
}

// ── Placeholder replacement ─────────────────────────────────────────────────

/**
 * Replace the `%% [[PLACEHOLDER — Phase N]]` comment block after
 * `\label{chap:xxx}` with actual section content.
 *
 * The regex captures everything between `\label{labelKey}` (+ optional
 * whitespace/comments) and the next structural delimiter.
 */
export function replacePlaceholder(
  tex: string,
  labelKey: string,
  content: string
): string {
  // Escape the label for use in regex
  const escapedLabel = labelKey.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");

  // Match from \label{chap:xxx} through placeholder comment block,
  // stopping before the next structural element.
  // The placeholder block is: optional blank lines + %% [[PLACEHOLDER ...]] + optional comment lines
  const pattern = new RegExp(
    `(\\\\label\\{${escapedLabel}\\})` + // group 1: the label itself
      `(\\s*(?:%%.*)*)`, // group 2: whitespace + comment lines (placeholder block)
    "m"
  );

  return tex.replace(pattern, `$1\n\n${content}\n`);
}

// ── BibTeX deduplication ────────────────────────────────────────────────────

/**
 * Parse `@type{key,` entries from BibTeX content, deduplicate by cite key
 * (last-write-wins so citations table entries override AI-generated ones).
 */
export function deduplicateBibEntries(bibContent: string): string {
  if (!bibContent.trim()) return "";

  // Split into individual entries by matching @type{key,
  const entryPattern = /(@\w+\{[^,]+,[\s\S]*?(?:\n\}|\n\}\s*$))/gm;

  // More robust: split on lines that start a new entry
  const entries: { key: string; entry: string }[] = [];
  const lines = bibContent.split("\n");
  let currentEntry = "";
  let currentKey = "";

  for (const line of lines) {
    const entryStart = line.match(/^@(\w+)\{([^,]+),/);
    if (entryStart) {
      // Save previous entry
      if (currentEntry && currentKey) {
        entries.push({ key: currentKey, entry: currentEntry.trim() });
      }
      currentKey = entryStart[2].trim();
      currentEntry = line + "\n";
    } else if (currentKey) {
      currentEntry += line + "\n";
    }
  }

  // Push last entry
  if (currentEntry && currentKey) {
    entries.push({ key: currentKey, entry: currentEntry.trim() });
  }

  // Deduplicate: last-write-wins
  const seen = new Map<string, string>();
  for (const { key, entry } of entries) {
    seen.set(key, entry);
  }

  return Array.from(seen.values()).join("\n\n");
}

// ── Main assembly function ──────────────────────────────────────────────────

export interface AssembleResult {
  tex: string;
  bib: string;
  warnings: string[];
}

/**
 * Assemble the full thesis .tex and .bib from template, project metadata,
 * approved/review sections, and citations.
 *
 * 1. Populate metadata via `generateTex()`
 * 2. For each phase 2–8: find the section, split body/bib, replace placeholder
 * 3. Collect BibTeX from section trailers + citations table
 * 4. Deduplicate BibTeX entries
 */
export function assembleThesisContent(
  template: string,
  project: Project,
  sections: Section[],
  citations: Citation[]
): AssembleResult {
  const warnings: string[] = [];

  // Step 1: Populate metadata
  const { tex: metadataTex, warnings: metaWarnings } = generateTex(template, project);
  warnings.push(...metaWarnings);

  let tex = metadataTex;
  const bibParts: string[] = [];

  // Step 2: For each chapter phase, inject section content
  for (const [phaseStr, labelKey] of Object.entries(PHASE_LABEL_MAP)) {
    const phaseNum = parseInt(phaseStr, 10);

    // Find the best section: prefer approved, then review
    const section = sections
      .filter((s) => s.phase_number === phaseNum)
      .sort((a, b) => {
        const priority: Record<string, number> = { approved: 0, review: 1 };
        return (priority[a.status] ?? 2) - (priority[b.status] ?? 2);
      })[0];

    if (!section || !section.latex_content) {
      warnings.push(`Phase ${phaseNum}: no approved/review content — placeholder retained`);
      continue;
    }

    const { body, bib } = splitBibtex(section.latex_content);

    if (body) {
      tex = replacePlaceholder(tex, labelKey, body);
    } else {
      warnings.push(`Phase ${phaseNum}: section exists but body is empty`);
    }

    if (bib) {
      bibParts.push(bib);
    }
  }

  // Step 3: Collect BibTeX from citations table
  for (const citation of citations) {
    if (citation.bibtex_entry?.trim()) {
      bibParts.push(citation.bibtex_entry.trim());
    }
  }

  // Step 4: Deduplicate
  const rawBib = bibParts.join("\n\n");
  const bib = deduplicateBibEntries(rawBib);

  return { tex, bib, warnings };
}

