import { describe, it, expect } from "vitest";
import {
  splitBibtex,
  replacePlaceholder,
  deduplicateBibEntries,
  assembleThesisContent,
} from "./assemble";
import type { Project, Section, Citation } from "@/lib/types/database";

// ── Helpers ─────────────────────────────────────────────────────────────────

function makeProject(overrides: Partial<Project> = {}): Project {
  return {
    id: "test-id",
    user_id: "user-id",
    organisation_id: null,
    status: "sandbox",
    license_id: null,
    title: "Prevalence of Anaemia in Pregnancy",
    synopsis_text: null,
    study_type: "Cross-sectional",
    university_type: "wbuhs",
    metadata_json: {
      candidate_name: "Dr. Ananya Sharma",
      guide_name: "Prof (Dr.) Rajesh Kumar",
      hod_name: "Prof (Dr.) Sunil Das",
      department: "Department of Obstetrics & Gynaecology",
      degree: "M.D.",
      speciality: "Obstetrics & Gynaecology",
      registration_no: "MD/2024/001",
      session: "2024--2027",
      year: "2026",
    },
    current_phase: 2,
    phases_completed: [0, 1],
    created_at: new Date().toISOString(),
    updated_at: new Date().toISOString(),
    ...overrides,
  };
}

function makeSection(overrides: Partial<Section> = {}): Section {
  return {
    id: "section-id",
    project_id: "test-id",
    phase_number: 2,
    phase_name: "introduction",
    latex_content: "\\section{Background}\nAnaemia is a common condition.",
    rich_content_json: null,
    ai_generated_latex: null,
    word_count: 50,
    citation_keys: [],
    status: "approved",
    ai_conversation_id: null,
    created_at: new Date().toISOString(),
    updated_at: new Date().toISOString(),
    ...overrides,
  };
}

function makeCitation(overrides: Partial<Citation> = {}): Citation {
  return {
    id: "citation-id",
    project_id: "test-id",
    cite_key: "smith2024",
    bibtex_entry: "@article{smith2024,\n  author={Smith, J},\n  title={Test Article},\n  journal={Test},\n  year={2024}\n}",
    provenance_tier: "A",
    evidence_type: "doi",
    evidence_value: "10.1000/test",
    source_doi: "10.1000/test",
    source_pmid: null,
    attested_by_user_id: null,
    attested_at: null,
    used_in_sections: ["introduction"],
    serial_number: 1,
    verified_at: null,
    created_at: new Date().toISOString(),
    ...overrides,
  };
}

// Minimal template that matches main.tex structure
const TEMPLATE = `\\documentclass{sskm-thesis}
%\\documentclass{ssuhs-thesis}
\\thesistitle{[YOUR THESIS TITLE HERE]}
\\candidatename{Dr. [Your Name]}
\\registrationno{[Registration Number]}
\\supervisorname{Prof (Dr.) [Guide Name]}
\\hodname{Prof (Dr.) [HOD Name]}
\\departmentname{Department of [Your Specialty]}
\\degreename{[M.D.]}
\\degreebranch{[Specialty]}
\\academicsession{[Start Year]--[End Year]}
\\submissionyear{[Year]}
\\begin{document}
\\frontmatter
\\maketitlepage

\\mainmatter

\\chapter{Introduction}
\\label{chap:introduction}

%% [[PLACEHOLDER — Phase 2]]
%% Target: 500–750 words, 10–15 references, present tense

%% ============================================================
%% CHAPTER 2: AIMS AND OBJECTIVES
%% ============================================================

\\chapter{Aims and Objectives}
\\label{chap:aims}

%% [[PLACEHOLDER — Phase 3]]
%% Target: 150–200 words, present tense, usually no new citations

%% ============================================================
%% CHAPTER 3: REVIEW OF LITERATURE
%% ============================================================

\\chapter{Review of Literature}
\\label{chap:literature}

%% [[PLACEHOLDER — Phase 4]]
%% Target: 2500–3500 words

\\chapter{Materials and Methods}
\\label{chap:methodology}

%% [[PLACEHOLDER — Phase 5]]

\\chapter{Results}
\\label{chap:results}

%% [[PLACEHOLDER — Phase 6b]]

\\chapter{Discussion}
\\label{chap:discussion}

%% [[PLACEHOLDER — Phase 7]]

\\chapter{Conclusion}
\\label{chap:conclusion}

%% [[PLACEHOLDER — Phase 8]]
%% Target: 500–750 words, NO new citations

\\listoffigures
\\backmatter
\\bibliography{references}
\\annexurematter
\\end{document}`;

// ── splitBibtex ─────────────────────────────────────────────────────────────

describe("splitBibtex", () => {
  it("returns body only when no marker present", () => {
    const result = splitBibtex("\\section{Intro}\nSome text.");
    expect(result.body).toBe("\\section{Intro}\nSome text.");
    expect(result.bib).toBe("");
  });

  it("splits at ---BIBTEX--- marker", () => {
    const input = `\\section{Intro}
Some text.

---BIBTEX---
@article{smith2024,
  author={Smith, J},
  title={Test},
  year={2024}
}`;
    const result = splitBibtex(input);
    expect(result.body).toBe("\\section{Intro}\nSome text.");
    expect(result.bib).toContain("@article{smith2024,");
    expect(result.bib).not.toContain("---BIBTEX---");
  });

  it("handles marker with no bib content after it", () => {
    const result = splitBibtex("Content here\n---BIBTEX---\n");
    expect(result.body).toBe("Content here");
    expect(result.bib).toBe("");
  });

  it("handles empty input", () => {
    const result = splitBibtex("");
    expect(result.body).toBe("");
    expect(result.bib).toBe("");
  });
});

// ── replacePlaceholder ──────────────────────────────────────────────────────

describe("replacePlaceholder", () => {
  it("replaces introduction placeholder", () => {
    const content = "Anaemia is a global health problem \\cite{who2024}.";
    const result = replacePlaceholder(TEMPLATE, "chap:introduction", content);

    expect(result).toContain("\\label{chap:introduction}");
    expect(result).toContain(content);
    expect(result).not.toContain("[[PLACEHOLDER — Phase 2]]");
  });

  it("replaces aims placeholder", () => {
    const content = "The primary aim of this study is to evaluate...";
    const result = replacePlaceholder(TEMPLATE, "chap:aims", content);

    expect(result).toContain("\\label{chap:aims}");
    expect(result).toContain(content);
    expect(result).not.toContain("[[PLACEHOLDER — Phase 3]]");
  });

  it("replaces conclusion placeholder", () => {
    const content = "In conclusion, our study demonstrates...";
    const result = replacePlaceholder(TEMPLATE, "chap:conclusion", content);

    expect(result).toContain("\\label{chap:conclusion}");
    expect(result).toContain(content);
    expect(result).not.toContain("[[PLACEHOLDER — Phase 8]]");
  });

  it("does not affect other placeholders", () => {
    const content = "Test content for introduction only.";
    const result = replacePlaceholder(TEMPLATE, "chap:introduction", content);

    expect(result).toContain("[[PLACEHOLDER — Phase 3]]");
    expect(result).toContain("[[PLACEHOLDER — Phase 4]]");
  });

  it("preserves template structure when label not found", () => {
    const result = replacePlaceholder(TEMPLATE, "chap:nonexistent", "content");
    expect(result).toBe(TEMPLATE);
  });
});

// ── deduplicateBibEntries ───────────────────────────────────────────────────

describe("deduplicateBibEntries", () => {
  it("returns empty string for empty input", () => {
    expect(deduplicateBibEntries("")).toBe("");
    expect(deduplicateBibEntries("   ")).toBe("");
  });

  it("passes through single entry unchanged", () => {
    const entry = `@article{smith2024,
  author={Smith, J},
  title={Test},
  year={2024}
}`;
    const result = deduplicateBibEntries(entry);
    expect(result).toContain("@article{smith2024,");
    expect(result).toContain("author={Smith, J}");
  });

  it("deduplicates by cite key (last-write-wins)", () => {
    const input = `@article{smith2024,
  author={Smith, J},
  title={Old Title},
  year={2024}
}

@article{smith2024,
  author={Smith, John},
  title={New Title},
  year={2024}
}`;
    const result = deduplicateBibEntries(input);
    expect(result).toContain("New Title");
    expect(result).not.toContain("Old Title");
    // Should only have one entry
    expect(result.match(/@article\{smith2024,/g)?.length).toBe(1);
  });

  it("keeps distinct entries", () => {
    const input = `@article{smith2024,
  author={Smith, J},
  title={First},
  year={2024}
}

@article{jones2023,
  author={Jones, A},
  title={Second},
  year={2023}
}`;
    const result = deduplicateBibEntries(input);
    expect(result).toContain("@article{smith2024,");
    expect(result).toContain("@article{jones2023,");
  });
});

// ── assembleThesisContent ───────────────────────────────────────────────────

describe("assembleThesisContent", () => {
  it("populates metadata and injects single section", () => {
    const project = makeProject();
    const sections = [
      makeSection({
        phase_number: 2,
        latex_content: "Anaemia is a common condition in pregnancy.",
        status: "approved",
      }),
    ];

    const { tex, warnings } = assembleThesisContent(TEMPLATE, project, sections, []);

    // Metadata populated
    expect(tex).toContain("\\thesistitle{Prevalence of Anaemia in Pregnancy}");
    // Section content injected
    expect(tex).toContain("Anaemia is a common condition in pregnancy.");
    // Other placeholders produce warnings
    expect(warnings.some((w) => w.includes("Phase 3"))).toBe(true);
  });

  it("injects all chapter sections (phases 2-8)", () => {
    const project = makeProject();
    const sections = [
      makeSection({ phase_number: 2, latex_content: "INTRO_CONTENT", status: "approved" }),
      makeSection({ id: "s3", phase_number: 3, latex_content: "AIMS_CONTENT", status: "approved" }),
      makeSection({ id: "s4", phase_number: 4, latex_content: "LITERATURE_CONTENT", status: "review" }),
      makeSection({ id: "s5", phase_number: 5, latex_content: "METHODS_CONTENT", status: "approved" }),
      makeSection({ id: "s6", phase_number: 6, latex_content: "RESULTS_CONTENT", status: "approved" }),
      makeSection({ id: "s7", phase_number: 7, latex_content: "DISCUSSION_CONTENT", status: "approved" }),
      makeSection({ id: "s8", phase_number: 8, latex_content: "CONCLUSION_CONTENT", status: "approved" }),
    ];

    const { tex, warnings } = assembleThesisContent(TEMPLATE, project, sections, []);

    expect(tex).toContain("INTRO_CONTENT");
    expect(tex).toContain("AIMS_CONTENT");
    expect(tex).toContain("LITERATURE_CONTENT");
    expect(tex).toContain("METHODS_CONTENT");
    expect(tex).toContain("RESULTS_CONTENT");
    expect(tex).toContain("DISCUSSION_CONTENT");
    expect(tex).toContain("CONCLUSION_CONTENT");
    // No missing-phase warnings for 2-8
    expect(warnings.filter((w) => w.includes("no approved/review content")).length).toBe(0);
  });

  it("collects BibTeX from section trailers", () => {
    const project = makeProject();
    const sections = [
      makeSection({
        phase_number: 2,
        latex_content: `\\section{Background}
Some text \\cite{smith2024}.

---BIBTEX---
@article{smith2024,
  author={Smith, J},
  title={Test},
  year={2024}
}`,
        status: "approved",
      }),
    ];

    const { bib } = assembleThesisContent(TEMPLATE, project, sections, []);

    expect(bib).toContain("@article{smith2024,");
  });

  it("collects BibTeX from citations table", () => {
    const project = makeProject();
    const citations = [
      makeCitation({
        cite_key: "jones2023",
        bibtex_entry: "@article{jones2023,\n  author={Jones, A},\n  title={Citation Table Entry},\n  year={2023}\n}",
      }),
    ];

    const { bib } = assembleThesisContent(TEMPLATE, project, [], citations);

    expect(bib).toContain("@article{jones2023,");
    expect(bib).toContain("Citation Table Entry");
  });

  it("deduplicates BibTeX: citations table wins over section trailer", () => {
    const project = makeProject();
    const sections = [
      makeSection({
        phase_number: 2,
        latex_content: `Text \\cite{smith2024}.

---BIBTEX---
@article{smith2024,
  author={Smith, J},
  title={AI Generated Version},
  year={2024}
}`,
        status: "approved",
      }),
    ];
    const citations = [
      makeCitation({
        cite_key: "smith2024",
        bibtex_entry: "@article{smith2024,\n  author={Smith, John},\n  title={Citation Table Version},\n  year={2024}\n}",
      }),
    ];

    const { bib } = assembleThesisContent(TEMPLATE, project, sections, citations);

    // Citations table entries come after section trailers → last-write-wins
    expect(bib).toContain("Citation Table Version");
    expect(bib).not.toContain("AI Generated Version");
  });

  it("warns about missing sections but does not fail", () => {
    const project = makeProject();
    const { tex, warnings } = assembleThesisContent(TEMPLATE, project, [], []);

    // Should still return valid tex
    expect(tex).toContain("\\begin{document}");
    expect(tex).toContain("\\end{document}");
    // Should have warnings for each missing phase
    expect(warnings.filter((w) => w.includes("no approved/review content")).length).toBe(7);
  });

  it("does not inject phase 0 or phase 1 as chapter content", () => {
    const project = makeProject();
    const sections = [
      makeSection({
        phase_number: 0,
        phase_name: "orientation",
        latex_content: "ORIENTATION_CONTENT",
        status: "approved",
      }),
      makeSection({
        id: "s1",
        phase_number: 1,
        phase_name: "front_matter",
        latex_content: "FRONT_MATTER_CONTENT",
        status: "approved",
      }),
    ];

    const { tex } = assembleThesisContent(TEMPLATE, project, sections, []);

    // Phase 0 and 1 should NOT appear in chapter bodies
    expect(tex).not.toContain("ORIENTATION_CONTENT");
    expect(tex).not.toContain("FRONT_MATTER_CONTENT");
  });

  it("prefers approved sections over review sections", () => {
    const project = makeProject();
    const sections = [
      makeSection({
        id: "s2-review",
        phase_number: 2,
        latex_content: "REVIEW_VERSION",
        status: "review",
      }),
      makeSection({
        id: "s2-approved",
        phase_number: 2,
        latex_content: "APPROVED_VERSION",
        status: "approved",
      }),
    ];

    const { tex } = assembleThesisContent(TEMPLATE, project, sections, []);

    expect(tex).toContain("APPROVED_VERSION");
    expect(tex).not.toContain("REVIEW_VERSION");
  });
});

