 29→ */
 30→export async function compileTex(
 31→  texContent: string,
 32→  options: {
 33→    projectId: string;
 34→    watermark?: boolean;
 35→    clsFiles?: string[];
 36→    bstFile?: string;
 37→    bibContent?: string;
 38→  }
 39→): Promise<CompileResult> {
 40→  const mode = getCompileMode();
 41→
 42→  if (mode === "mock") {
 43→    return mockCompile(texContent);
 44→  }
 45→
 46→  if (mode === "docker") {
 47→    return dockerCompile(texContent, options);
 48→  }
 49→
 50→  return localCompile(texContent, options);
 51→}
 52→
 53→async function mockCompile(texContent: string): Promise<CompileResult> {
 54→  // For testing: validate basic TeX structure
 55→  const hasDocumentClass = /\\documentclass/.test(texContent);
 56→  const hasBeginDocument = /\\begin\{document\}/.test(texContent);
 57→  const hasEndDocument = /\\end\{document\}/.test(texContent);
 58→
 59→  const errors: string[] = [];
 60→  if (!hasDocumentClass) errors.push("Missing \\documentclass");
 61→  if (!hasBeginDocument) errors.push("Missing \\begin{document}");
 62→  if (!hasEndDocument) errors.push("Missing \\end{document}");
 63→
 64→  const success = errors.length === 0;
 65→
 66→  // In mock mode, write a minimal placeholder PDF so the compile route sees a pdfPath
 67→  let pdfPath: string | null = null;
 68→  if (success) {
 69→    const mockPdfDir = path.join(os.tmpdir(), "apollo-mock-pdf");
 70→    await mkdir(mockPdfDir, { recursive: true });
 71→    pdfPath = path.join(mockPdfDir, "main.pdf");
 72→    // Minimal valid PDF (1-page blank)
 73→    const minimalPdf = `%PDF-1.0\n1 0 obj<</Pages 2 0 R>>endobj\n2 0 obj<</Kids[3 0 R]/Count 1>>endobj\n3 0 obj<</MediaBox[0 0 612 792]>>endobj\ntrailer<</Root 1 0 R>>\n`;
 74→    await writeFile(pdfPath, minimalPdf);
 75→  }
 76→
 77→  return {
 78→    success,
 79→    pdfPath,
 80→    log: { errors, warnings: [], errorCount: errors.length, warningCount: 0 },
 81→    rawLog: success ? "Mock compilation successful" : errors.join("\n"),
 82→    compileTimeMs: 1,
 83→  };
 84→}
 85→
 86→async function dockerCompile(
 87→  texContent: string,
 88→  options: {
 89→    projectId: string;
 90→    watermark?: boolean;
 91→    clsFiles?: string[];
 92→    bstFile?: string;
 93→    bibContent?: string;
 94→  }
 95→): Promise<CompileResult> {
 96→  const start = Date.now();
 97→  const workDir = path.join(os.tmpdir(), `apollo-compile-${options.projectId}`);
 98→
 99→  try {
100→    await mkdir(workDir, { recursive: true });
101→
102→    // Write tex content
103→    await writeFile(path.join(workDir, "main.tex"), texContent);
104→
105→    // Copy CLS and BST files from templates
106→    const templatesDir = path.resolve(process.cwd(), "../../templates");
107→    const clsFiles = options.clsFiles ?? ["sskm-thesis.cls", "ssuhs-thesis.cls"];
108→
109→    for (const cls of clsFiles) {
110→      try {
111→        const content = await readFile(path.join(templatesDir, cls));
112→        await writeFile(path.join(workDir, cls), content);
113→      } catch {
114→        // CLS file may not exist locally in all environments
115→      }
116→    }
117→
118→    const bstFile = options.bstFile ?? "vancouver.bst";
119→    try {
120→      const bstContent = await readFile(path.join(templatesDir, bstFile));
121→      await writeFile(path.join(workDir, bstFile), bstContent);
122→    } catch {
123→      // BST may not exist locally
124→    }
125→
126→    // Copy logo directory
127→    try {
128→      const logoDir = path.join(templatesDir, "logo");
129→      await mkdir(path.join(workDir, "logo"), { recursive: true });
130→      const { stdout: logoFiles } = await execFileAsync("ls", [logoDir]);
131→      for (const file of logoFiles.trim().split("\n").filter(Boolean)) {
132→        const content = await readFile(path.join(logoDir, file));
133→        await writeFile(path.join(workDir, "logo", file), content);
134→      }
135→    } catch {
136→      // Logo dir may not exist
137→    }
138→
139→    // Write references.bib (populated or empty)
140→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
141→
142→    // Create output directory
143→    const outputDir = path.join(workDir, "output");
144→    await mkdir(outputDir, { recursive: true });
145→
146→    // Run Docker compile
147→    const containerName = process.env.LATEX_CONTAINER_NAME ?? "apollo-latex";
148→    const args = [
149→      "run",
150→      "--rm",
151→      "--network=none",
152→      "--read-only",
153→      "--tmpfs", "/tmp:rw,size=512m",
154→      "--memory=1g",
155→      "-v", `${workDir}:/thesis`,
156→      containerName,
157→    ];
158→
159→    if (options.watermark) {
160→      args.push("--watermark");
161→    }
162→
163→    const { stdout, stderr } = await execFileAsync("docker", args, {
164→      timeout: 120_000,
165→    });
166→
167→    // Read output
168→    let pdfPath: string | null = null;
169→    try {
170→      await readFile(path.join(outputDir, "main.pdf"));
171→      pdfPath = path.join(outputDir, "main.pdf");
172→    } catch {
173→      // PDF may not have been generated
174→    }
175→
176→    let rawLog = "";
177→    try {
178→      rawLog = await readFile(path.join(outputDir, "main.log"), "utf-8");
179→    } catch {
180→      rawLog = stdout + stderr;
181→    }
182→
183→    const log = parseLatexLog(rawLog);
184→
185→    return {
186→      success: log.errorCount === 0 && pdfPath !== null,
187→      pdfPath,
188→      log,
189→      rawLog,
190→      compileTimeMs: Date.now() - start,
191→    };
192→  } catch (err) {
193→    return {
194→      success: false,
195→      pdfPath: null,
196→      log: {
197→        errors: [err instanceof Error ? err.message : "Docker compilation failed"],
198→        warnings: [],
199→        errorCount: 1,
200→        warningCount: 0,
201→      },
202→      rawLog: err instanceof Error ? err.message : "Unknown error",
203→      compileTimeMs: Date.now() - start,
204→    };
205→  } finally {
206→    // Cleanup
207→    try {
208→      await rm(workDir, { recursive: true, force: true });
209→    } catch {
210→      // Best effort cleanup
211→    }
212→  }
213→}
