215→async function localCompile(
216→  texContent: string,
217→  options: {
218→    projectId: string;
219→    watermark?: boolean;
220→    bibContent?: string;
221→  }
222→): Promise<CompileResult> {
223→  const start = Date.now();
224→  const workDir = path.join(os.tmpdir(), `apollo-compile-${options.projectId}`);
225→
226→  try {
227→    await mkdir(workDir, { recursive: true });
228→    await writeFile(path.join(workDir, "main.tex"), texContent);
229→
230→    // Copy templates
231→    const templatesDir = path.resolve(process.cwd(), "../../templates");
232→    for (const file of ["sskm-thesis.cls", "ssuhs-thesis.cls", "vancouver.bst"]) {
233→      try {
234→        const content = await readFile(path.join(templatesDir, file));
235→        await writeFile(path.join(workDir, file), content);
236→      } catch {
237→        // May not exist
238→      }
239→    }
240→
241→    // Copy logos
242→    try {
243→      await mkdir(path.join(workDir, "logo"), { recursive: true });
244→      const { stdout } = await execFileAsync("ls", [path.join(templatesDir, "logo")]);
245→      for (const file of stdout.trim().split("\n").filter(Boolean)) {
246→        const content = await readFile(path.join(templatesDir, "logo", file));
247→        await writeFile(path.join(workDir, "logo", file), content);
248→      }
249→    } catch {
250→      // May not exist
251→    }
252→
253→    // Write references.bib (populated or empty)
254→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
255→
256→    // Run pdflatex → bibtex → pdflatex × 2
257→    const pdflatexArgs = ["-interaction=nonstopmode", "-output-directory", workDir, path.join(workDir, "main.tex")];
258→
259→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
260→    await execFileAsync("bibtex", [path.join(workDir, "main")], { timeout: 30_000, cwd: workDir }).catch(() => {});
261→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
262→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
263→
264→    let pdfPath: string | null = null;
265→    try {
266→      await readFile(path.join(workDir, "main.pdf"));
267→      pdfPath = path.join(workDir, "main.pdf");
268→    } catch {
269→      // PDF may not have been generated
270→    }
271→
272→    let rawLog = "";
273→    try {
274→      rawLog = await readFile(path.join(workDir, "main.log"), "utf-8");
275→    } catch {
276→      rawLog = "No log file generated";
277→    }
278→
279→    const log = parseLatexLog(rawLog);
280→
281→    return {
282→      success: log.errorCount === 0 && pdfPath !== null,
283→      pdfPath,
284→      log,
285→      rawLog,
286→      compileTimeMs: Date.now() - start,
287→    };
288→  } catch (err) {
289→    return {
290→      success: false,
291→      pdfPath: null,
292→      log: {
293→        errors: [err instanceof Error ? err.message : "Local compilation failed"],
294→        warnings: [],
295→        errorCount: 1,
296→        warningCount: 0,
297→      },
298→      rawLog: err instanceof Error ? err.message : "Unknown error",
299→      compileTimeMs: Date.now() - start,
300→    };
301→  }
302→}
