  1→import { NextRequest } from "next/server";
  2→import { getAuthenticatedUser } from "@/lib/api/auth";
  3→import {
  4→  unauthorised,
  5→  notFound,
  6→  badRequest,
  7→  internalError,
  8→  rateLimited,
  9→  conflict,
 10→} from "@/lib/api/errors";
 11→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 12→import { isValidPhase } from "@/lib/phases/transitions";
 13→import { getPhase } from "@/lib/phases/constants";
 14→import { getAnthropicClient } from "@/lib/ai/client";
 15→import { redactPII } from "@/lib/ai/redact";
 16→import { checkRateLimit } from "@/lib/ai/rate-limit";
 17→import {
 18→  SYNOPSIS_PARSE_SYSTEM_PROMPT,
 19→  getPhaseSystemPrompt,
 20→  getPhaseUserMessage,
 21→} from "@/lib/ai/prompts";
 22→import { parseSynopsisResponse } from "@/lib/ai/parse-synopsis-response";
 23→import { latexToTiptap } from "@/lib/latex/latex-to-tiptap";
 24→import type { Project } from "@/lib/types/database";
 25→
 26→export async function POST(
 27→  request: NextRequest,
 28→  { params }: { params: Promise<{ id: string; phase: string }> }
 29→) {
 30→  try {
 31→    const authResult = await getAuthenticatedUser();
 32→    if (!authResult) return unauthorised();
 33→
 34→    const { id, phase } = await params;
 35→    const phaseNumber = parseInt(phase, 10);
 36→
 37→    if (!isValidPhase(phaseNumber)) {
 38→      return badRequest("Invalid phase number");
 39→    }
 40→
 41→    // Rate limit check
 42→    const rateCheck = checkRateLimit(authResult.user.id);
 43→    if (!rateCheck.allowed) {
 44→      return rateLimited(rateCheck.retryAfterSeconds);
 45→    }
 46→
 47→    const supabase = createAdminSupabaseClient();
 48→
 49→    // Fetch project
 50→    const { data: project, error: projectError } = await supabase
 51→      .from("projects")
 52→      .select("*")
 53→      .eq("id", id)
 54→      .eq("user_id", authResult.user.id)
 55→      .single();
 56→
 57→    if (projectError || !project) {
 58→      return notFound("Project not found");
 59→    }
 60→
 61→    const typedProject = project as Project;
 62→
 63→    // Phase 0: Synopsis parsing (special handling)
 64→    if (phaseNumber === 0) {
 65→      return handlePhase0Generate(request, typedProject, supabase);
 66→    }
 67→
 68→    // Phases 1-8: General thesis section generation
 69→    const systemPrompt = getPhaseSystemPrompt(phaseNumber);
 70→    if (!systemPrompt) {
 71→      return badRequest(`AI generation for Phase ${phaseNumber} is not yet supported`);
 72→    }
 73→
 74→    return handleSectionGenerate(phaseNumber, typedProject, supabase);
 75→  } catch (err) {
 76→    console.error("Unexpected error in POST generate:", err);
 77→    return internalError();
 78→  }
 79→}
 80→
 81→/**
 82→ * Generic handler for phases 1-8: generates thesis section content via SSE streaming.
 83→ */
 84→async function handleSectionGenerate(
 85→  phaseNumber: number,
 86→  project: Project,
 87→  supabase: ReturnType<typeof createAdminSupabaseClient>
 88→) {
 89→  if (!project.synopsis_text) {
 90→    return badRequest("Project has no synopsis text — upload a synopsis first");
 91→  }
 92→
 93→  // Check section isn't already generating
 94→  const { data: existingSection } = await supabase
 95→    .from("sections")
 96→    .select("status")
 97→    .eq("project_id", project.id)
 98→    .eq("phase_number", phaseNumber)
 99→    .single();
100→
101→  if (existingSection?.status === "generating") {
102→    return conflict("Generation already in progress for this section");
103→  }
104→
105→  // Gather context from previously completed sections
106→  const { data: approvedSections } = await supabase
107→    .from("sections")
108→    .select("phase_number, phase_name, latex_content")
109→    .eq("project_id", project.id)
110→    .eq("status", "approved")
111→    .order("phase_number", { ascending: true });
112→
113→  const previousSections = (approvedSections ?? [])
114→    .filter((s) => s.phase_number < phaseNumber && s.latex_content)
115→    .map((s) => ({
116→      phaseName: s.phase_name as string,
117→      content: s.latex_content as string,
118→    }));
119→
120→  // Redact PII from synopsis
121→  const { redacted: redactedSynopsis } = redactPII(project.synopsis_text);
122→
123→  // Get prompts
124→  const systemPrompt = getPhaseSystemPrompt(phaseNumber)!;
125→  const userMessage = getPhaseUserMessage(
126→    phaseNumber,
127→    redactedSynopsis,
128→    (project.metadata_json ?? {}) as Record<string, unknown>,
129→    previousSections
130→  );
131→
132→  // Create/update section as "generating"
133→  const phaseDef = getPhase(phaseNumber);
134→  await supabase.from("sections").upsert(
135→    {
136→      project_id: project.id,
137→      phase_number: phaseNumber,
138→      phase_name: phaseDef?.name ?? `phase_${phaseNumber}`,
139→      latex_content: "",
140→      word_count: 0,
141→      citation_keys: [],
142→      status: "generating",
143→    },
144→    { onConflict: "project_id,phase_number" }
145→  );
146→
147→  // Use Opus for Introduction and Discussion (higher quality), Sonnet for others
148→  // Sonnet for all phases (Opus requires separate API access)
149→  const model = "claude-sonnet-4-5-20250929";
150→  const maxTokens = phaseNumber === 4 ? 8000 : 4000; // ROL needs more tokens
151→
152→  // Stream response via SSE
153→  const encoder = new TextEncoder();
154→  const stream = new ReadableStream({
155→    async start(controller) {
156→      try {
157→        const client = getAnthropicClient();
158→        let fullResponse = "";
159→
160→        const messageStream = client.messages.stream({
161→          model,
162→          max_tokens: maxTokens,
163→          system: systemPrompt,
164→          messages: [
165→            { role: "user", content: userMessage },
166→          ],
167→        });
168→
169→        messageStream.on("text", (text) => {
170→          fullResponse += text;
171→          controller.enqueue(
172→            encoder.encode(`data: ${JSON.stringify({ type: "delta", text })}\n\n`)
173→          );
174→        });
175→
176→        await messageStream.finalMessage();
177→
178→        // Convert LaTeX → Tiptap JSON for rich text editor
179→        const tiptapResult = latexToTiptap(fullResponse);
180→        const citationKeys = tiptapResult.citationKeys;
181→
182→        if (tiptapResult.warnings.length > 0) {
183→          console.warn(
184→            `LaTeX→Tiptap parse warnings (phase ${phaseNumber}):`,
185→            tiptapResult.warnings,
186→          );
187→        }
188→
189→        // Count words (rough: strip LaTeX commands)
190→        const plainText = fullResponse
191→          .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
192→          .replace(/\\[a-zA-Z]+/g, " ")
193→          .replace(/[{}\\]/g, " ");
194→        const wordCount = plainText.split(/\s+/).filter(Boolean).length;
195→
196→        // Update section with generated content + rich text JSON
197→        await supabase
198→          .from("sections")
199→          .update({
200→            latex_content: fullResponse,
201→            rich_content_json: tiptapResult.json,
202→            ai_generated_latex: fullResponse,
203→            word_count: wordCount,
204→            citation_keys: citationKeys,
205→            status: "review",
206→            updated_at: new Date().toISOString(),
207→          })
208→          .eq("project_id", project.id)
209→          .eq("phase_number", phaseNumber);
210→
211→        controller.enqueue(
212→          encoder.encode(
213→            `data: ${JSON.stringify({
214→              type: "complete",
215→              wordCount,
216→              citationKeys,
217→              parseWarnings: tiptapResult.warnings,
218→            })}\n\n`
219→          )
220→        );
221→
222→        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
223→        controller.close();
224→      } catch (err) {
225→        // Reset section status on error
226→        await supabase
227→          .from("sections")
228→          .update({ status: "draft", updated_at: new Date().toISOString() })
229→          .eq("project_id", project.id)
230→          .eq("phase_number", phaseNumber);
231→
232→        const errorMessage =
233→          err instanceof Error ? err.message : "AI generation failed";
234→        controller.enqueue(
235→          encoder.encode(
236→            `data: ${JSON.stringify({ type: "error", message: errorMessage })}\n\n`
237→          )
238→        );
239→        controller.close();
240→      }
241→    },
242→  });
243→
244→  return new Response(stream, {
245→    headers: {
246→      "Content-Type": "text/event-stream",
247→      "Cache-Control": "no-cache",
248→      Connection: "keep-alive",
249→    },
250→  });
251→}
