253→/**
254→ * Phase 0: Synopsis parsing (returns structured JSON, not LaTeX).
255→ */
256→async function handlePhase0Generate(
257→  _request: NextRequest,
258→  project: Project,
259→  supabase: ReturnType<typeof createAdminSupabaseClient>
260→) {
261→  if (!project.synopsis_text) {
262→    return badRequest("Project has no synopsis text — upload a synopsis first");
263→  }
264→
265→  // Check section isn't already generating
266→  const { data: existingSection } = await supabase
267→    .from("sections")
268→    .select("status")
269→    .eq("project_id", project.id)
270→    .eq("phase_number", 0)
271→    .single();
272→
273→  if (existingSection?.status === "generating") {
274→    return conflict("Generation already in progress for this section");
275→  }
276→
277→  // Redact PII before sending to AI
278→  const { redacted: redactedSynopsis } = redactPII(project.synopsis_text);
279→
280→  // Create/update section as "generating"
281→  const phaseDef = getPhase(0);
282→  await supabase.from("sections").upsert(
283→    {
284→      project_id: project.id,
285→      phase_number: 0,
286→      phase_name: phaseDef?.name ?? "orientation",
287→      latex_content: "",
288→      word_count: 0,
289→      citation_keys: [],
290→      status: "generating",
291→    },
292→    { onConflict: "project_id,phase_number" }
293→  );
294→
295→  // Stream response via SSE
296→  const encoder = new TextEncoder();
297→  const stream = new ReadableStream({
298→    async start(controller) {
299→      try {
300→        const client = getAnthropicClient();
301→        let fullResponse = "";
302→
303→        const messageStream = client.messages.stream({
304→          model: "claude-sonnet-4-5-20250929",
305→          max_tokens: 2000,
306→          system: SYNOPSIS_PARSE_SYSTEM_PROMPT,
307→          messages: [
308→            {
309→              role: "user",
310→              content: `Parse the following medical thesis synopsis and extract structured metadata as JSON:\n\n${redactedSynopsis}`,
311→            },
312→          ],
313→        });
314→
315→        messageStream.on("text", (text) => {
316→          fullResponse += text;
317→          controller.enqueue(
318→            encoder.encode(`data: ${JSON.stringify({ type: "delta", text })}\n\n`)
319→          );
320→        });
321→
322→        await messageStream.finalMessage();
323→
324→        // Parse the complete response
325→        const parsed = parseSynopsisResponse(fullResponse);
326→
327→        if (parsed) {
328→          // Update project metadata with extracted data
329→          const metadataUpdate: Record<string, unknown> = {
330→            ...project.metadata_json,
331→          };
332→          if (parsed.department) metadataUpdate.department = parsed.department;
333→
334→          const projectUpdate: Record<string, unknown> = {
335→            metadata_json: metadataUpdate,
336→            updated_at: new Date().toISOString(),
337→          };
338→          if (parsed.title && !project.title) {
339→            projectUpdate.title = parsed.title;
340→          }
341→          if (parsed.study_type) {
342→            projectUpdate.study_type = parsed.study_type;
343→          }
344→
345→          await supabase
346→            .from("projects")
347→            .update(projectUpdate)
348→            .eq("id", project.id);
349→
350→          // Update section with parsed content and set to review
351→          await supabase
352→            .from("sections")
353→            .update({
354→              latex_content: fullResponse,
355→              status: "review",
356→              updated_at: new Date().toISOString(),
357→            })
358→            .eq("project_id", project.id)
359→            .eq("phase_number", 0);
360→
361→          controller.enqueue(
362→            encoder.encode(
363→              `data: ${JSON.stringify({ type: "complete", parsed })}\n\n`
364→            )
365→          );
366→        } else {
367→          // Parsing failed — still save raw content
368→          await supabase
369→            .from("sections")
370→            .update({
371→              latex_content: fullResponse,
372→              status: "review",
373→              updated_at: new Date().toISOString(),
374→            })
375→            .eq("project_id", project.id)
376→            .eq("phase_number", 0);
377→
378→          controller.enqueue(
379→            encoder.encode(
380→              `data: ${JSON.stringify({ type: "complete", parsed: null, raw: fullResponse })}\n\n`
381→            )
382→          );
383→        }
384→
385→        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
386→        controller.close();
387→      } catch (err) {
388→        // Reset section status on error
389→        await supabase
390→          .from("sections")
391→          .update({ status: "draft", updated_at: new Date().toISOString() })
392→          .eq("project_id", project.id)
393→          .eq("phase_number", 0);
394→
395→        const errorMessage =
396→          err instanceof Error ? err.message : "AI generation failed";
397→        controller.enqueue(
398→          encoder.encode(
399→            `data: ${JSON.stringify({ type: "error", message: errorMessage })}\n\n`
400→          )
401→        );
402→        controller.close();
403→      }
404→    },
405→  });
406→
407→  return new Response(stream, {
408→    headers: {
409→      "Content-Type": "text/event-stream",
410→      "Cache-Control": "no-cache",
411→      Connection: "keep-alive",
412→    },
413→  });
414→}
415→

  1→/**
  2→ * LaTeX → Tiptap JSON converter.
  3→ *
  4→ * Two-pass regex-based parser targeting the predictable subset of LaTeX
  5→ * produced by AI generation. NOT a general-purpose LaTeX parser.
  6→ *
  7→ * Pass 1: Block tokeniser — splits input into heading / list / blockquote /
  8→ *         codeBlock / paragraph blocks.
  9→ * Pass 2: Inline parser — recursive-descent within each block's text to
 10→ *         handle \textbf, \textit, \emph, \texttt, \underline, \cite, and
 11→ *         escaped characters.
 12→ */
 13→
 14→import type { TiptapNode, TiptapMark } from "./tiptap-to-latex";
 15→
 16→// ── Result type ─────────────────────────────────────────────────────────────
 17→
 18→export interface LatexToTiptapResult {
 19→  json: TiptapNode;
 20→  citationKeys: string[];
 21→  warnings: string[];
 22→}
 23→
 24→// ── Pre-processing ──────────────────────────────────────────────────────────
 25→
 26→/**
 27→ * Strip non-content LaTeX that the Rich Text editor should never display:
 28→ * labels, needspace, bibtex trailer, comment lines.
 29→ */
 30→function preprocess(latex: string): string {
 31→  let result = latex;
 32→  // Remove ---BIBTEX--- trailer (everything after it)
 33→  const bibtexIdx = result.indexOf("---BIBTEX---");
 34→  if (bibtexIdx !== -1) {
 35→    result = result.slice(0, bibtexIdx);
 36→  }
 37→  // Remove \label{...}
 38→  result = result.replace(/\\label\{[^}]*\}/g, "");
 39→  // Remove \needspace{...}
 40→  result = result.replace(/\\needspace\{[^}]*\}/g, "");
 41→  // Remove full-line comments (lines starting with %)
 42→  result = result.replace(/^%.*$/gm, "");
 43→  return result.trim();
 44→}
 45→
 46→// ── Escaped character mapping (inverse of escape.ts) ────────────────────────
 47→
 48→const UNESCAPE_MAP: [RegExp, string][] = [
 49→  [/\\textbackslash\{\}/g, "\\"],
 50→  [/\\textasciitilde\{\}/g, "~"],
 51→  [/\\textasciicircum\{\}/g, "^"],
 52→  [/\\&/g, "&"],
 53→  [/\\%/g, "%"],
 54→  [/\\\$/g, "$"],
 55→  [/\\#/g, "#"],
 56→  [/\\_/g, "_"],
 57→  [/\\\{/g, "{"],
 58→  [/\\\}/g, "}"],
 59→];
 60→
 61→function unescapeLatex(text: string): string {
 62→  let result = text;
 63→  for (const [pattern, replacement] of UNESCAPE_MAP) {
 64→    result = result.replace(pattern, replacement);
 65→  }
 66→  return result;
 67→}
 68→
 69→// ── Citation key extraction ─────────────────────────────────────────────────
 70→
 71→function extractCiteKeys(text: string): string[] {
 72→  const keys: string[] = [];
 73→  const re = /\\cite\{([^}]+)\}/g;
 74→  let m: RegExpExecArray | null;
 75→  while ((m = re.exec(text)) !== null) {
 76→    for (const k of m[1].split(",")) {
 77→      const trimmed = k.trim();
 78→      if (trimmed && !keys.includes(trimmed)) keys.push(trimmed);
 79→    }
 80→  }
 81→  return keys;
 82→}
 83→
 84→// ── Pass 2: Inline parser ───────────────────────────────────────────────────
 85→
 86→/**
 87→ * Parse inline LaTeX content into Tiptap text nodes with marks.
 88→ * Uses recursive descent to handle nested commands like \textbf{\textit{...}}.
 89→ */
 90→function parseInline(
 91→  input: string,
 92→  marks: TiptapMark[],
 93→  citationKeys: string[],
 94→): TiptapNode[] {
 95→  const nodes: TiptapNode[] = [];
 96→  let pos = 0;
 97→
 98→  while (pos < input.length) {
 99→    // Try to match a LaTeX command
100→    const cmdMatch = matchCommand(input, pos);
101→
102→    if (cmdMatch) {
103→      // Flush any plain text before this command
104→      if (cmdMatch.start > pos) {
105→        const plain = unescapeLatex(input.slice(pos, cmdMatch.start));
106→        if (plain) {
107→          nodes.push(textNode(plain, marks));
108→        }
109→      }
110→
111→      if (cmdMatch.type === "cite") {
112→        // \cite{keys} → code-marked text node with raw command
113→        const raw = input.slice(cmdMatch.start, cmdMatch.end);
114→        const keys = extractCiteKeys(raw);
115→        for (const k of keys) {
116→          if (!citationKeys.includes(k)) citationKeys.push(k);
117→        }
118→        nodes.push(textNode(raw, [...marks, { type: "code" }]));
119→      } else if (cmdMatch.type === "format") {
120→        // \textbf{...}, \textit{...}, etc. → recurse with added mark
121→        const innerNodes = parseInline(
122→          cmdMatch.content,
123→          [...marks, { type: cmdMatch.mark! }],
124→          citationKeys,
125→        );
126→        nodes.push(...innerNodes);
127→      }
128→
129→      pos = cmdMatch.end;
130→    } else {
131→      // No command found — consume one character or up to next backslash
132→      const nextBackslash = input.indexOf("\\", pos);
133→      const end =
134→        nextBackslash === -1 || nextBackslash === pos
135→          ? input.length
136→          : nextBackslash;
137→
138→      if (end === pos && pos < input.length) {
139→        // Backslash at current position but no command matched — it's an escaped char
140→        // Try to consume the escaped char pattern
141→        const escMatch = matchEscapedChar(input, pos);
142→        if (escMatch) {
143→          nodes.push(textNode(escMatch.char, marks));
144→          pos = escMatch.end;
145→        } else {
146→          // Unknown backslash sequence — consume as-is
147→          nodes.push(textNode(input[pos], marks));
148→          pos++;
149→        }
150→      } else {
151→        const plain = unescapeLatex(input.slice(pos, end));
152→        if (plain) {
153→          nodes.push(textNode(plain, marks));
154→        }
155→        pos = end;
156→      }
157→    }
158→  }
159→
160→  return mergeAdjacentTextNodes(nodes);
161→}
162→
163→interface CommandMatch {
164→  type: "format" | "cite";
165→  start: number;
166→  end: number;
167→  content: string;
168→  mark?: string;
169→}
170→
171→const FORMAT_COMMANDS: Record<string, string> = {
172→  "\\textbf": "bold",
173→  "\\textit": "italic",
174→  "\\emph": "italic",
175→  "\\texttt": "code",
176→  "\\underline": "underline",
177→};
178→
179→/**
180→ * Try to match a LaTeX command at the given position.
181→ */
182→function matchCommand(input: string, pos: number): CommandMatch | null {
183→  if (input[pos] !== "\\") return null;
184→
185→  // Try \cite{...}
186→  if (input.startsWith("\\cite{", pos)) {
187→    const braceContent = extractBraceContent(input, pos + 5);
188→    if (braceContent) {
189→      return {
190→        type: "cite",
191→        start: pos,
192→        end: braceContent.end,
193→        content: braceContent.content,
194→      };
195→    }
196→  }
197→
198→  // Try format commands
199→  for (const [cmd, mark] of Object.entries(FORMAT_COMMANDS)) {
200→    if (input.startsWith(cmd + "{", pos)) {
201→      const braceContent = extractBraceContent(input, pos + cmd.length);
202→      if (braceContent) {
203→        return {
204→          type: "format",
205→          start: pos,
206→          end: braceContent.end,
207→          content: braceContent.content,
208→          mark,
209→        };
210→      }
211→    }
212→  }
213→
214→  return null;
215→}
216→
217→interface EscapedCharMatch {
218→  char: string;
219→  end: number;
220→}
221→
222→/**
223→ * Match an escaped character sequence at the given position.
224→ */
225→function matchEscapedChar(input: string, pos: number): EscapedCharMatch | null {
226→  if (input[pos] !== "\\") return null;
227→
228→  // Multi-character escapes
229→  if (input.startsWith("\\textbackslash{}", pos))
230→    return { char: "\\", end: pos + 16 };
231→  if (input.startsWith("\\textasciitilde{}", pos))
232→    return { char: "~", end: pos + 17 };
233→  if (input.startsWith("\\textasciicircum{}", pos))
234→    return { char: "^", end: pos + 18 };
235→
236→  // Single-character escapes: \& \% \$ \# \_ \{ \}
237→  const next = input[pos + 1];
238→  if (next && "&%$#_{} ".includes(next)) {
239→    return { char: next, end: pos + 2 };
240→  }
241→
242→  return null;
243→}
244→
245→/**
246→ * Extract content between balanced braces starting at `pos` (which must point to `{`).
247→ */
248→function extractBraceContent(
249→  input: string,
250→  pos: number,
251→): { content: string; end: number } | null {
252→  if (input[pos] !== "{") return null;
253→
254→  let depth = 1;
255→  let i = pos + 1;
256→
257→  while (i < input.length && depth > 0) {
258→    if (input[i] === "{" && input[i - 1] !== "\\") {
259→      depth++;
260→    } else if (input[i] === "}" && input[i - 1] !== "\\") {
261→      depth--;
262→    }
263→    if (depth > 0) i++;
264→  }
265→
266→  if (depth !== 0) return null;
267→
268→  return {
269→    content: input.slice(pos + 1, i),
270→    end: i + 1,
271→  };
272→}
273→
274→function textNode(text: string, marks: TiptapMark[]): TiptapNode {
275→  const node: TiptapNode = { type: "text", text };
276→  if (marks.length > 0) {
277→    node.marks = [...marks];
278→  }
279→  return node;
280→}
281→
282→/**
283→ * Merge adjacent text nodes that have identical marks.
284→ */
285→function mergeAdjacentTextNodes(nodes: TiptapNode[]): TiptapNode[] {
286→  if (nodes.length === 0) return nodes;
287→
288→  const merged: TiptapNode[] = [nodes[0]];
289→
290→  for (let i = 1; i < nodes.length; i++) {
291→    const prev = merged[merged.length - 1];
292→    const curr = nodes[i];
293→
294→    if (
295→      prev.type === "text" &&
296→      curr.type === "text" &&
297→      marksEqual(prev.marks, curr.marks)
298→    ) {
299→      prev.text = (prev.text ?? "") + (curr.text ?? "");
300→    } else {
301→      merged.push(curr);
302→    }
303→  }
304→
305→  return merged;
306→}
307→
308→function marksEqual(
309→  a: TiptapMark[] | undefined,
310→  b: TiptapMark[] | undefined,
311→): boolean {
312→  const aMarks = a ?? [];
313→  const bMarks = b ?? [];
314→  if (aMarks.length !== bMarks.length) return false;
315→  return aMarks.every(
316→    (m, i) => m.type === bMarks[i].type,
317→  );
318→}
319→
320→// ── Pass 1: Block tokeniser ─────────────────────────────────────────────────
321→
322→interface Block {
323→  type:
324→    | "heading"
325→    | "paragraph"
326→    | "bulletList"
327→    | "orderedList"
328→    | "blockquote"
329→    | "codeBlock";
330→  level?: number; // for headings
331→  content: string;
332→  items?: string[]; // for lists
333→}
334→
335→/**
336→ * Tokenise LaTeX into blocks for conversion to Tiptap nodes.
337→ */
338→function tokeniseBlocks(latex: string): Block[] {
339→  const blocks: Block[] = [];
340→  const lines = latex.split("\n");
341→  let i = 0;
342→
343→  while (i < lines.length) {
344→    const line = lines[i];
345→    const trimmed = line.trim();
346→
347→    // Skip empty lines
348→    if (!trimmed) {
349→      i++;
350→      continue;
351→    }
352→
353→    // Headings: \section{...}, \subsection{...}, \subsubsection{...}
354→    // Also handle starred variants
355→    const headingMatch = trimmed.match(
356→      /^\\(section|subsection|subsubsection)\*?\{(.+)\}$/,
357→    );
358→    if (headingMatch) {
359→      const levelMap: Record<string, number> = {
360→        section: 1,
361→        subsection: 2,
362→        subsubsection: 3,
363→      };
364→      blocks.push({
365→        type: "heading",
366→        level: levelMap[headingMatch[1]],
367→        content: headingMatch[2],
368→      });
369→      i++;
370→      continue;
371→    }
372→
373→    // Environment blocks: \begin{env}...\end{env}
374→    const beginMatch = trimmed.match(/^\\begin\{(\w+)\}/);
375→    if (beginMatch) {
376→      const envName = beginMatch[1];
377→      const envContent: string[] = [];
378→      const startLine = trimmed;
379→      i++;
380→
381→      // Collect until \end{envName}
382→      while (i < lines.length) {
383→        const eLine = lines[i];
384→        if (eLine.trim() === `\\end{${envName}}`) {
385→          i++;
386→          break;
387→        }
388→        envContent.push(eLine);
389→        i++;
390→      }
391→
392→      if (envName === "itemize") {
393→        blocks.push({
394→          type: "bulletList",
395→          content: envContent.join("\n"),
396→          items: parseListItems(envContent),
397→        });
398→      } else if (envName === "enumerate") {
399→        blocks.push({
400→          type: "orderedList",
401→          content: envContent.join("\n"),
402→          items: parseListItems(envContent),
403→        });
404→      } else if (envName === "quote") {
405→        blocks.push({
406→          type: "blockquote",
407→          content: envContent.join("\n").trim(),
408→        });
409→      } else {
410→        // Complex environments (longtable, table, figure, etc.) → codeBlock
411→        blocks.push({
412→          type: "codeBlock",
413→          content:
414→            startLine +
415→            "\n" +
416→            envContent.join("\n") +
417→            `\n\\end{${envName}}`,
418→        });
419→      }
420→      continue;
421→    }
422→
423→    // Regular paragraph text — collect until empty line or command
424→    const paraLines: string[] = [];
425→    while (i < lines.length) {
426→      const pLine = lines[i].trim();
427→      if (!pLine) {
428→        i++;
429→        break;
430→      }
431→      // Stop if we hit a block-level command
432→      if (
433→        pLine.match(/^\\(section|subsection|subsubsection)\*?\{/) ||
434→        pLine.match(/^\\begin\{/)
435→      ) {
436→        break;
437→      }
438→      paraLines.push(lines[i]);
439→      i++;
440→    }
441→
442→    if (paraLines.length > 0) {
443→      blocks.push({
444→        type: "paragraph",
445→        content: paraLines.join(" ").trim(),
446→      });
447→    }
448→  }
449→
450→  return blocks;
451→}
452→
453→/**
454→ * Parse \item entries from list environment content lines.
455→ */
456→function parseListItems(lines: string[]): string[] {
457→  const items: string[] = [];
458→  let currentItem = "";
459→
460→  for (const line of lines) {
461→    const trimmed = line.trim();
462→    const itemMatch = trimmed.match(/^\\item\s*(.*)/);
463→    if (itemMatch) {
464→      if (currentItem) {
465→        items.push(currentItem.trim());
466→      }
467→      currentItem = itemMatch[1];
468→    } else if (currentItem && trimmed) {
469→      currentItem += " " + trimmed;
470→    }
471→  }
472→
473→  if (currentItem) {
474→    items.push(currentItem.trim());
475→  }
476→
477→  return items;
478→}
479→
480→// ── Block → Tiptap node conversion ─────────────────────────────────────────
481→
482→function blockToNode(
483→  block: Block,
484→  citationKeys: string[],
485→  warnings: string[],
486→): TiptapNode | TiptapNode[] {
487→  switch (block.type) {
488→    case "heading": {
489→      const inlineNodes = parseInline(block.content, [], citationKeys);
490→      return {
491→        type: "heading",
492→        attrs: { level: block.level ?? 1 },
493→        content: inlineNodes.length > 0 ? inlineNodes : undefined,
494→      };
495→    }
496→
497→    case "paragraph": {
498→      const inlineNodes = parseInline(block.content, [], citationKeys);
499→      return {
500→        type: "paragraph",
501→        content: inlineNodes.length > 0 ? inlineNodes : undefined,
502→      };
503→    }
504→
505→    case "bulletList":
506→    case "orderedList": {
507→      const listItems = (block.items ?? []).map((itemText) => {
508→        const inlineNodes = parseInline(itemText, [], citationKeys);
509→        return {
510→          type: "listItem",
511→          content: [
512→            {
513→              type: "paragraph",
514→              content: inlineNodes.length > 0 ? inlineNodes : undefined,
515→            },
516→          ],
517→        } as TiptapNode;
518→      });
519→      return {
520→        type: block.type,
521→        content: listItems.length > 0 ? listItems : undefined,
522→      };
523→    }
524→
525→    case "blockquote": {
526→      const inlineNodes = parseInline(block.content, [], citationKeys);
527→      return {
528→        type: "blockquote",
529→        content: [
530→          {
531→            type: "paragraph",
532→            content: inlineNodes.length > 0 ? inlineNodes : undefined,
533→          },
534→        ],
535→      };
536→    }
537→
538→    case "codeBlock":
539→      return {
540→        type: "codeBlock",
541→        content: [
542→          {
543→            type: "text",
544→            text: block.content,
545→          },
546→        ],
547→      };
548→
549→    default: {
550→      warnings.push(`Unknown block type: ${block.type}`);
551→      return {
552→        type: "paragraph",
553→        content: [{ type: "text", text: block.content }],
554→      };
555→    }
556→  }
557→}
558→
559→// ── Public API ──────────────────────────────────────────────────────────────
560→
561→export function latexToTiptap(latex: string): LatexToTiptapResult {
562→  const warnings: string[] = [];
563→  const citationKeys: string[] = [];
564→
565→  if (!latex || !latex.trim()) {
566→    return {
567→      json: { type: "doc", content: [{ type: "paragraph" }] },
568→      citationKeys: [],
569→      warnings: [],
570→    };
571→  }
572→
573→  const cleaned = preprocess(latex);
574→  const blocks = tokeniseBlocks(cleaned);
575→
576→  const content: TiptapNode[] = [];
577→  for (const block of blocks) {
578→    const node = blockToNode(block, citationKeys, warnings);
579→    if (Array.isArray(node)) {
580→      content.push(...node);
581→    } else {
582→      content.push(node);
583→    }
584→  }
585→
586→  // Ensure at least one node
587→  if (content.length === 0) {
588→    content.push({ type: "paragraph" });
589→  }
590→
591→  return {
592→    json: { type: "doc", content },
593→    citationKeys,
594→    warnings,
595→  };
596→}
