 1→import { NextRequest, NextResponse } from "next/server";
 2→import { getAuthenticatedUser } from "@/lib/api/auth";
 3→import {
 4→  unauthorised,
 5→  notFound,
 6→  badRequest,
 7→  internalError,
 8→} from "@/lib/api/errors";
 9→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
10→import { isValidPhase } from "@/lib/phases/transitions";
11→import { reviewSection } from "@/lib/ai/review-section";
12→import type { Section } from "@/lib/types/database";
13→
14→export async function POST(
15→  _request: NextRequest,
16→  { params }: { params: Promise<{ id: string; phase: string }> }
17→) {
18→  try {
19→    const authResult = await getAuthenticatedUser();
20→    if (!authResult) return unauthorised();
21→
22→    const { id, phase } = await params;
23→    const phaseNumber = parseInt(phase, 10);
24→
25→    if (!isValidPhase(phaseNumber)) {
26→      return badRequest("Invalid phase number");
27→    }
28→
29→    const supabase = createAdminSupabaseClient();
30→
31→    // Verify project ownership
32→    const { data: project, error: projectError } = await supabase
33→      .from("projects")
34→      .select("id")
35→      .eq("id", id)
36→      .eq("user_id", authResult.user.id)
37→      .single();
38→
39→    if (projectError || !project) {
40→      return notFound("Project not found");
41→    }
42→
43→    // Fetch section
44→    const { data: section, error: sectionError } = await supabase
45→      .from("sections")
46→      .select("*")
47→      .eq("project_id", id)
48→      .eq("phase_number", phaseNumber)
49→      .single();
50→
51→    if (sectionError || !section) {
52→      return notFound("Section not found — generate content first");
53→    }
54→
55→    const typedSection = section as Section;
56→
57→    const result = reviewSection(
58→      typedSection.latex_content,
59→      typedSection.ai_generated_latex,
60→      phaseNumber,
61→    );
62→
63→    return NextResponse.json({ data: result });
64→  } catch (err) {
65→    console.error(
66→      "Unexpected error in POST /api/projects/[id]/sections/[phase]/review:",
67→      err,
68→    );
69→    return internalError();
70→  }
71→}
