  1→import { generateTex } from "./generate-tex";
  2→import type { Project, Section, Citation } from "@/lib/types/database";
  3→
  4→// ── Phase → label mapping (from main.tex template) ─────────────────────────
  5→
  6→const PHASE_LABEL_MAP: Record<number, string> = {
  7→  2: "chap:introduction",
  8→  3: "chap:aims",
  9→  4: "chap:literature",
 10→  5: "chap:methodology",
 11→  6: "chap:results",
 12→  7: "chap:discussion",
 13→  8: "chap:conclusion",
 14→};
 15→
 16→// ── BibTeX splitter ─────────────────────────────────────────────────────────
 17→
 18→const BIBTEX_SEPARATOR = "---BIBTEX---";
 19→
 20→export interface SplitResult {
 21→  body: string;
 22→  bib: string;
 23→}
 24→
 25→/**
 26→ * Split section latex_content at the `---BIBTEX---` marker.
 27→ * Returns the body (before marker) and bib entries (after marker).
 28→ */
 29→export function splitBibtex(latexContent: string): SplitResult {
 30→  const idx = latexContent.indexOf(BIBTEX_SEPARATOR);
 31→  if (idx === -1) {
 32→    return { body: latexContent.trim(), bib: "" };
 33→  }
 34→  return {
 35→    body: latexContent.slice(0, idx).trim(),
 36→    bib: latexContent.slice(idx + BIBTEX_SEPARATOR.length).trim(),
 37→  };
 38→}
 39→
 40→// ── Placeholder replacement ─────────────────────────────────────────────────
 41→
 42→/**
 43→ * Replace the `%% [[PLACEHOLDER — Phase N]]` comment block after
 44→ * `\label{chap:xxx}` with actual section content.
 45→ *
 46→ * The regex captures everything between `\label{labelKey}` (+ optional
 47→ * whitespace/comments) and the next structural delimiter.
 48→ */
 49→export function replacePlaceholder(
 50→  tex: string,
 51→  labelKey: string,
 52→  content: string
 53→): string {
 54→  // Escape the label for use in regex
 55→  const escapedLabel = labelKey.replace(/[.*+?^${}()|[\]\\]/g, "\\$&");
 56→
 57→  // Match from \label{chap:xxx} through placeholder comment block,
 58→  // stopping before the next structural element.
 59→  // The placeholder block is: optional blank lines + %% [[PLACEHOLDER ...]] + optional comment lines
 60→  const pattern = new RegExp(
 61→    `(\\\\label\\{${escapedLabel}\\})` + // group 1: the label itself
 62→      `(\\s*(?:%%.*)*)`, // group 2: whitespace + comment lines (placeholder block)
 63→    "m"
 64→  );
 65→
 66→  return tex.replace(pattern, `$1\n\n${content}\n`);
 67→}
 68→
 69→// ── BibTeX deduplication ────────────────────────────────────────────────────
 70→
 71→/**
 72→ * Parse `@type{key,` entries from BibTeX content, deduplicate by cite key
 73→ * (last-write-wins so citations table entries override AI-generated ones).
 74→ */
 75→export function deduplicateBibEntries(bibContent: string): string {
 76→  if (!bibContent.trim()) return "";
 77→
 78→  // Split into individual entries by matching @type{key,
 79→  const entryPattern = /(@\w+\{[^,]+,[\s\S]*?(?:\n\}|\n\}\s*$))/gm;
 80→
 81→  // More robust: split on lines that start a new entry
 82→  const entries: { key: string; entry: string }[] = [];
 83→  const lines = bibContent.split("\n");
 84→  let currentEntry = "";
 85→  let currentKey = "";
 86→
 87→  for (const line of lines) {
 88→    const entryStart = line.match(/^@(\w+)\{([^,]+),/);
 89→    if (entryStart) {
 90→      // Save previous entry
 91→      if (currentEntry && currentKey) {
 92→        entries.push({ key: currentKey, entry: currentEntry.trim() });
 93→      }
 94→      currentKey = entryStart[2].trim();
 95→      currentEntry = line + "\n";
 96→    } else if (currentKey) {
 97→      currentEntry += line + "\n";
 98→    }
 99→  }
100→
101→  // Push last entry
102→  if (currentEntry && currentKey) {
103→    entries.push({ key: currentKey, entry: currentEntry.trim() });
104→  }
105→
106→  // Deduplicate: last-write-wins
107→  const seen = new Map<string, string>();
108→  for (const { key, entry } of entries) {
109→    seen.set(key, entry);
110→  }
111→
112→  return Array.from(seen.values()).join("\n\n");
113→}
114→
115→// ── Main assembly function ──────────────────────────────────────────────────
116→
117→export interface AssembleResult {
118→  tex: string;
119→  bib: string;
120→  warnings: string[];
121→}
122→
123→/**
124→ * Assemble the full thesis .tex and .bib from template, project metadata,
125→ * approved/review sections, and citations.
126→ *
127→ * 1. Populate metadata via `generateTex()`
128→ * 2. For each phase 2–8: find the section, split body/bib, replace placeholder
129→ * 3. Collect BibTeX from section trailers + citations table
130→ * 4. Deduplicate BibTeX entries
131→ */
132→export function assembleThesisContent(
133→  template: string,
134→  project: Project,
135→  sections: Section[],
136→  citations: Citation[]
137→): AssembleResult {
138→  const warnings: string[] = [];
139→
140→  // Step 1: Populate metadata
141→  const { tex: metadataTex, warnings: metaWarnings } = generateTex(template, project);
142→  warnings.push(...metaWarnings);
143→
144→  let tex = metadataTex;
145→  const bibParts: string[] = [];
146→
147→  // Step 2: For each chapter phase, inject section content
148→  for (const [phaseStr, labelKey] of Object.entries(PHASE_LABEL_MAP)) {
149→    const phaseNum = parseInt(phaseStr, 10);
150→
151→    // Find the best section: prefer approved, then review
152→    const section = sections
153→      .filter((s) => s.phase_number === phaseNum)
154→      .sort((a, b) => {
155→        const priority: Record<string, number> = { approved: 0, review: 1 };
156→        return (priority[a.status] ?? 2) - (priority[b.status] ?? 2);
157→      })[0];
158→
159→    if (!section || !section.latex_content) {
160→      warnings.push(`Phase ${phaseNum}: no approved/review content — placeholder retained`);
161→      continue;
162→    }
163→
164→    const { body, bib } = splitBibtex(section.latex_content);
165→
166→    if (body) {
167→      tex = replacePlaceholder(tex, labelKey, body);
168→    } else {
169→      warnings.push(`Phase ${phaseNum}: section exists but body is empty`);
170→    }
171→
172→    if (bib) {
173→      bibParts.push(bib);
174→    }
175→  }
176→
177→  // Step 3: Collect BibTeX from citations table
178→  for (const citation of citations) {
179→    if (citation.bibtex_entry?.trim()) {
180→      bibParts.push(citation.bibtex_entry.trim());
181→    }
182→  }
183→
184→  // Step 4: Deduplicate
185→  const rawBib = bibParts.join("\n\n");
186→  const bib = deduplicateBibEntries(rawBib);
187→
188→  return { tex, bib, warnings };
189→}
