  4→// Helper: wrap inline content in a doc > paragraph
  5→function docWith(...paragraphs: TiptapNode[]) {
  6→  return {
  7→    type: "doc" as const,
  8→    content: paragraphs,
  9→  };
 10→}
 11→
 12→function para(...content: TiptapNode[]): TiptapNode {
 13→  return { type: "paragraph", content };
 14→}
 15→
 16→function text(t: string, marks?: { type: string }[]): TiptapNode {
 17→  return { type: "text", text: t, ...(marks ? { marks } : {}) };
 18→}
 19→
 20→describe("tiptapToLatex", () => {
