190→      para(
191→        text("Normal "),
192→        text("bold", [{ type: "bold" }]),
193→        text(" and "),
194→        text("italic", [{ type: "italic" }]),
195→        text(" text")
196→      )
197→    );
198→    const result = tiptapToLatex(doc);
199→    expect(result.latex).toBe(
200→      "Normal \\textbf{bold} and \\textit{italic} text\n"
201→    );
202→  });
203→
204→  it("should handle heading with formatted content", () => {
205→    const doc = docWith({
206→      type: "heading",
207→      attrs: { level: 1 },
208→      content: [
209→        text("Review of "),
210→        text("Literature", [{ type: "italic" }]),
211→      ],
212→    });
213→    const result = tiptapToLatex(doc);
214→    expect(result.latex).toBe(
215→      "\\section{Review of \\textit{Literature}}\n"
216→    );
217→  });
218→
219→  it("should warn when root node is not doc", () => {
220→    const notDoc = { type: "paragraph", content: [text("Hello")] };
221→    const result = tiptapToLatex(notDoc);
222→    expect(result.warnings).toContain("Root node is not 'doc', wrapping as-is");
223→    expect(result.latex).toContain("Hello");
224→  });
225→});
