  1→import { NextRequest, NextResponse } from "next/server";
  2→import { readFile } from "fs/promises";
  3→import path from "path";
  4→import { getAuthenticatedUser } from "@/lib/api/auth";
  5→import {
  6→  unauthorised,
  7→  notFound,
  8→  internalError,
  9→  conflict,
 10→  queueFull,
 11→} from "@/lib/api/errors";
 12→import { tryAcquire, release } from "@/lib/compute/semaphore";
 13→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 14→import { assembleThesisContent } from "@/lib/latex/assemble";
 15→import { compileTex } from "@/lib/latex/compile";
 16→import type { Project, Section, Citation, Compilation } from "@/lib/types/database";
 17→
 18→export async function POST(
 19→  _request: NextRequest,
 20→  { params }: { params: Promise<{ id: string }> }
 21→) {
 22→  try {
 23→    const authResult = await getAuthenticatedUser();
 24→    if (!authResult) return unauthorised();
 25→
 26→    const { id } = await params;
 27→    const supabase = createAdminSupabaseClient();
 28→
 29→    // Fetch project
 30→    const { data: project, error: projectError } = await supabase
 31→      .from("projects")
 32→      .select("*")
 33→      .eq("id", id)
 34→      .eq("user_id", authResult.user.id)
 35→      .single();
 36→
 37→    if (projectError || !project) {
 38→      return notFound("Project not found");
 39→    }
 40→
 41→    const typedProject = project as Project;
 42→
 43→    // Check for existing running compilation
 44→    const { data: runningCompilation } = await supabase
 45→      .from("compilations")
 46→      .select("id")
 47→      .eq("project_id", id)
 48→      .eq("status", "running")
 49→      .single();
 50→
 51→    if (runningCompilation) {
 52→      return conflict("A compilation is already in progress");
 53→    }
 54→
 55→    // Admission control — acquire compute slot
 56→    const slot = tryAcquire("compile", id);
 57→    if (!slot.acquired) {
 58→      if (slot.position !== undefined) {
 59→        return queueFull(Math.ceil((slot.estimatedWaitMs ?? 30000) / 1000));
 60→      }
 61→      return queueFull();
 62→    }
 63→
 64→    try {
 65→      // Create compilation record
 66→      const { data: compilation, error: compilationError } = await supabase
 67→        .from("compilations")
 68→        .insert({
 69→          project_id: id,
 70→          trigger: "manual",
 71→          status: "running",
 72→          warnings: [],
 73→          errors: [],
 74→        })
 75→        .select("*")
 76→        .single();
 77→
 78→      if (compilationError || !compilation) {
 79→        console.error("Failed to create compilation record:", compilationError);
 80→        return internalError("Failed to start compilation");
 81→      }
 82→
 83→      // Read the template
 84→      let template: string;
 85→      try {
 86→        const templatesDir = path.resolve(process.cwd(), "../../templates");
 87→        template = await readFile(path.join(templatesDir, "main.tex"), "utf-8");
 88→      } catch {
 89→        await updateCompilation(supabase, compilation.id, {
 90→          status: "failed",
 91→          errors: ["Template file not found"],
 92→        });
 93→        return internalError("LaTeX template not found");
 94→      }
 95→
 96→      // Fetch sections and citations for assembly
 97→      const [sectionsResult, citationsResult] = await Promise.all([
 98→        supabase
 99→          .from("sections")
100→          .select("*")
101→          .eq("project_id", id)
102→          .in("status", ["approved", "review"]),
103→        supabase
104→          .from("citations")
105→          .select("*")
106→          .eq("project_id", id),
107→      ]);
108→
109→      const sections = (sectionsResult.data ?? []) as Section[];
110→      const citations = (citationsResult.data ?? []) as Citation[];
111→
112→      // Assemble full thesis content (metadata + section bodies + BibTeX)
113→      const { tex, bib, warnings: texWarnings } = assembleThesisContent(
114→        template,
115→        typedProject,
116→        sections,
117→        citations
118→      );
119→
120→      // Compile
121→      const isWatermark = typedProject.status === "sandbox";
122→      const result = await compileTex(tex, {
123→        projectId: id,
124→        watermark: isWatermark,
125→        bibContent: bib,
126→      });
127→
128→      // Update compilation record
129→      const allWarnings = [...texWarnings, ...result.log.warnings.map(String)];
130→
131→      if (result.success && result.pdfPath) {
132→        // Read PDF and store URL (in production this would upload to R2)
133→        // For now, store the local path reference
134→        await updateCompilation(supabase, compilation.id, {
135→          status: "completed",
136→          warnings: allWarnings,
137→          errors: [],
138→          pdf_url: result.pdfPath,
139→          log_text: result.rawLog.slice(0, 50000),
140→          compile_time_ms: result.compileTimeMs,
141→        });
142→
143→        return NextResponse.json({
144→          data: {
145→            compilation_id: compilation.id,
146→            status: "completed",
147→            warnings: allWarnings,
148→            errors: [],
149→            compile_time_ms: result.compileTimeMs,
150→          },
151→        });
152→      }
153→
154→      // Compilation failed
155→      await updateCompilation(supabase, compilation.id, {
156→        status: "failed",
157→        warnings: allWarnings,
158→        errors: result.log.errors,
159→        log_text: result.rawLog.slice(0, 50000),
160→        compile_time_ms: result.compileTimeMs,
161→      });
162→
163→      return NextResponse.json(
164→        {
165→          data: {
166→            compilation_id: compilation.id,
167→            status: "failed",
168→            warnings: allWarnings,
169→            errors: result.log.errors,
170→            compile_time_ms: result.compileTimeMs,
171→          },
172→        },
173→        { status: 422 }
174→      );
175→    } finally {
176→      release(slot.jobId!);
177→    }
178→  } catch (err) {
179→    console.error("Unexpected error in POST /api/projects/[id]/compile:", err);
180→    return internalError();
181→  }
182→}
183→
184→async function updateCompilation(
185→  supabase: ReturnType<typeof createAdminSupabaseClient>,
186→  compilationId: string,
187→  data: Partial<Compilation>
188→) {
189→  await supabase
190→    .from("compilations")
191→    .update(data)
192→    .eq("id", compilationId);
193→}
