  1→/**
  2→ * Tiptap JSON AST → LaTeX serialiser.
  3→ *
  4→ * Walks a Tiptap/ProseMirror JSON document and emits LaTeX suitable for
  5→ * inclusion in a thesis section body. Uses escapeLatex() for all user text.
  6→ */
  7→
  8→import { escapeLatex } from "./escape";
  9→
 10→// ── Tiptap JSON node types ──────────────────────────────────────────────────
 11→
 12→export interface TiptapMark {
 13→  type: "bold" | "italic" | "code" | "underline" | "link" | string;
 14→  attrs?: Record<string, unknown>;
 15→}
 16→
 17→export interface TiptapNode {
 18→  type: string;
 19→  content?: TiptapNode[];
 20→  text?: string;
 21→  marks?: TiptapMark[];
 22→  attrs?: Record<string, unknown>;
 23→}
 24→
 25→// ── Result type ─────────────────────────────────────────────────────────────
 26→
 27→export interface TiptapToLatexResult {
 28→  latex: string;
 29→  citationKeys: string[];
 30→  warnings: string[];
 31→}
 32→
 33→// ── Citation key extraction ─────────────────────────────────────────────────
 34→
 35→const CITE_REGEX = /\\cite\{([^}]+)\}/g;
 36→
 37→function extractCitationKeys(latex: string): string[] {
 38→  const keys = new Set<string>();
 39→  let match: RegExpExecArray | null;
 40→  while ((match = CITE_REGEX.exec(latex)) !== null) {
 41→    for (const key of match[1].split(",")) {
 42→      const trimmed = key.trim();
 43→      if (trimmed) keys.add(trimmed);
 44→    }
 45→  }
 46→  return [...keys];
 47→}
 48→
 49→// ── Heading depth mapping ───────────────────────────────────────────────────
 50→
 51→function headingCommand(level: number): string {
 52→  switch (level) {
 53→    case 1:
 54→      return "\\section";
 55→    case 2:
 56→      return "\\subsection";
 57→    case 3:
 58→      return "\\subsubsection";
 59→    default:
 60→      return "\\paragraph";
 61→  }
 62→}
 63→
 64→// ── Mark wrappers ───────────────────────────────────────────────────────────
 65→
 66→function applyMarks(text: string, marks: TiptapMark[]): string {
 67→  // Citation commands in code marks pass through raw
 68→  if (
 69→    marks.length === 1 &&
 70→    marks[0].type === "code" &&
 71→    /^\\cite\{[^}]+\}$/.test(text)
 72→  ) {
 73→    return text;
 74→  }
 75→
 76→  let result = text;
 77→  for (const mark of marks) {
 78→    switch (mark.type) {
 79→      case "bold":
 80→        result = `\\textbf{${result}}`;
 81→        break;
 82→      case "italic":
 83→        result = `\\textit{${result}}`;
 84→        break;
 85→      case "code":
 86→        result = `\\texttt{${result}}`;
 87→        break;
 88→      case "underline":
 89→        result = `\\underline{${result}}`;
 90→        break;
 91→      // Other marks (link, etc.) are passed through as plain text
 92→    }
 93→  }
 94→  return result;
 95→}
 96→
 97→// ── Node serialisation ──────────────────────────────────────────────────────
 98→
 99→function serializeNode(node: TiptapNode, warnings: string[]): string {
100→  switch (node.type) {
101→    case "doc":
102→      return serializeChildren(node, warnings);
103→
104→    case "paragraph":
105→      return serializeChildren(node, warnings) + "\n\n";
106→
107→    case "heading": {
108→      const level = (node.attrs?.level as number) ?? 1;
109→      const cmd = headingCommand(level);
110→      const content = serializeChildren(node, warnings);
111→      return `${cmd}{${content}}\n\n`;
112→    }
113→
114→    case "bulletList":
115→      return (
116→        "\\begin{itemize}\n" +
117→        serializeChildren(node, warnings) +
118→        "\\end{itemize}\n\n"
119→      );
120→
121→    case "orderedList":
122→      return (
123→        "\\begin{enumerate}\n" +
124→        serializeChildren(node, warnings) +
125→        "\\end{enumerate}\n\n"
126→      );
127→
