191→
192→const AMERICAN_TO_BRITISH: [RegExp, string][] = [
193→  [/\banalyze\b/gi, "analyse"],
194→  [/\banalyzed\b/gi, "analysed"],
195→  [/\banalyzing\b/gi, "analysing"],
196→  [/\bbehavior\b/gi, "behaviour"],
197→  [/\bcolor\b/gi, "colour"],
