211→  [/\bpediatric\b/gi, "paediatric"],
212→  [/\banesthesia\b/gi, "anaesthesia"],
213→  [/\bhemoglobin\b/gi, "haemoglobin"],
214→  [/\besophagus\b/gi, "oesophagus"],
