218→  const issues: ReviewIssue[] = [];
219→  // Strip LaTeX commands to only check prose
220→  const prose = latex
221→    .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
222→    .replace(/\\[a-zA-Z]+/g, " ");
223→
224→  for (const [pattern, suggestion] of AMERICAN_TO_BRITISH) {
225→    const match = pattern.exec(prose);
226→    if (match) {
227→      issues.push({
228→        severity: "info",
229→        category: "spelling",
230→        message: `"${match[0]}" → consider British English spelling "${suggestion}"`,
231→      });
