 68→declare global {
 69→  interface Window {
 70→    Razorpay?: new (options: Record<string, unknown>) => {
 71→      open: () => void;
 72→      on: (event: string, handler: () => void) => void;
 73→    };
 74→  }
 75→}
 76→
 77→export default function CheckoutPage() {
 78→  return (
 79→    <Suspense fallback={<div className="animate-pulse space-y-4 p-8"><div className="h-8 w-48 rounded bg-[#F0F0F0]" /><div className="h-64 rounded bg-[#F5F5F5]" /></div>}>
 80→      <CheckoutContent />
 81→    </Suspense>
 82→  );
 83→}
 84→
 85→function CheckoutContent() {
 86→  const searchParams = useSearchParams();
 87→  const router = useRouter();
 88→  const attachProjectId = searchParams.get("attach");
 89→  const [currency, setCurrency] = useState<"INR" | "USD">("INR");
 90→  const [loading, setLoading] = useState<string | null>(null);
 91→
 92→  async function handleCheckout(planType: string) {
 93→    setLoading(planType);
 94→
 95→    try {
 96→      const res = await fetch("/api/checkout", {
 97→        method: "POST",
 98→        headers: { "Content-Type": "application/json" },
 99→        body: JSON.stringify({
100→          plan_type: planType,
101→          currency,
102→          ...(attachProjectId ? { project_id: attachProjectId } : {}),
103→        }),
104→      });
105→
106→      if (!res.ok) {
107→        const err = await res.json();
108→        throw new Error(err.error?.message ?? "Checkout failed");
109→      }
110→
111→      const { data } = await res.json();
112→
113→      if (data.provider === "stripe" && data.redirect_url) {
114→        // Stripe — redirect to hosted checkout
115→        window.location.href = data.redirect_url;
116→        return;
117→      }
118→
119→      if (data.provider === "razorpay" && data.order_id) {
120→        // Razorpay — open checkout modal
121→        await openRazorpayCheckout(data);
122→        return;
123→      }
124→
125→      throw new Error("Unknown payment provider response");
126→    } catch (err) {
127→      toast.error(
128→        err instanceof Error ? err.message : "Payment initiation failed"
129→      );
130→    } finally {
131→      setLoading(null);
132→    }
133→  }
134→
135→  async function openRazorpayCheckout(data: {
136→    order_id: string;
137→    key_id: string;
138→    amount: number;
139→    currency: string;
140→    notes: Record<string, string>;
141→  }) {
142→    // Load Razorpay script if not already loaded
143→    if (!window.Razorpay) {
144→      await new Promise<void>((resolve, reject) => {
145→        const script = document.createElement("script");
146→        script.src = "https://checkout.razorpay.com/v1/checkout.js";
147→        script.onload = () => resolve();
148→        script.onerror = () => reject(new Error("Failed to load Razorpay"));
149→        document.body.appendChild(script);
150→      });
151→    }
152→
153→    const razorpay = new window.Razorpay!({
154→      key: data.key_id,
155→      amount: data.amount,
156→      currency: data.currency,
157→      name: "Apollo Thesis",
158→      description: data.notes.plan_label ?? "Thesis Licence",
159→      order_id: data.order_id,
160→      notes: data.notes,
161→      theme: { color: "#2F2F2F" },
162→      handler: () => {
163→        toast.success("Payment successful! Your licence is being provisioned.");
164→        // Redirect to dashboard after short delay to allow webhook processing
165→        setTimeout(() => {
166→          if (attachProjectId) {
167→            router.push(`/projects/${attachProjectId}`);
168→          } else {
169→            router.push("/dashboard?payment=success");
170→          }
171→        }, 2000);
172→      },
173→      modal: {
174→        ondismiss: () => {
175→          setLoading(null);
176→        },
177→      },
178→    });
179→
180→    razorpay.open();
181→  }
182→
183→  return (
184→    <div className="mx-auto max-w-4xl space-y-8">
185→      <div className="space-y-2">
186→        <Link
187→          href={attachProjectId ? `/projects/${attachProjectId}` : "/dashboard"}
188→          className="inline-flex items-center gap-1 text-sm text-[#6B6B6B] hover:text-[#2F2F2F] transition-colors"
189→        >
190→          <ArrowLeft className="h-3.5 w-3.5" />
191→          Back
192→        </Link>
193→        <h1 className="font-serif text-2xl font-bold text-[#2F2F2F]">
194→          Choose a Plan
195→        </h1>
196→        <p className="text-sm text-[#6B6B6B]">
197→          Attach a thesis licence to continue generating your thesis beyond
198→          Phase 1.
199→          {attachProjectId && (
200→            <span className="ml-1 font-medium text-[#8B9D77]">
201→              The licence will be attached to your current project.
202→            </span>
203→          )}
