"use client";

import React, { useMemo, useRef } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import {
  Environment,
  RoundedBox,
  Text,
  ContactShadows,
} from "@react-three/drei";
import { EffectComposer, DepthOfField } from "@react-three/postprocessing";
import * as THREE from "three";

// --- 3D COMPONENTS ---

const NetworkTopology: React.FC = () => {
  // Generate a complex geometric shape and type the extracted vertices
  const { vertices, edgesGeometry } = useMemo(() => {
    const geom = new THREE.IcosahedronGeometry(2.2, 1);
    const posAttribute = geom.attributes.position;
    const verts: THREE.Vector3[] = [];

    for (let i = 0; i < posAttribute.count; i++) {
      verts.push(new THREE.Vector3().fromBufferAttribute(posAttribute, i));
    }

    // Remove duplicate vertices to place spheres properly
    const uniqueVerts = verts.filter(
      (v, i, a) => a.findIndex((v2) => v2.distanceTo(v) < 0.1) === i
    );

    return {
      vertices: uniqueVerts,
      edgesGeometry: new THREE.EdgesGeometry(geom),
    };
  }, []);

  // Strictly type the ref to a Three.js Group
  const ref = useRef<THREE.Group>(null);

  // Slow, subtle floating animation with a null check for TypeScript
  useFrame((state) => {
    if (ref.current) {
      const t = state.clock.getElapsedTime();
      ref.current.rotation.y = Math.sin(t / 4) * 0.1;
      ref.current.position.y = Math.sin(t / 2) * 0.1;
    }
  });

  return (
    <group ref={ref} position={[-1.5, 1, 0]}>
      {/* The wireframe connecting the nodes */}
      <lineSegments geometry={edgesGeometry}>
        <lineBasicMaterial
          color="#4A4A4A"
          transparent
          opacity={0.3}
          linewidth={1}
        />
      </lineSegments>

      {/* The ceramic nodes */}
      {vertices.map((pos, i) => (
        <mesh key={i} position={pos} castShadow receiveShadow>
          <sphereGeometry args={[0.18, 32, 32]} />
          <meshStandardMaterial
            color={i % 3 === 0 ? "#D4A373" : "#8B9D77"}
            roughness={0.8}
            metalness={0.1}
          />
        </mesh>
      ))}
    </group>
  );
};

const FacingTablet: React.FC = () => {
  return (
    <group position={[2.5, -0.5, 1.5]}>
      {/* Device Bezel */}
      <RoundedBox args={[3.2, 4.5, 0.15]} radius={0.1} castShadow receiveShadow>
        <meshStandardMaterial color="#EAEAEA" roughness={0.4} />
      </RoundedBox>

      {/* E-ink Screen */}
      <mesh position={[0, 0, 0.08]} receiveShadow>
        <planeGeometry args={[2.9, 4.2]} />
        <meshStandardMaterial color="#FAFAFA" roughness={0.9} />
      </mesh>

      {/* Screen Content */}
      <group position={[-1.2, 1.6, 0.09]}>
        <Text
          color="#2C2C2C"
          fontSize={0.22}
          font="https://fonts.gstatic.com/s/tiempos/v1/..."
          anchorX="left"
          anchorY="top"
          maxWidth={2.6}
          lineHeight={1.2}
        >
          Apollo Research Platform:
        </Text>

        <Text
          position={[0, -0.35, 0]}
          color="#4A4A4A"
          fontSize={0.14}
          anchorX="left"
          anchorY="top"
          maxWidth={2.6}
        >
          Connecting knowledge, accelerating discovery. Version 3.2.
        </Text>

        <Text
          position={[0, -1.2, 0]}
          color="#2C2C2C"
          fontSize={0.16}
          anchorX="left"
          anchorY="top"
        >
          Recent Publications:
        </Text>

        <Text
          position={[0, -1.5, 0]}
          color="#6B6B6B"
          fontSize={0.13}
          anchorX="left"
          anchorY="top"
          maxWidth={2.6}
        >
          {`"Quantum Field Topology in Complex Systems" - A. Sharma, et al., 2025.`}
        </Text>
      </group>
    </group>
  );
};

const GlassPaperweight: React.FC = () => {
  return (
    <mesh position={[4.2, -2.2, 2.5]} castShadow rotation={[0.4, 0.2, 0.3]}>
      <dodecahedronGeometry args={[0.6, 0]} />
      <meshPhysicalMaterial
        transmission={1}
        opacity={1}
        roughness={0.05}
        ior={1.5}
        thickness={0.8}
        color="#ffffff"
        attenuationColor="#ffffff"
        attenuationDistance={1}
      />
    </mesh>
  );
};

const StylizedPampasVase: React.FC = () => {
  return (
    <group position={[-4.5, -1.5, 1]}>
      {/* Frosted Glass Vase */}
      <mesh position={[0, 0, 0]} castShadow receiveShadow>
        <cylinderGeometry args={[0.4, 0.4, 1.5, 32]} />
        <meshPhysicalMaterial
          transmission={0.8}
          roughness={0.3}
          ior={1.4}
          thickness={0.2}
          color="#F5F5F0"
        />
      </mesh>

      {/* Stylized Dried Grass Stem */}
      <mesh position={[0, 1.5, 0]} rotation={[0, 0, -0.1]}>
        <cylinderGeometry args={[0.02, 0.04, 3, 8]} />
        <meshStandardMaterial color="#D4A373" roughness={1} />
      </mesh>

      {/* Stylized Fluff */}
      <mesh position={[0.1, 2.5, 0]} rotation={[0, 0, -0.1]}>
        <coneGeometry args={[0.3, 1.5, 8]} />
        <meshStandardMaterial
          color="#E5D3B3"
          roughness={1}
          transparent
          opacity={0.8}
        />
      </mesh>
    </group>
  );
};

// --- MAIN LAYOUT COMPONENT ---

export default function ApolloLeftPane(): JSX.Element {
  return (
    <div className="relative w-full h-screen bg-[#F5F5F0] overflow-hidden flex items-center justify-center">
      {/* Minimalist Dot Grid Pattern */}
      <div
        className="absolute inset-0 z-0 opacity-60"
        style={{
          backgroundImage: "radial-gradient(#E5E5E5 1.5px, transparent 1.5px)",
          backgroundSize: "24px 24px",
        }}
      />

      {/* 3D Canvas */}
      <div className="absolute inset-0 z-10 pl-[80px]">
        <Canvas shadows camera={{ position: [0, 1.5, 10], fov: 45 }}>
          <ambientLight intensity={0.5} />
          <directionalLight
            position={[-8, 8, 4]}
            intensity={1.5}
            castShadow
            shadow-mapSize={[2048, 2048]}
            shadow-bias={-0.0001}
          />

          <Environment preset="city" />

          <NetworkTopology />
          <FacingTablet />
          <GlassPaperweight />
          <StylizedPampasVase />

          {/* White Ceramic Platform Base */}
          <mesh position={[-1.5, -2.4, 0]} receiveShadow>
            <cylinderGeometry args={[4.5, 4.5, 0.2, 64]} />
            <meshStandardMaterial color="#FFFFFF" roughness={0.2} />
          </mesh>

          <ContactShadows
            position={[0, -2.5, 0]}
            opacity={0.4}
            scale={20}
            blur={2}
            far={4}
          />

          <EffectComposer>
            <DepthOfField
              focusDistance={0.05}
              focalLength={0.04}
              bokehScale={3}
              height={480}
            />
          </EffectComposer>
        </Canvas>
      </div>
    </div>
  );
}

