"use client";

import { useRef, useMemo, useEffect, useState } from "react";
import { Canvas, useFrame } from "@react-three/fiber";
import {
  Float,
  Environment,
  ContactShadows,
  MeshTransmissionMaterial,
  RoundedBox,
} from "@react-three/drei";
import * as THREE from "three";

// ── Reduced motion detection ────────────────────────────────────────────────
function useReducedMotion() {
  const [reduced, setReduced] = useState(false);

  useEffect(() => {
    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
    setReduced(mq.matches);
    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
    mq.addEventListener("change", handler);
    return () => mq.removeEventListener("change", handler);
  }, []);

  return reduced;
}

// ── Procedural studio environment (no external HDR download) ────────────────
function StudioEnvironment() {
  return (
    <Environment frames={1} resolution={128} background={false}>
      {/* Warm key from above */}
      <mesh position={[0, 8, 0]} scale={30}>
        <planeGeometry />
        <meshBasicMaterial color="#FFFEF8" />
      </mesh>
      {/* Warm bounce from below */}
      <mesh position={[0, -6, 0]} scale={30} rotation={[Math.PI, 0, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#F0EBE0" />
      </mesh>
      {/* Warm fill from left */}
      <mesh position={[-8, 2, 0]} scale={20} rotation={[0, Math.PI / 2, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#FFF5E8" />
      </mesh>
      {/* Cool fill from right */}
      <mesh position={[8, 2, 0]} scale={20} rotation={[0, -Math.PI / 2, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#E8ECF0" />
      </mesh>
      {/* Neutral backlight */}
      <mesh position={[0, 3, 8]} scale={15} rotation={[0, Math.PI, 0]}>
        <planeGeometry />
        <meshBasicMaterial color="#FFFFFF" />
      </mesh>
    </Environment>
  );
}

// ── Network topology — icosahedron wireframe with ceramic nodes ─────────────
function NetworkTopology({ reducedMotion }: { reducedMotion: boolean }) {
  const { vertices, edgesGeometry } = useMemo(() => {
    const geom = new THREE.IcosahedronGeometry(2.0, 1);
    const posAttribute = geom.attributes.position;
    const verts: THREE.Vector3[] = [];

    for (let i = 0; i < posAttribute.count; i++) {
      verts.push(new THREE.Vector3().fromBufferAttribute(posAttribute, i));
    }

    // Deduplicate vertices (icosahedron shares vertex positions)
    const uniqueVerts = verts.filter(
      (v, idx, arr) => arr.findIndex((v2) => v2.distanceTo(v) < 0.1) === idx
    );

    return {
      vertices: uniqueVerts,
      edgesGeometry: new THREE.EdgesGeometry(geom),
    };
  }, []);

  const groupRef = useRef<THREE.Group>(null);

  useFrame(({ clock }) => {
    if (reducedMotion || !groupRef.current) return;
    const t = clock.getElapsedTime();
    // Slow orbital drift
    groupRef.current.rotation.y = Math.sin(t * 0.15) * 0.12;
    groupRef.current.rotation.x = Math.cos(t * 0.1) * 0.04;
    // Gentle vertical float
    groupRef.current.position.y = 0.8 + Math.sin(t * 0.4) * 0.08;
  });

  return (
    <group ref={groupRef} position={[-1.2, 0.8, 0]}>
      {/* Wireframe edges — subtle, thin */}
      <lineSegments geometry={edgesGeometry}>
        <lineBasicMaterial
          color="#9A9A9A"
          transparent
          opacity={0.18}
          linewidth={1}
        />
      </lineSegments>

      {/* Ceramic node spheres — alternating sage and amber */}
      {vertices.map((pos, i) => (
        <mesh key={i} position={pos} castShadow receiveShadow>
          <sphereGeometry args={[0.14, 24, 24]} />
          <meshStandardMaterial
            color={i % 3 === 0 ? "#D4A373" : "#8B9D77"}
            roughness={0.85}
            metalness={0.05}
            envMapIntensity={0.6}
          />
        </mesh>
      ))}
    </group>
  );
}

// ── Canvas-textured tablet screen ───────────────────────────────────────────
function createScreenTexture(): THREE.CanvasTexture {
  const w = 512;
  const h = 720;
  const canvas = document.createElement("canvas");
  canvas.width = w;
  canvas.height = h;
  const ctx = canvas.getContext("2d")!;

  // Off-white screen background
  ctx.fillStyle = "#FAFAF8";
  ctx.fillRect(0, 0, w, h);

  const margin = 36;

  // Title
  ctx.fillStyle = "#2C2C2C";
  ctx.font = "bold 28px Georgia, 'Times New Roman', serif";
  ctx.fillText("Apollo Research", margin, 60);

  // Subtitle
  ctx.fillStyle = "#6B6B6B";
  ctx.font = "16px 'Helvetica Neue', Helvetica, Arial, sans-serif";
  ctx.fillText("Platform", margin, 86);

  // Separator
  ctx.fillStyle = "#D0D0D0";
  ctx.fillRect(margin, 100, (w - margin * 2) * 0.35, 1.5);

  // Body lines — simulating a research summary
  ctx.font = "13px 'Helvetica Neue', Helvetica, Arial, sans-serif";
  ctx.fillStyle = "#4A4A4A";
  const lines = [
    "Connecting knowledge,",
    "accelerating discovery.",
    "",
    "Version 3.2",
    "",
    "Recent Publications:",
    "",
    '"Quantum Field Topology',
    "in Complex Systems\"",
    "— A. Sharma et al., 2025",
    "",
    '"Biomarker Screening for',
    "Early Renal Impairment\"",
    "— P. Kumar et al., 2025",
  ];

  let y = 130;
  for (const line of lines) {
    if (line === "") {
      y += 8;
      continue;
    }
    ctx.fillText(line, margin, y);
    y += 20;
  }

  // Faint page number at bottom
  ctx.fillStyle = "#BBBBBB";
  ctx.font = "11px 'Helvetica Neue', Helvetica, Arial, sans-serif";
  ctx.textAlign = "center";
  ctx.fillText("1", w / 2, h - 24);

  const texture = new THREE.CanvasTexture(canvas);
  texture.minFilter = THREE.LinearFilter;
  texture.magFilter = THREE.LinearFilter;
  texture.colorSpace = THREE.SRGBColorSpace;
  return texture;
}

function FacingTablet({ reducedMotion }: { reducedMotion: boolean }) {
  const screenTexture = useMemo(() => createScreenTexture(), []);

  useEffect(() => {
    return () => {
      screenTexture.dispose();
    };
  }, [screenTexture]);

  const Wrapper = reducedMotion ? StaticGroup : FloatWrap;

  return (
    <Wrapper speed={0.6} floatIntensity={0.08} rotationIntensity={0.01}>
      <group position={[2.2, -0.3, 1.2]} rotation={[0.02, -0.12, 0.01]}>
        {/* Device bezel — warm off-white ceramic feel */}
        <RoundedBox
          args={[2.8, 4.0, 0.12]}
          radius={0.08}
          smoothness={4}
          castShadow
          receiveShadow
        >
          <meshStandardMaterial
            color="#EAEAE6"
            roughness={0.5}
            metalness={0.02}
            envMapIntensity={0.5}
          />
        </RoundedBox>

        {/* Screen — Canvas texture for sharp, readable text */}
        <mesh position={[0, 0, 0.062]} receiveShadow>
          <planeGeometry args={[2.5, 3.7]} />
          <meshStandardMaterial
            map={screenTexture}
            color="#FFFFFF"
            roughness={0.88}
            metalness={0}
            emissive="#FFFFFF"
            emissiveIntensity={0.04}
          />
        </mesh>
      </group>
    </Wrapper>
  );
}

// ── Glass paperweight — high-fidelity transmission material ─────────────────
function GlassPaperweight({ reducedMotion }: { reducedMotion: boolean }) {
  const meshRef = useRef<THREE.Mesh>(null);

  useFrame(({ clock }) => {
    if (reducedMotion || !meshRef.current) return;
    const t = clock.getElapsedTime();
    // Very slow rotation to catch light caustics
    meshRef.current.rotation.y = t * 0.08;
    meshRef.current.rotation.z = Math.sin(t * 0.2) * 0.03;
  });

  return (
    <mesh
      ref={meshRef}
      position={[3.8, -2.0, 2.2]}
      castShadow
      rotation={[0.4, 0.2, 0.3]}
    >
      <dodecahedronGeometry args={[0.55, 0]} />
      <MeshTransmissionMaterial
        transmissionSampler
        transmission={1}
        thickness={1.2}
        roughness={0.02}
        chromaticAberration={0.03}
        anisotropicBlur={0.06}
        distortion={0.1}
        distortionScale={0.3}
        temporalDistortion={0.01}
        backside
        backsideThickness={0.6}
        color="#FFFFFF"
        toneMapped={false}
        opacity={0.7}
        transparent
      />
    </mesh>
  );
}

// ── Stylised pampas vase ────────────────────────────────────────────────────
function StylizedPampasVase({ reducedMotion }: { reducedMotion: boolean }) {
  const Wrapper = reducedMotion ? StaticGroup : FloatWrap;

  return (
    <Wrapper speed={0.4} floatIntensity={0.06} rotationIntensity={0.008}>
      <group position={[-4.0, -1.6, 0.8]}>
        {/* Frosted glass vase — transmission for translucency */}
        <mesh position={[0, 0, 0]} castShadow receiveShadow>
          <cylinderGeometry args={[0.35, 0.42, 1.4, 32]} />
          <MeshTransmissionMaterial
            transmissionSampler
            transmission={0.85}
            thickness={0.4}
            roughness={0.25}
            chromaticAberration={0.01}
            anisotropicBlur={0.04}
            backside
            backsideThickness={0.2}
            color="#F0EDE6"
            toneMapped={false}
            opacity={0.85}
            transparent
          />
        </mesh>

        {/* Dried grass stem */}
        <mesh position={[0, 1.4, 0]} rotation={[0, 0, -0.08]}>
          <cylinderGeometry args={[0.018, 0.035, 2.8, 8]} />
          <meshStandardMaterial
            color="#D4A373"
            roughness={0.95}
            metalness={0}
          />
        </mesh>

        {/* Pampas fluff — soft cone with transparency */}
        <mesh position={[0.08, 2.4, 0]} rotation={[0, 0, -0.08]}>
          <coneGeometry args={[0.28, 1.4, 12]} />
          <meshStandardMaterial
            color="#E8D8C4"
            roughness={0.95}
            metalness={0}
            transparent
            opacity={0.75}
            side={THREE.DoubleSide}
          />
        </mesh>

        {/* Second stem — slight splay for realism */}
        <mesh position={[-0.1, 1.3, 0.05]} rotation={[0.06, 0, 0.12]}>
          <cylinderGeometry args={[0.015, 0.03, 2.5, 8]} />
          <meshStandardMaterial
            color="#C8966A"
            roughness={0.95}
            metalness={0}
          />
        </mesh>

        {/* Second fluff */}
        <mesh position={[-0.16, 2.2, 0.06]} rotation={[0.06, 0, 0.12]}>
          <coneGeometry args={[0.22, 1.1, 10]} />
          <meshStandardMaterial
            color="#DDD0BE"
            roughness={0.95}
            metalness={0}
            transparent
            opacity={0.65}
            side={THREE.DoubleSide}
          />
        </mesh>
      </group>
    </Wrapper>
  );
}

// ── White ceramic platform base ─────────────────────────────────────────────
function CeramicPlatform() {
  return (
    <mesh position={[-0.5, -2.35, 0.5]} receiveShadow castShadow>
      <cylinderGeometry args={[4.2, 4.4, 0.18, 64]} />
      <meshStandardMaterial
        color="#FAFAF8"
        roughness={0.3}
        metalness={0.02}
        envMapIntensity={0.4}
      />
    </mesh>
  );
}

// ── Float / Static wrappers ─────────────────────────────────────────────────
function FloatWrap({
  children,
  speed,
  floatIntensity,
  rotationIntensity,
}: {
  children: React.ReactNode;
  speed: number;
  floatIntensity: number;
  rotationIntensity: number;
}) {
  return (
    <Float
      speed={speed}
      floatIntensity={floatIntensity}
      rotationIntensity={rotationIntensity}
    >
      {children}
    </Float>
  );
}

function StaticGroup({
  children,
}: {
  children: React.ReactNode;
  speed?: number;
  floatIntensity?: number;
  rotationIntensity?: number;
}) {
  return <group>{children}</group>;
}

// ── Main scene — all objects + lighting ──────────────────────────────────────
function AuthScene() {
  const reducedMotion = useReducedMotion();

  return (
    <>
      <StudioEnvironment />

      {/* === Lighting rig — key / fill / rim for sculptural depth === */}

      {/* Ambient — low to preserve contrast */}
      <ambientLight intensity={0.35} />

      {/* Key light — warm directional from upper-left */}
      <directionalLight
        position={[-6, 8, 5]}
        intensity={1.6}
        color="#FFF8F0"
        castShadow
        shadow-mapSize-width={2048}
        shadow-mapSize-height={2048}
        shadow-camera-near={0.5}
        shadow-camera-far={25}
        shadow-camera-left={-8}
        shadow-camera-right={8}
        shadow-camera-top={8}
        shadow-camera-bottom={-8}
        shadow-bias={-0.0003}
        shadow-normalBias={0.02}
      />

      {/* Fill light — cooler, softer from right */}
      <directionalLight
        position={[5, 3, 3]}
        intensity={0.6}
        color="#E8ECF0"
      />

      {/* Rim light — backlit edge definition */}
      <pointLight
        position={[-3, 4, -5]}
        intensity={0.8}
        color="#FFF5E6"
        distance={20}
        decay={2}
      />

      {/* Under-fill — subtle warmth from below to soften hard shadows */}
      <pointLight
        position={[0, -3, 4]}
        intensity={0.25}
        color="#F5EDE0"
        distance={15}
        decay={2}
      />

      {/* === Scene objects === */}

      <NetworkTopology reducedMotion={reducedMotion} />
      <FacingTablet reducedMotion={reducedMotion} />
      <GlassPaperweight reducedMotion={reducedMotion} />
      <StylizedPampasVase reducedMotion={reducedMotion} />
      <CeramicPlatform />

      {/* === Contact shadows — high resolution, soft, grounded === */}
      <ContactShadows
        position={[0, -2.45, 0.5]}
        opacity={0.35}
        scale={22}
        blur={2.5}
        smooth
        far={6}
        resolution={512}
        color="#2A2520"
      />
    </>
  );
}

// ── Exported component ──────────────────────────────────────────────────────
export default function Auth3DScene() {
  return (
    <Canvas
      shadows
      camera={{
        position: [0.5, 1.2, 9.5],
        fov: 40,
        near: 0.1,
        far: 50,
      }}
      dpr={[1, 1.5]}
      gl={{
        antialias: true,
        alpha: true,
        powerPreference: "default",
        toneMapping: THREE.ACESFilmicToneMapping,
        toneMappingExposure: 1.1,
      }}
      style={{ background: "transparent" }}
    >
      <AuthScene />
    </Canvas>
  );
}

