115→  ctx.fillStyle = "#2A2A2A";
116→  ctx.font = "bold 42px Georgia, 'Times New Roman', serif";
117→  ctx.fillText(content.title, margin, 120);
118→
119→  // Subtitle
120→  ctx.fillStyle = "#4A4A4A";
121→  ctx.font = "italic 28px Georgia, 'Times New Roman', serif";
122→  ctx.fillText(content.subtitle, margin, 165);
123→
124→  // Separator line
125→  ctx.fillStyle = "#C0C0C0";
126→  ctx.fillRect(margin, 185, contentW * 0.3, 1.5);
127→
128→  // Body paragraphs — sans-serif
129→  ctx.font = "22px 'Helvetica Neue', Helvetica, Arial, sans-serif";
130→  ctx.fillStyle = "#505050";
131→  const lineHeight = 32;
132→  let y = 230;
133→
134→  for (const paragraph of content.body) {
135→    const wrappedLines = wrapText(ctx, paragraph, contentW);
136→    for (const line of wrappedLines) {
137→      ctx.fillText(line, margin, y);
138→      y += lineHeight;
139→    }
140→    y += 18; // paragraph gap
141→  }
142→
143→  const texture = new THREE.CanvasTexture(canvas);
144→  texture.minFilter = THREE.LinearFilter;
145→  texture.magFilter = THREE.LinearFilter;
146→  texture.colorSpace = THREE.SRGBColorSpace;
147→  return texture;
148→}
149→
150→function roundRect(
151→  ctx: CanvasRenderingContext2D,
152→  x: number,
153→  y: number,
154→  w: number,
155→  h: number,
156→  r: number,
157→) {
158→  ctx.beginPath();
159→  ctx.roundRect(x, y, w, h, r);
160→  ctx.fill();
161→}
162→
163→// ── Thesis Page mesh ────────────────────────────────────────────────────────
164→function ThesisPageMesh({ variant }: { variant: 0 | 1 | 2 }) {
165→  const texture = useMemo(() => createPageTexture(variant), [variant]);
166→
167→  useEffect(() => {
168→    return () => { texture.dispose(); };
169→  }, [texture]);
170→
171→  return (
172→    <group>
173→      {/* Paper body — thicker for visible edges, warm tint for contrast */}
174→      <RoundedBox args={[2.1, 2.97, 0.04]} radius={0.006} smoothness={4} castShadow receiveShadow>
175→        <meshStandardMaterial
176→          color="#F0EDE6"
177→          roughness={0.82}
178→          metalness={0}
179→        />
180→      </RoundedBox>
181→      {/* Front face — bright white texture, sits on front surface */}
182→      <mesh position={[0, 0, 0.021]} castShadow receiveShadow>
183→        <planeGeometry args={[2.1, 2.97]} />
184→        <meshStandardMaterial
185→          map={texture}
186→          color="#FFFFFF"
187→          roughness={0.78}
188→          metalness={0}
189→          emissive="#FFFFFF"
190→          emissiveIntensity={0.06}
191→        />
192→      </mesh>
193→    </group>
194→  );
195→}
196→
197→// ── Thesis pages group — visible fan, 3 edges showing ───────────────────────
198→function ThesisPagesGroup({ reducedMotion }: { reducedMotion: boolean }) {
199→  const Wrapper = reducedMotion ? StaticGroup : FloatWrapper;
200→
201→  return (
202→    <group position={[-0.6, 0.5, 0]}>
203→      {/* Page A — back-left, peeks above and left */}
204→      <Wrapper speed={1.1} floatIntensity={0.15} rotationIntensity={0.03}>
205→        <group
206→          rotation={[0.05, -0.16, -0.08]}
207→          position={[-0.6, 0.4, -1.0]}
208→        >
209→          <ThesisPageMesh variant={0} />
210→        </group>
211→      </Wrapper>
212→
213→      {/* Page B — front-centre, dominant */}
214→      <Wrapper speed={0.9} floatIntensity={0.12} rotationIntensity={0.02}>
215→        <group
216→          rotation={[0.02, -0.03, 0.02]}
217→          position={[0, 0.05, 0]}
218→        >
219→          <ThesisPageMesh variant={1} />
220→        </group>
221→      </Wrapper>
222→
223→      {/* Page C — back-right, peeks right and slightly down */}
224→      <Wrapper speed={0.7} floatIntensity={0.12} rotationIntensity={0.03}>
225→        <group
226→          rotation={[-0.04, 0.18, 0.08]}
227→          position={[0.65, -0.3, -0.65]}
228→        >
229→          <ThesisPageMesh variant={2} />
230→        </group>
231→      </Wrapper>
232→    </group>
233→  );
234→}
235→
236→// ── Float / Static wrapper ──────────────────────────────────────────────────
237→function FloatWrapper({
238→  children,
239→  speed,
240→  floatIntensity,
241→  rotationIntensity,
242→}: {
243→  children: React.ReactNode;
244→  speed: number;
245→  floatIntensity: number;
246→  rotationIntensity: number;
247→}) {
248→  return (
249→    <Float speed={speed} floatIntensity={floatIntensity} rotationIntensity={rotationIntensity}>
250→      {children}
251→    </Float>
252→  );
253→}
254→
255→function StaticGroup({
256→  children,
257→}: {
258→  children: React.ReactNode;
259→  speed?: number;
260→  floatIntensity?: number;
261→  rotationIntensity?: number;
262→}) {
263→  return <group>{children}</group>;
264→}
265→
266→// ── Scatter data — 18 points with varying sizes ─────────────────────────────
267→const SCATTER_COLORS = ["#2D8C5A", "#D4644A", "#3A82B8", "#D4A030"];
268→
269→// Breathe config per colour group — frequency, phase offset, amplitude
270→const GROUP_BREATHE: Record<string, { freq: number; phase: number; amp: number }> = {
271→  [SCATTER_COLORS[0]]: { freq: 0.7, phase: 0, amp: 0.06 },
272→  [SCATTER_COLORS[1]]: { freq: 1.1, phase: 1.2, amp: 0.08 },
273→  [SCATTER_COLORS[2]]: { freq: 0.9, phase: 2.5, amp: 0.05 },
274→  [SCATTER_COLORS[3]]: { freq: 1.4, phase: 0.8, amp: 0.07 },
275→};
276→
277→const SCATTER_POINTS: {
278→  x: number;
279→  y: number;
280→  z: number;
281→  radius: number;
282→  color: string;
283→}[] = [
284→  // Large spheres — spread across Z depth
285→  { x: 0.15, y: 0.22, z: 0.85, radius: 0.12, color: SCATTER_COLORS[0] },
286→  { x: 1.65, y: 1.50, z: 0.20, radius: 0.11, color: SCATTER_COLORS[1] },
287→  { x: 0.90, y: 0.85, z: 1.10, radius: 0.10, color: SCATTER_COLORS[2] },
288→  // Medium-large
289→  { x: 0.50, y: 0.52, z: 0.10, radius: 0.09, color: SCATTER_COLORS[3] },
290→  { x: 1.30, y: 1.20, z: 0.75, radius: 0.09, color: SCATTER_COLORS[0] },
291→  { x: 1.10, y: 0.78, z: 0.40, radius: 0.085, color: SCATTER_COLORS[1] },
292→  // Medium
293→  { x: 0.35, y: 0.55, z: 1.20, radius: 0.075, color: SCATTER_COLORS[2] },
294→  { x: 0.75, y: 0.60, z: 0.05, radius: 0.07, color: SCATTER_COLORS[3] },
295→  { x: 1.45, y: 1.35, z: 0.55, radius: 0.07, color: SCATTER_COLORS[0] },
296→  { x: 1.80, y: 1.58, z: 0.90, radius: 0.075, color: SCATTER_COLORS[1] },
297→  // Small
298→  { x: 0.60, y: 0.75, z: 1.30, radius: 0.055, color: SCATTER_COLORS[2] },
299→  { x: 1.20, y: 0.92, z: 0.15, radius: 0.05, color: SCATTER_COLORS[3] },
300→  { x: 0.25, y: 0.10, z: 0.60, radius: 0.05, color: SCATTER_COLORS[0] },
301→  { x: 1.55, y: 1.10, z: 1.05, radius: 0.055, color: SCATTER_COLORS[1] },
302→  // Tiny
303→  { x: 0.45, y: 0.30, z: 0.95, radius: 0.04, color: SCATTER_COLORS[2] },
304→  { x: 0.85, y: 1.05, z: 1.25, radius: 0.04, color: SCATTER_COLORS[3] },
305→  { x: 1.70, y: 1.42, z: 0.30, radius: 0.035, color: SCATTER_COLORS[0] },
306→  { x: 1.00, y: 0.65, z: 0.70, radius: 0.038, color: SCATTER_COLORS[1] },
307→];
308→
309→// ── Scatter sphere — per-group breathe animation ────────────────────────────
310→function ScatterSphere({
311→  position,
312→  color,
313→  radius,
314→  reducedMotion,
315→}: {
316→  position: [number, number, number];
317→  color: string;
318→  radius: number;
319→  reducedMotion: boolean;
320→}) {
321→  const meshRef = useRef<THREE.Mesh>(null);
322→  const geometry = useMemo(() => new THREE.SphereGeometry(radius, 32, 32), [radius]);
323→  const breathe = GROUP_BREATHE[color] ?? { freq: 0.8, phase: 0, amp: 0.05 };
324→
325→  useFrame(({ clock }) => {
326→    if (reducedMotion || !meshRef.current) return;
327→    const t = clock.getElapsedTime();
328→    const s = 1 + Math.sin(t * breathe.freq + breathe.phase) * breathe.amp;
329→    meshRef.current.scale.setScalar(s);
330→  });
331→
332→  return (
333→    <mesh ref={meshRef} geometry={geometry} position={position} castShadow>
334→      <MeshTransmissionMaterial
335→        transmissionSampler
336→        transmission={1}
337→        thickness={radius * 5}
338→        roughness={0.01}
339→        chromaticAberration={0.04}
340→        anisotropicBlur={0.08}
341→        distortion={0.15}
342→        distortionScale={0.4}
343→        temporalDistortion={0.02}
344→        backside
345→        backsideThickness={radius * 2}
346→        color={color}
347→        toneMapped={false}
348→        opacity={0.65}
349→        transparent
350→      />
351→    </mesh>
352→  );
353→}
354→
355→// ── Axes with tick marks and arrowheads ──────────────────────────────────────
356→const AXIS_COLOR = "#C0C0C0";
357→const AXIS_LENGTH = 2.2;
358→const TICK_SIZE = 0.025;
359→const TICK_INTERVAL = 0.5;
360→const ARROW_HEIGHT = 0.1;
361→const ARROW_RADIUS = 0.025;
362→
363→function AxisArrow({
364→  position,
365→  rotation,
366→}: {
367→  position: [number, number, number];
368→  rotation: [number, number, number];
369→}) {
370→  return (
371→    <mesh position={position} rotation={rotation}>
372→      <coneGeometry args={[ARROW_RADIUS, ARROW_HEIGHT, 8]} />
373→      <meshBasicMaterial color={AXIS_COLOR} />
374→    </mesh>
375→  );
376→}
377→
378→function ScatterAxes({ reducedMotion }: { reducedMotion: boolean }) {
379→  const groupRef = useRef<THREE.Group>(null);
380→
381→  const ticks = useMemo(() => {
382→    const result: { start: [number, number, number]; end: [number, number, number] }[] = [];
383→
384→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
385→      result.push({ start: [t, -TICK_SIZE, 0], end: [t, TICK_SIZE, 0] });
386→    }
387→    for (let t = TICK_INTERVAL; t <= AXIS_LENGTH; t += TICK_INTERVAL) {
388→      result.push({ start: [-TICK_SIZE, t, 0], end: [TICK_SIZE, t, 0] });
389→    }
390→    for (let t = TICK_INTERVAL; t <= 0.6; t += TICK_INTERVAL) {
391→      result.push({ start: [0, -TICK_SIZE, t], end: [0, TICK_SIZE, t] });
392→    }
393→
394→    return result;
395→  }, []);
396→
397→  useFrame(({ clock }) => {
398→    if (reducedMotion || !groupRef.current) return;
399→    const t = clock.getElapsedTime();
400→    const s = 1 + Math.sin(t * 0.35) * 0.008;
401→    groupRef.current.scale.setScalar(s);
402→  });
403→
404→  return (
405→    <group ref={groupRef}>
406→      {/* Axis lines */}
407→      <Line points={[[0, 0, 0], [AXIS_LENGTH, 0, 0]]} color={AXIS_COLOR} lineWidth={1} />
408→      <Line points={[[0, 0, 0], [0, AXIS_LENGTH, 0]]} color={AXIS_COLOR} lineWidth={1} />
409→      <Line points={[[0, 0, 0], [0, 0, 0.6]]} color={AXIS_COLOR} lineWidth={1} />
410→
411→      {/* Arrowheads — cones at axis tips */}
412→      <AxisArrow position={[AXIS_LENGTH, 0, 0]} rotation={[0, 0, -Math.PI / 2]} />
413→      <AxisArrow position={[0, AXIS_LENGTH, 0]} rotation={[0, 0, 0]} />
414→      <AxisArrow position={[0, 0, 0.6]} rotation={[Math.PI / 2, 0, 0]} />
415→
416→      {/* Tick marks */}
417→      {ticks.map((tick, i) => (
418→        <Line key={i} points={[tick.start, tick.end]} color={AXIS_COLOR} lineWidth={1} />
419→      ))}
420→    </group>
421→  );
422→}
423→
424→// ── Scatter plot — overlaps right edge of pages ─────────────────────────────
425→function ScatterPlotGroup({ reducedMotion }: { reducedMotion: boolean }) {
426→  const groupRef = useRef<THREE.Group>(null);
427→
428→  useFrame(({ clock }) => {
429→    if (reducedMotion || !groupRef.current) return;
430→    const t = clock.getElapsedTime();
431→    groupRef.current.position.y = -0.3 + Math.sin(t * 0.6) * 0.1;
432→  });
433→
434→  return (
435→    <group
436→      ref={groupRef}
437→      position={[0.0, -0.3, 1.5]}
438→      rotation={[0.1, -45 * (Math.PI / 180), 0.15]}
439→    >
440→      <ScatterAxes reducedMotion={reducedMotion} />
441→      {SCATTER_POINTS.map((pt, i) => (
442→        <ScatterSphere
443→          key={i}
444→          position={[pt.x, pt.y, pt.z]}
445→          color={pt.color}
446→          radius={pt.radius}
447→          reducedMotion={reducedMotion}
448→        />
449→      ))}
450→    </group>
451→  );
452→}
453→
454→// ── Procedural environment (no HDR download) ────────────────────────────────
455→function StudioEnvironment() {
456→  return (
457→    <Environment frames={1} resolution={128} background={false}>
458→      <mesh position={[0, 6, 0]} scale={30}>
459→        <planeGeometry />
460→        <meshBasicMaterial color="#FFFEF8" />
461→      </mesh>
462→      <mesh position={[0, -6, 0]} scale={30} rotation={[Math.PI, 0, 0]}>
463→        <planeGeometry />
464→        <meshBasicMaterial color="#E8E0D0" />
465→      </mesh>
466→      <mesh position={[-8, 0, 0]} scale={20} rotation={[0, Math.PI / 2, 0]}>
467→        <planeGeometry />
468→        <meshBasicMaterial color="#FFF5E8" />
469→      </mesh>
470→      <mesh position={[8, 0, 0]} scale={20} rotation={[0, -Math.PI / 2, 0]}>
471→        <planeGeometry />
472→        <meshBasicMaterial color="#E8ECF0" />
473→      </mesh>
474→      <mesh position={[0, 2, 8]} scale={15} rotation={[0, Math.PI, 0]}>
475→        <planeGeometry />
476→        <meshBasicMaterial color="#FFFFFF" />
477→      </mesh>
478→    </Environment>
479→  );
480→}
481→
482→// ── Main scene ──────────────────────────────────────────────────────────────
483→function Scene() {
484→  const reducedMotion = useReducedMotion();
485→
486→  return (
487→    <>
488→      <StudioEnvironment />
489→
490→      {/* Lighting — key/fill/rim for depth */}
491→      <ambientLight intensity={0.5} />
492→      <directionalLight
493→        position={[4, 8, 3]}
494→        intensity={1.8}
495→        castShadow
496→        shadow-mapSize-width={1024}
497→        shadow-mapSize-height={1024}
498→        shadow-camera-near={0.5}
499→        shadow-camera-far={20}
500→        shadow-camera-left={-6}
501→        shadow-camera-right={6}
502→        shadow-camera-top={6}
503→        shadow-camera-bottom={-6}
504→        shadow-bias={-0.0005}
505→      />
506→      <pointLight position={[-4, 4, 3]} intensity={0.6} color="#FFF5E6" />
507→      <pointLight position={[2, -2, 5]} intensity={0.3} color="#F5F0E8" />
508→      <directionalLight position={[-3, 2, -4]} intensity={0.8} color="#E8ECF0" />
509→
510→      {/* Soft contact shadows beneath objects */}
511→      <ContactShadows
512→        position={[0, -1.5, 0]}
513→        opacity={0.3}
514→        scale={20}
515→        blur={2.5}
516→        smooth
517→        far={5}
518→        resolution={512}
519→        color="#2A2520"
520→      />
521→
522→      <ThesisPagesGroup reducedMotion={reducedMotion} />
523→      <ScatterPlotGroup reducedMotion={reducedMotion} />
524→    </>
525→  );
526→}
527→
528→// ── Exported component ──────────────────────────────────────────────────────
529→export function Hero3DScene() {
530→  return (
531→    <Canvas
532→      shadows
533→      camera={{ position: [0, 0.5, 7], fov: 38, near: 0.1, far: 50 }}
534→      dpr={[1, 1.5]}
535→      gl={{ antialias: true, alpha: true, powerPreference: "default" }}
536→      style={{ background: "transparent" }}
537→    >
538→      <Scene />
539→    </Canvas>
540→  );
541→}
