 90→function createPageTexture(variant: 0 | 1 | 2): THREE.CanvasTexture {
 91→  const w = 1024;
 92→  const h = 1440;
 93→  const canvas = document.createElement("canvas");
 94→  canvas.width = w;
 95→  canvas.height = h;
 96→  const ctx = canvas.getContext("2d")!;
 97→
 98→  // Clean white paper background
 99→  ctx.fillStyle = "#FEFEFE";
100→  ctx.fillRect(0, 0, w, h);
101→
102→  // Subtle grain noise
103→  for (let i = 0; i < 800; i++) {
104→    const gx = Math.floor(((i * 7919 + 104729) % 100003) / 100003 * w);
105→    const gy = Math.floor(((i * 6271 + 73856) % 100003) / 100003 * h);
106→    ctx.fillStyle = `rgba(0,0,0,${0.008 + (i % 3) * 0.004})`;
107→    ctx.fillRect(gx, gy, 2, 2);
108→  }
109→
110→  const margin = 88;
111→  const contentW = w - margin * 2;
112→  const content = PAGE_CONTENT[variant];
113→
114→  // Chapter title — large serif
115→  ctx.fillStyle = "#2A2A2A";
116→  ctx.font = "bold 42px Georgia, 'Times New Roman', serif";
117→  ctx.fillText(content.title, margin, 120);
118→
119→  // Subtitle
120→  ctx.fillStyle = "#4A4A4A";
121→  ctx.font = "italic 28px Georgia, 'Times New Roman', serif";
122→  ctx.fillText(content.subtitle, margin, 165);
123→
124→  // Separator line
125→  ctx.fillStyle = "#C0C0C0";
126→  ctx.fillRect(margin, 185, contentW * 0.3, 1.5);
127→
128→  // Body paragraphs — sans-serif
129→  ctx.font = "22px 'Helvetica Neue', Helvetica, Arial, sans-serif";
130→  ctx.fillStyle = "#505050";
131→  const lineHeight = 32;
132→  let y = 230;
133→
134→  for (const paragraph of content.body) {
135→    const wrappedLines = wrapText(ctx, paragraph, contentW);
136→    for (const line of wrappedLines) {
137→      ctx.fillText(line, margin, y);
138→      y += lineHeight;
139→    }
140→    y += 18; // paragraph gap
141→  }
142→
143→  const texture = new THREE.CanvasTexture(canvas);
144→  texture.minFilter = THREE.LinearFilter;
145→  texture.magFilter = THREE.LinearFilter;
146→  texture.colorSpace = THREE.SRGBColorSpace;
147→  return texture;
148→}
