 14→// ── Reduced motion detection ────────────────────────────────────────────────
 15→function useReducedMotion() {
 16→  const [reduced, setReduced] = useState(false);
 17→
 18→  useEffect(() => {
 19→    const mq = window.matchMedia("(prefers-reduced-motion: reduce)");
 20→    setReduced(mq.matches);
 21→    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
 22→    mq.addEventListener("change", handler);
 23→    return () => mq.removeEventListener("change", handler);
 24→  }, []);
 25→
 26→  return reduced;
 27→}
