 62→// ── Network topology — icosahedron wireframe with ceramic nodes ─────────────
 63→function NetworkTopology({ reducedMotion }: { reducedMotion: boolean }) {
 64→  const { vertices, edgesGeometry } = useMemo(() => {
 65→    const geom = new THREE.IcosahedronGeometry(2.0, 1);
 66→    const posAttribute = geom.attributes.position;
 67→    const verts: THREE.Vector3[] = [];
 68→
 69→    for (let i = 0; i < posAttribute.count; i++) {
 70→      verts.push(new THREE.Vector3().fromBufferAttribute(posAttribute, i));
 71→    }
 72→
 73→    // Deduplicate vertices (icosahedron shares vertex positions)
 74→    const uniqueVerts = verts.filter(
 75→      (v, idx, arr) => arr.findIndex((v2) => v2.distanceTo(v) < 0.1) === idx
 76→    );
 77→
 78→    return {
 79→      vertices: uniqueVerts,
 80→      edgesGeometry: new THREE.EdgesGeometry(geom),
 81→    };
 82→  }, []);
 83→
 84→  const groupRef = useRef<THREE.Group>(null);
 85→
 86→  useFrame(({ clock }) => {
 87→    if (reducedMotion || !groupRef.current) return;
 88→    const t = clock.getElapsedTime();
 89→    // Slow orbital drift
 90→    groupRef.current.rotation.y = Math.sin(t * 0.15) * 0.12;
 91→    groupRef.current.rotation.x = Math.cos(t * 0.1) * 0.04;
 92→    // Gentle vertical float
 93→    groupRef.current.position.y = 0.8 + Math.sin(t * 0.4) * 0.08;
 94→  });
 95→
 96→  return (
 97→    <group ref={groupRef} position={[-1.2, 0.8, 0]}>
 98→      {/* Wireframe edges — subtle, thin */}
 99→      <lineSegments geometry={edgesGeometry}>
100→        <lineBasicMaterial
101→          color="#9A9A9A"
102→          transparent
103→          opacity={0.18}
104→          linewidth={1}
105→        />
106→      </lineSegments>
107→
108→      {/* Ceramic node spheres — alternating sage and amber */}
109→      {vertices.map((pos, i) => (
110→        <mesh key={i} position={pos} castShadow receiveShadow>
111→          <sphereGeometry args={[0.14, 24, 24]} />
112→          <meshStandardMaterial
113→            color={i % 3 === 0 ? "#D4A373" : "#8B9D77"}
114→            roughness={0.85}
115→            metalness={0.05}
116→            envMapIntensity={0.6}
117→          />
118→        </mesh>
119→      ))}
120→    </group>
121→  );
122→}
