124→// ── Canvas-textured tablet screen ───────────────────────────────────────────
125→function createScreenTexture(): THREE.CanvasTexture {
126→  const w = 512;
127→  const h = 720;
128→  const canvas = document.createElement("canvas");
129→  canvas.width = w;
130→  canvas.height = h;
131→  const ctx = canvas.getContext("2d")!;
132→
133→  // Off-white screen background
134→  ctx.fillStyle = "#FAFAF8";
135→  ctx.fillRect(0, 0, w, h);
136→
137→  const margin = 36;
138→
139→  // Title
140→  ctx.fillStyle = "#2C2C2C";
141→  ctx.font = "bold 28px Georgia, 'Times New Roman', serif";
142→  ctx.fillText("Apollo Research", margin, 60);
143→
144→  // Subtitle
145→  ctx.fillStyle = "#6B6B6B";
146→  ctx.font = "16px 'Helvetica Neue', Helvetica, Arial, sans-serif";
147→  ctx.fillText("Platform", margin, 86);
148→
149→  // Separator
150→  ctx.fillStyle = "#D0D0D0";
151→  ctx.fillRect(margin, 100, (w - margin * 2) * 0.35, 1.5);
152→
153→  // Body lines — simulating a research summary
154→  ctx.font = "13px 'Helvetica Neue', Helvetica, Arial, sans-serif";
155→  ctx.fillStyle = "#4A4A4A";
156→  const lines = [
157→    "Connecting knowledge,",
158→    "accelerating discovery.",
159→    "",
160→    "Version 3.2",
161→    "",
162→    "Recent Publications:",
163→    "",
164→    '"Quantum Field Topology',
165→    "in Complex Systems\"",
166→    "— A. Sharma et al., 2025",
167→    "",
168→    '"Biomarker Screening for',
169→    "Early Renal Impairment\"",
170→    "— P. Kumar et al., 2025",
171→  ];
172→
173→  let y = 130;
174→  for (const line of lines) {
175→    if (line === "") {
176→      y += 8;
177→      continue;
178→    }
179→    ctx.fillText(line, margin, y);
180→    y += 20;
181→  }
182→
183→  // Faint page number at bottom
184→  ctx.fillStyle = "#BBBBBB";
185→  ctx.font = "11px 'Helvetica Neue', Helvetica, Arial, sans-serif";
186→  ctx.textAlign = "center";
187→  ctx.fillText("1", w / 2, h - 24);
188→
189→  const texture = new THREE.CanvasTexture(canvas);
190→  texture.minFilter = THREE.LinearFilter;
191→  texture.magFilter = THREE.LinearFilter;
192→  texture.colorSpace = THREE.SRGBColorSpace;
193→  return texture;
194→}
