275→        color="#E8EDE4"
276→        toneMapped={false}
277→        opacity={0.85}
278→        transparent
279→      />
280→    </mesh>
281→  );
282→}
283→
284→// ── Stylised pampas vase ────────────────────────────────────────────────────
285→function StylizedPampasVase({ reducedMotion }: { reducedMotion: boolean }) {
286→  const Wrapper = reducedMotion ? StaticGroup : FloatWrap;
287→
288→  return (
289→    <Wrapper speed={0.4} floatIntensity={0.06} rotationIntensity={0.008}>
290→      <group position={[-3.0, -1.6, 0.8]}>
291→        {/* Frosted glass vase — transmission for translucency */}
292→        <mesh position={[0, 0, 0]} castShadow receiveShadow>
293→          <cylinderGeometry args={[0.35, 0.42, 1.4, 32]} />
294→          <MeshTransmissionMaterial
295→            transmissionSampler
296→            transmission={0.85}
297→            thickness={0.4}
298→            roughness={0.25}
299→            chromaticAberration={0.01}
300→            anisotropicBlur={0.04}
301→            backside
302→            backsideThickness={0.2}
303→            color="#F0EDE6"
304→            toneMapped={false}
305→            opacity={0.85}
306→            transparent
307→          />
308→        </mesh>
309→
310→        {/* Dried grass stem */}
311→        <mesh position={[0, 1.4, 0]} rotation={[0, 0, -0.08]}>
312→          <cylinderGeometry args={[0.018, 0.035, 2.8, 8]} />
313→          <meshStandardMaterial
314→            color="#D4A373"
315→            roughness={0.95}
316→            metalness={0}
317→          />
318→        </mesh>
319→
320→        {/* Pampas fluff — soft cone with transparency */}
321→        <mesh position={[0.08, 2.4, 0]} rotation={[0, 0, -0.08]}>
322→          <coneGeometry args={[0.28, 1.4, 12]} />
323→          <meshStandardMaterial
324→            color="#E8D8C4"
325→            roughness={0.95}
326→            metalness={0}
327→            transparent
328→            opacity={0.75}
329→            side={THREE.DoubleSide}
330→          />
331→        </mesh>
332→
333→        {/* Second stem — slight splay for realism */}
334→        <mesh position={[-0.1, 1.3, 0.05]} rotation={[0.06, 0, 0.12]}>
335→          <cylinderGeometry args={[0.015, 0.03, 2.5, 8]} />
336→          <meshStandardMaterial
337→            color="#C8966A"
338→            roughness={0.95}
339→            metalness={0}
340→          />
341→        </mesh>
342→
343→        {/* Second fluff */}
344→        <mesh position={[-0.16, 2.2, 0.06]} rotation={[0.06, 0, 0.12]}>
345→          <coneGeometry args={[0.22, 1.1, 10]} />
346→          <meshStandardMaterial
347→            color="#DDD0BE"
348→            roughness={0.95}
349→            metalness={0}
350→            transparent
351→            opacity={0.65}
352→            side={THREE.DoubleSide}
353→          />
354→        </mesh>
355→      </group>
356→    </Wrapper>
357→  );
358→}
359→
360→// ── White ceramic platform base ─────────────────────────────────────────────
361→function CeramicPlatform() {
362→  return (
363→    <mesh position={[-0.5, -2.35, 0.5]} receiveShadow castShadow>
364→      <cylinderGeometry args={[4.2, 4.4, 0.18, 64]} />
365→      <meshStandardMaterial
366→        color="#FAFAF8"
367→        roughness={0.3}
368→        metalness={0.02}
369→        envMapIntensity={0.4}
370→      />
371→    </mesh>
372→  );
373→}
374→
375→// ── Float / Static wrappers ─────────────────────────────────────────────────
376→function FloatWrap({
377→  children,
378→  speed,
379→  floatIntensity,
380→  rotationIntensity,
381→}: {
382→  children: React.ReactNode;
383→  speed: number;
384→  floatIntensity: number;
385→  rotationIntensity: number;
386→}) {
387→  return (
388→    <Float
389→      speed={speed}
390→      floatIntensity={floatIntensity}
391→      rotationIntensity={rotationIntensity}
392→    >
393→      {children}
394→    </Float>
395→  );
396→}
397→
398→function StaticGroup({
399→  children,
400→}: {
401→  children: React.ReactNode;
402→  speed?: number;
403→  floatIntensity?: number;
404→  rotationIntensity?: number;
405→}) {
406→  return <group>{children}</group>;
407→}
408→
409→// ── Main scene — all objects + lighting ──────────────────────────────────────
410→function AuthScene() {
411→  const reducedMotion = useReducedMotion();
412→
413→  return (
414→    <>
415→      <StudioEnvironment />
416→
417→      {/* === Lighting rig — key / fill / rim for sculptural depth === */}
418→
419→      {/* Ambient — low to preserve contrast */}
420→      <ambientLight intensity={0.35} />
421→
422→      {/* Key light — warm directional from upper-left */}
423→      <directionalLight
424→        position={[-6, 8, 5]}
425→        intensity={1.6}
426→        color="#FFF8F0"
427→        castShadow
428→        shadow-mapSize-width={2048}
429→        shadow-mapSize-height={2048}
430→        shadow-camera-near={0.5}
431→        shadow-camera-far={25}
432→        shadow-camera-left={-8}
433→        shadow-camera-right={8}
434→        shadow-camera-top={8}
435→        shadow-camera-bottom={-8}
436→        shadow-bias={-0.0003}
437→        shadow-normalBias={0.02}
438→      />
439→
440→      {/* Fill light — cooler, softer from right */}
441→      <directionalLight
442→        position={[5, 3, 3]}
443→        intensity={0.6}
444→        color="#E8ECF0"
445→      />
446→
447→      {/* Rim light — backlit edge definition */}
448→      <pointLight
449→        position={[-3, 4, -5]}
450→        intensity={0.8}
451→        color="#FFF5E6"
452→        distance={20}
453→        decay={2}
454→      />
455→
456→      {/* Under-fill — subtle warmth from below to soften hard shadows */}
457→      <pointLight
458→        position={[0, -3, 4]}
459→        intensity={0.25}
460→        color="#F5EDE0"
461→        distance={15}
462→        decay={2}
463→      />
464→
465→      {/* === Scene objects === */}
466→
467→      <NetworkTopology reducedMotion={reducedMotion} />
468→      <FacingTablet reducedMotion={reducedMotion} />
469→      <GlassPaperweight reducedMotion={reducedMotion} />
470→      <StylizedPampasVase reducedMotion={reducedMotion} />
471→      <CeramicPlatform />
472→
473→      {/* === Contact shadows — high resolution, soft, grounded === */}
474→      <ContactShadows
475→        position={[0, -2.45, 0.5]}
476→        opacity={0.35}
477→        scale={22}
478→        blur={2.5}
479→        smooth
480→        far={6}
481→        resolution={512}
482→        color="#2A2520"
483→      />
484→    </>
485→  );
486→}
487→
488→// ── Exported component ──────────────────────────────────────────────────────
489→export default function Auth3DScene() {
490→  return (
491→    <Canvas
492→      shadows
493→      camera={{
494→        position: [0.5, 1.2, 9.5],
495→        fov: 40,
496→        near: 0.1,
497→        far: 50,
498→      }}
499→      dpr={[1, 1.5]}
500→      gl={{
501→        antialias: true,
502→        alpha: true,
503→        powerPreference: "default",
504→        toneMapping: THREE.ACESFilmicToneMapping,
505→        toneMappingExposure: 1.1,
506→      }}
507→      style={{ background: "transparent" }}
508→    >
509→      <AuthScene />
510→    </Canvas>
511→  );
512→}
