 1→import { NextRequest, NextResponse } from "next/server";
 2→import { getAuthenticatedUser } from "@/lib/api/auth";
 3→import {
 4→  unauthorised,
 5→  validationError,
 6→  badRequest,
 7→  internalError,
 8→} from "@/lib/api/errors";
 9→import {
10→  generateUploadUrl,
11→  ALLOWED_UPLOAD_TYPES,
12→} from "@/lib/r2/client";
13→
14→export async function POST(request: NextRequest) {
15→  try {
16→    const authResult = await getAuthenticatedUser();
17→    if (!authResult) return unauthorised();
18→
19→    const body = await request.json();
20→    const { fileName, contentType, projectId } = body as {
21→      fileName?: string;
22→      contentType?: string;
23→      projectId?: string;
24→    };
25→
26→    if (!fileName || !contentType || !projectId) {
27→      return validationError(
28→        "fileName, contentType, and projectId are required"
29→      );
30→    }
31→
32→    // Validate content type
33→    if (!ALLOWED_UPLOAD_TYPES.includes(contentType)) {
34→      return badRequest(
35→        `File type not allowed: ${contentType}. Allowed types: ${ALLOWED_UPLOAD_TYPES.join(", ")}`
36→      );
37→    }
38→
39→    // Validate file name - reject path traversal characters
40→    if (
41→      fileName.includes("..") ||
42→      fileName.includes("//") ||
43→      fileName.includes("\\")
44→    ) {
45→      return badRequest("Invalid file name: path traversal characters are not permitted");
46→    }
47→
48→    // Sanitise the file name (remove any remaining unsafe characters)
49→    const sanitisedFileName = fileName
50→      .replace(/[^\w.\-]/g, "_")
51→      .replace(/_{2,}/g, "_");
52→
53→    const key = `projects/${projectId}/${crypto.randomUUID()}/${sanitisedFileName}`;
54→
55→    const result = await generateUploadUrl(key, contentType);
56→
57→    return NextResponse.json({ data: result });
58→  } catch (err) {
59→    console.error("Unexpected error in POST /api/upload/signed-url:", err);
60→    return internalError();
61→  }
62→}
