import { NextResponse } from "next/server";
import { ERROR_CODES, type ApiErrorResponse } from "@/lib/types/api";

function errorResponse(
  code: string,
  message: string,
  status: number,
  details?: Record<string, unknown>
): NextResponse<ApiErrorResponse> {
  return NextResponse.json({ error: { code, message, details } }, { status });
}

export function unauthorised(message = "Authentication required") {
  return errorResponse(ERROR_CODES.UNAUTHORISED, message, 401);
}

export function forbidden(message = "Insufficient permissions") {
  return errorResponse(ERROR_CODES.FORBIDDEN, message, 403);
}

export function notFound(message = "Resource not found") {
  return errorResponse(ERROR_CODES.NOT_FOUND, message, 404);
}

export function licenceRequired(message = "Active licence required") {
  return errorResponse(ERROR_CODES.LICENCE_REQUIRED, message, 402);
}

export function validationError(
  message: string,
  details?: Record<string, unknown>
) {
  return errorResponse(ERROR_CODES.VALIDATION_ERROR, message, 400, details);
}

export function conflict(message: string) {
  return errorResponse(ERROR_CODES.CONFLICT, message, 409);
}

export function rateLimited(retryAfterSeconds: number) {
  return errorResponse(ERROR_CODES.RATE_LIMITED, "Rate limit exceeded", 429, {
    retry_after_seconds: retryAfterSeconds,
  });
}

export function badRequest(message: string) {
  return errorResponse(ERROR_CODES.BAD_REQUEST, message, 400);
}

export function queueFull(estimatedWaitSeconds?: number) {
  return errorResponse(ERROR_CODES.RATE_LIMITED, "Compute queue is full", 429, {
    ...(estimatedWaitSeconds !== undefined ? { estimated_wait_seconds: estimatedWaitSeconds } : {}),
  });
}

export function internalError(message = "Internal server error") {
  return errorResponse(ERROR_CODES.INTERNAL_ERROR, message, 500);
}

-- 016: Fix infinite recursion in users RLS policy (error 42P17)
--
-- The "Admins can view org users" policy on public.users does
-- SELECT FROM public.users inside itself, causing infinite recursion
-- when Postgres evaluates RLS. Fix: SECURITY DEFINER helper functions
-- that bypass RLS for auth lookups.
--
-- Rollback:
--   DROP FUNCTION IF EXISTS public.get_current_user_id();
--   DROP FUNCTION IF EXISTS public.get_current_user_org_id();
--   DROP FUNCTION IF EXISTS public.is_current_user_admin();
--   Then re-run the original 002 "Admins can view org users" policy.

-- Helper: get the current user's UUID (bypasses RLS)
CREATE OR REPLACE FUNCTION public.get_current_user_id()
RETURNS UUID AS $$
  SELECT id FROM public.users
  WHERE clerk_user_id = (SELECT auth.jwt() ->> 'sub')
  LIMIT 1;
$$ LANGUAGE sql SECURITY DEFINER STABLE;

-- Helper: get the current user's organisation_id (bypasses RLS)
CREATE OR REPLACE FUNCTION public.get_current_user_org_id()
RETURNS UUID AS $$
  SELECT organisation_id FROM public.users
  WHERE clerk_user_id = (SELECT auth.jwt() ->> 'sub')
  LIMIT 1;
$$ LANGUAGE sql SECURITY DEFINER STABLE;

-- Helper: check if the current user is an admin (bypasses RLS)
CREATE OR REPLACE FUNCTION public.is_current_user_admin()
RETURNS BOOLEAN AS $$
  SELECT EXISTS (
    SELECT 1 FROM public.users
    WHERE clerk_user_id = (SELECT auth.jwt() ->> 'sub')
      AND role = 'admin'
  );
$$ LANGUAGE sql SECURITY DEFINER STABLE;

-- ── Fix users table ──

DROP POLICY IF EXISTS "Admins can view org users" ON public.users;

CREATE POLICY "Admins can view org users"
  ON public.users FOR SELECT TO authenticated
  USING (
    public.is_current_user_admin()
    AND organisation_id = public.get_current_user_org_id()
  );

-- ── Fix projects table (also queries users, triggering the recursion) ──

DROP POLICY IF EXISTS "Users can view own projects" ON public.projects;
CREATE POLICY "Users can view own projects"
  ON public.projects FOR SELECT TO authenticated
  USING (user_id = public.get_current_user_id());

DROP POLICY IF EXISTS "Users can create own projects" ON public.projects;
CREATE POLICY "Users can create own projects"
  ON public.projects FOR INSERT TO authenticated
  WITH CHECK (user_id = public.get_current_user_id());

DROP POLICY IF EXISTS "Users can update own projects" ON public.projects;
CREATE POLICY "Users can update own projects"
  ON public.projects FOR UPDATE TO authenticated
  USING (user_id = public.get_current_user_id())
  WITH CHECK (user_id = public.get_current_user_id());

DROP POLICY IF EXISTS "Users can delete own projects" ON public.projects;
CREATE POLICY "Users can delete own projects"
  ON public.projects FOR DELETE TO authenticated
  USING (user_id = public.get_current_user_id());

DROP POLICY IF EXISTS "Admins can view org projects" ON public.projects;
CREATE POLICY "Admins can view org projects"
  ON public.projects FOR SELECT TO authenticated
  USING (
    public.is_current_user_admin()
    AND organisation_id = public.get_current_user_org_id()
  );

DROP POLICY IF EXISTS "Supervisors can view shared projects" ON public.projects;
CREATE POLICY "Supervisors can view shared projects"
  ON public.projects FOR SELECT TO authenticated
  USING (
    metadata_json ->> 'supervisor_user_id' = public.get_current_user_id()::text
  );

-- 002: Create users table
-- Rollback: DROP TABLE IF EXISTS public.users CASCADE;

CREATE TABLE public.users (
  id UUID PRIMARY KEY DEFAULT gen_random_uuid(),
  clerk_user_id TEXT NOT NULL UNIQUE,
  email TEXT NOT NULL,
  name TEXT NOT NULL DEFAULT '',
  role TEXT NOT NULL DEFAULT 'student' CHECK (role IN ('student', 'supervisor', 'admin')),
  organisation_id UUID REFERENCES public.organisations(id),
  created_at TIMESTAMPTZ NOT NULL DEFAULT now()
);

CREATE INDEX idx_users_clerk_id ON public.users(clerk_user_id);
CREATE INDEX idx_users_organisation_id ON public.users(organisation_id);

ALTER TABLE public.users ENABLE ROW LEVEL SECURITY;

-- Users can read their own row
CREATE POLICY "Users can view own profile"
  ON public.users FOR SELECT TO authenticated
  USING (clerk_user_id = (select auth.jwt()->>'sub'));

-- Users can update their own row
CREATE POLICY "Users can update own profile"
  ON public.users FOR UPDATE TO authenticated
  USING (clerk_user_id = (select auth.jwt()->>'sub'))
  WITH CHECK (clerk_user_id = (select auth.jwt()->>'sub'));

-- Admins can read users in their organisation
CREATE POLICY "Admins can view org users"
  ON public.users FOR SELECT TO authenticated
  USING (
    organisation_id IN (
      SELECT u.organisation_id FROM public.users u
      WHERE u.clerk_user_id = (select auth.jwt()->>'sub')
        AND u.role = 'admin'
    )
  );

