/**
 * Webhook replay protection — reject events older than 5 minutes.
 */

const MAX_AGE_SECONDS = 5 * 60; // 5 minutes

/**
 * Returns true if the webhook event timestamp is older than 5 minutes.
 * @param createdAtSeconds - Unix timestamp in seconds (e.g. Razorpay created_at, Stripe event.created)
 */
export function isWebhookStale(createdAtSeconds: number | undefined): boolean {
  if (createdAtSeconds === undefined || createdAtSeconds === null) return false;
  const nowSeconds = Math.floor(Date.now() / 1000);
  return nowSeconds - createdAtSeconds > MAX_AGE_SECONDS;
}

