import { describe, it, expect } from "vitest";
import { isWebhookStale } from "./webhook-guard";

describe("isWebhookStale", () => {
  it("should return true for events older than 5 minutes", () => {
    const sixMinutesAgo = Math.floor(Date.now() / 1000) - 6 * 60;
    expect(isWebhookStale(sixMinutesAgo)).toBe(true);
  });

  it("should return false for events within the last 5 minutes", () => {
    const oneMinuteAgo = Math.floor(Date.now() / 1000) - 60;
    expect(isWebhookStale(oneMinuteAgo)).toBe(false);
  });

  it("should return false for undefined input", () => {
    expect(isWebhookStale(undefined)).toBe(false);
  });
});

