/**
 * Strip PII fields from project metadata before sending to AI.
 * Only non-PII fields (department, degree, speciality, study details) are kept.
 */

const PII_KEYS = new Set([
  "candidate_name",
  "guide_name",
  "hod_name",
  "registration_no",
  "supervisor_user_id",
]);

export function sanitiseMetadataForAI(
  metadata: Record<string, unknown>
): Record<string, unknown> {
  const cleaned: Record<string, unknown> = {};
  for (const [key, value] of Object.entries(metadata)) {
    if (!PII_KEYS.has(key)) {
      cleaned[key] = value;
    }
  }
  return cleaned;
}

