 * the synopsis and previously completed sections.
 */
export function getPhaseUserMessage(
  phaseNumber: number,
  synopsis: string,
  metadata: Record<string, unknown>,
  previousSections: { phaseName: string; content: string }[]
): string {
  const safeMetadata = sanitiseMetadataForAI(metadata);
  const metadataStr = Object.entries(safeMetadata)
    .filter(([, v]) => v !== null && v !== undefined && v !== "")
    .map(([k, v]) => `${k}: ${String(v)}`)
    .join("
");

  const prevContext = previousSections.length > 0
    ? `

Previously completed sections for context:
${previousSections
        .map((s) => `--- ${s.phaseName} ---
${s.content.slice(0, 3000)}`)
        .join("

")}`
    : "";

  switch (phaseNumber) {
    case 0:
      return `Parse the following medical thesis synopsis and extract structured metadata as JSON:

${synopsis}`;

    case 1:
      return `Generate the front matter (acknowledgements and structured abstract) for this medical thesis.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 2:
      return `Write the Introduction chapter for this medical thesis. Use the synopsis as the primary source of truth for the research topic, and generate appropriate citations.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 3:
      return `Write the Aims and Objectives chapter for this medical thesis. Extract the aims and objectives EXACTLY as stated in the synopsis.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 4:
      return `Write the Review of Literature chapter for this medical thesis. Cover the topic comprehensively with proper citations, ending with a chronological summary longtable.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 5:
      return `Write the Materials and Methods chapter for this medical thesis. Follow the synopsis EXACTLY for study design, procedures, and criteria. Include all 12 NBEMS mandatory sections.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 6:
      return `Write the Results chapter for this medical thesis. Present ALL statistical findings from the completed analyses.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 7:
      return `Write the Discussion chapter for this medical thesis. Compare findings with literature, discuss strengths and limitations, and suggest future directions.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 8:
      return `Write the Conclusion chapter for this medical thesis. Provide a structured summary, data-driven conclusions, and recommendations.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    case 10:
      return `Generate the Appendices for this medical thesis. Include: Patient Information Sheet (PIS), Informed Consent Form (ICF), Master Chart headers, List of Abbreviations, and Ethics Approval Certificate placeholder.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;

    default:
      return `Generate content for this phase of the medical thesis.

Synopsis:
${synopsis}

Metadata:
${metadataStr}${prevContext}`;
  }
}

