  1→import { NextRequest } from "next/server";
  2→import * as Sentry from "@sentry/nextjs";
  3→import { getAuthenticatedUser } from "@/lib/api/auth";
  4→import {
  5→  unauthorised,
  6→  notFound,
  7→  badRequest,
  8→  internalError,
  9→  rateLimited,
 10→  conflict,
 11→} from "@/lib/api/errors";
 12→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 13→import { isValidPhase } from "@/lib/phases/transitions";
 14→import { getPhase } from "@/lib/phases/constants";
 15→import { getAnthropicClient } from "@/lib/ai/client";
 16→import { redactPII } from "@/lib/ai/redact";
 17→import { checkRateLimit } from "@/lib/ai/rate-limit";
 18→import {
 19→  SYNOPSIS_PARSE_SYSTEM_PROMPT,
 20→  getPhaseSystemPrompt,
 21→  getPhaseUserMessage,
 22→} from "@/lib/ai/prompts";
 23→import { parseSynopsisResponse } from "@/lib/ai/parse-synopsis-response";
 24→import { latexToTiptap } from "@/lib/latex/latex-to-tiptap";
 25→import { resolveSectionCitations, type CitationResolutionSummary } from "@/lib/citations/auto-resolve";
 26→import { preSeedReferences, formatReferencesForPrompt } from "@/lib/citations/pre-seed";
 27→import { checkTokenBudget, recordTokenUsage } from "@/lib/ai/token-budget";
 28→import type { Project } from "@/lib/types/database";
 29→
 30→// Sections stuck in "generating" for longer than this are considered stale
 31→const STALE_GENERATING_MS = 2 * 60 * 1000; // 2 minutes
 32→
 33→export async function POST(
 34→  request: NextRequest,
 35→  { params }: { params: Promise<{ id: string; phase: string }> }
 36→) {
 37→  try {
 38→    const authResult = await getAuthenticatedUser();
 39→    if (!authResult) return unauthorised();
 40→
 41→    const { id, phase } = await params;
 42→    const phaseNumber = parseInt(phase, 10);
 43→
 44→    if (!isValidPhase(phaseNumber)) {
 45→      return badRequest("Invalid phase number");
 46→    }
 47→
 48→    // Rate limit check
 49→    const rateCheck = checkRateLimit(authResult.user.id);
 50→    if (!rateCheck.allowed) {
 51→      return rateLimited(rateCheck.retryAfterSeconds);
 52→    }
 53→
 54→    const supabase = createAdminSupabaseClient();
 55→
 56→    // Fetch project
 57→    const { data: project, error: projectError } = await supabase
 58→      .from("projects")
 59→      .select("*")
 60→      .eq("id", id)
 61→      .eq("user_id", authResult.user.id)
 62→      .single();
 63→
 64→    if (projectError || !project) {
 65→      return notFound("Project not found");
 66→    }
 67→
 68→    const typedProject = project as Project;
 69→
 70→    // Phase 0: Synopsis parsing (special handling)
 71→    if (phaseNumber === 0) {
 72→      return handlePhase0Generate(request, typedProject, supabase);
 73→    }
 74→
 75→    // Phases 1-8: General thesis section generation
 76→    const systemPrompt = getPhaseSystemPrompt(phaseNumber);
 77→    if (!systemPrompt) {
 78→      return badRequest(`AI generation for Phase ${phaseNumber} is not yet supported`);
 79→    }
 80→
 81→    return handleSectionGenerate(phaseNumber, typedProject, supabase);
 82→  } catch (err) {
 83→    console.error("Unexpected error in POST generate:", err);
 84→    return internalError();
 85→  }
 86→}
 87→
 88→/**
 89→ * Check if a "generating" section is stale (stuck for > 5 minutes).
 90→ * If stale, reset to "draft" so re-generation can proceed.
 91→ */
 92→async function checkAndResetStaleGeneration(
 93→  supabase: ReturnType<typeof createAdminSupabaseClient>,
 94→  projectId: string,
 95→  phaseNumber: number
 96→): Promise<boolean> {
 97→  const { data: existingSection } = await supabase
 98→    .from("sections")
 99→    .select("status, updated_at")
100→    .eq("project_id", projectId)
101→    .eq("phase_number", phaseNumber)
102→    .single();
103→
104→  if (existingSection?.status !== "generating") {
105→    return false; // Not generating, no conflict
106→  }
107→
108→  // Check if the generation is stale
109→  const updatedAt = new Date(existingSection.updated_at).getTime();
110→  const isStale = Date.now() - updatedAt > STALE_GENERATING_MS;
111→
112→  if (isStale) {
113→    console.warn(
114→      `Resetting stale "generating" section: project=${projectId} phase=${phaseNumber} (stuck since ${existingSection.updated_at})`
115→    );
116→    await supabase
117→      .from("sections")
118→      .update({ status: "draft", updated_at: new Date().toISOString() })
119→      .eq("project_id", projectId)
120→      .eq("phase_number", phaseNumber);
121→    return false; // Reset done, no conflict
122→  }
123→
124→  return true; // Still actively generating
125→}
126→
127→/**
128→ * Create a cancel handler that resets section status when the client disconnects.
129→ */
130→function makeStreamCancelHandler(
131→  supabase: ReturnType<typeof createAdminSupabaseClient>,
132→  projectId: string,
133→  phaseNumber: number
134→) {
135→  return async () => {
136→    console.warn(
137→      `SSE stream cancelled (client disconnect): project=${projectId} phase=${phaseNumber} — resetting to draft`
138→    );
139→    await supabase
140→      .from("sections")
141→      .update({ status: "draft", updated_at: new Date().toISOString() })
142→      .eq("project_id", projectId)
143→      .eq("phase_number", phaseNumber);
144→  };
145→}
146→
147→/**
148→ * Generic handler for phases 1-8: generates thesis section content via SSE streaming.
149→ */
150→async function handleSectionGenerate(
151→  phaseNumber: number,
152→  project: Project,
153→  supabase: ReturnType<typeof createAdminSupabaseClient>
154→) {
155→  if (!project.synopsis_text) {
156→    return badRequest("Project has no synopsis text — upload a synopsis first");
157→  }
158→
159→  // Check section isn't already generating (with stale detection)
160→  const isActivelyGenerating = await checkAndResetStaleGeneration(
161→    supabase,
162→    project.id,
163→    phaseNumber
164→  );
165→
166→  if (isActivelyGenerating) {
167→    return conflict("Generation already in progress for this section");
168→  }
169→
170→  // Token budget check — hard-stop if budget exhausted
171→  const budgetCheck = await checkTokenBudget(project.id, phaseNumber);
172→  if (!budgetCheck.allowed) {
173→    return badRequest(budgetCheck.reason ?? "Token budget exhausted");
174→  }
175→
176→  // Gather context from previously completed sections
177→  const { data: approvedSections } = await supabase
178→    .from("sections")
179→    .select("phase_number, phase_name, latex_content")
180→    .eq("project_id", project.id)
181→    .eq("status", "approved")
182→    .order("phase_number", { ascending: true });
183→
184→  const previousSections = (approvedSections ?? [])
185→    .filter((s) => s.phase_number < phaseNumber && s.latex_content)
186→    .map((s) => ({
187→      phaseName: s.phase_name as string,
188→      content: s.latex_content as string,
189→    }));
190→
191→  // Redact PII from synopsis
192→  const { redacted: redactedSynopsis } = redactPII(project.synopsis_text);
193→
194→  // Get prompts
195→  const systemPrompt = getPhaseSystemPrompt(phaseNumber)!;
196→  let userMessage = getPhaseUserMessage(
197→    phaseNumber,
198→    redactedSynopsis,
199→    (project.metadata_json ?? {}) as Record<string, unknown>,
200→    previousSections
201→  );
202→
203→  // Phase 6 (Results): gather analysis context — summaries, tables, figures
204→  if (phaseNumber === 6) {
205→    const { data: completedAnalyses } = await supabase
206→      .from("analyses")
207→      .select("analysis_type, results_json, r_script")
208→      .eq("project_id", project.id)
209→      .eq("status", "completed")
210→      .order("created_at", { ascending: true });
211→
212→    const { data: projectFigures } = await supabase
213→      .from("figures")
214→      .select("figure_type, label, caption, file_url")
215→      .eq("project_id", project.id)
216→      .order("created_at", { ascending: true });
217→
218→    let analysisContext = "\n\n--- COMPLETED ANALYSES ---\n";
219→
220→    for (const analysis of completedAnalyses ?? []) {
221→      const results = analysis.results_json as Record<string, unknown>;
222→      analysisContext += `\nAnalysis: ${analysis.analysis_type}\n`;
223→      if (results.summary) {
224→        analysisContext += `Summary: ${JSON.stringify(results.summary, null, 2)}\n`;
225→      }
226→      if (results.table_latex) {
227→        analysisContext += `LaTeX Table (include VERBATIM):\n${results.table_latex}\n`;
228→      }
229→    }
230→
231→    if ((projectFigures ?? []).length > 0) {
232→      analysisContext += "\n--- FIGURES ---\n";
233→      for (const fig of projectFigures ?? []) {
234→        analysisContext += `Figure: \\label{${fig.label}} — ${fig.caption} (\\includegraphics{${fig.file_url}})\n`;
235→      }
236→    }
237→
238→    userMessage += analysisContext;
239→  }
240→
241→  // Pre-seed real PubMed references for citation-heavy phases
242→  const CITATION_HEAVY_PHASES = new Set([2, 4, 5, 7]);
243→  if (CITATION_HEAVY_PHASES.has(phaseNumber)) {
244→    try {
245→      const metadata = (project.metadata_json ?? {}) as Record<string, unknown>;
246→      const keywords = (metadata.keywords as string[]) ?? [];
247→      const studyType = (metadata.study_type as string) ?? null;
248→      const department = (metadata.department as string) ?? null;
249→      const maxRefs = phaseNumber === 4 ? 30 : 20; // ROL needs more references
250→
251→      const preSeeded = await preSeedReferences(
252→        project.title ?? "",
253→        keywords,
254→        studyType,
255→        department,
256→        maxRefs
257→      );
258→
259→      if (preSeeded.length > 0) {
260→        userMessage += formatReferencesForPrompt(preSeeded);
261→      }
262→    } catch (err) {
263→      console.warn("Pre-seed references failed (non-blocking):", err);
264→    }
265→  }
266→
267→  // Create/update section as "generating"
268→  const phaseDef = getPhase(phaseNumber);
269→  await supabase.from("sections").upsert(
270→    {
271→      project_id: project.id,
272→      phase_number: phaseNumber,
273→      phase_name: phaseDef?.name ?? `phase_${phaseNumber}`,
274→      latex_content: "",
275→      word_count: 0,
276→      citation_keys: [],
277→      status: "generating",
278→    },
279→    { onConflict: "project_id,phase_number" }
280→  );
281→
282→  // Model routing: Opus for Introduction/Discussion, Sonnet for others
283→  // (Haiku used separately for QC/review — see review route)
284→  const model = [2, 7].includes(phaseNumber)
285→    ? "claude-sonnet-4-5-20250929" // TODO: Switch to Opus when API access available
286→    : "claude-sonnet-4-5-20250929";
287→  // ROL needs most tokens; citation-heavy phases need extra for BibTeX trailer
288→  const maxTokens = phaseNumber === 4 ? 12000 : [2, 5, 7].includes(phaseNumber) ? 8000 : 6000;
289→
290→  // Track whether the stream completed normally
291→  let streamCompleted = false;
292→
293→  // Stream response via SSE
294→  const encoder = new TextEncoder();
295→  const stream = new ReadableStream({
296→    async start(controller) {
297→      try {
298→        const client = getAnthropicClient();
299→        let fullResponse = "";
300→
301→        const finalMessage = await Sentry.startSpan(
302→          {
303→            name: "claude.generate",
304→            op: "ai.generate",
305→            attributes: {
306→              "ai.model": model,
307→              "ai.phase": phaseNumber,
308→              "ai.max_tokens": maxTokens,
309→              "project.id": project.id,
310→            },
311→          },
312→          async () => {
313→            const messageStream = client.messages.stream({
314→              model,
315→              max_tokens: maxTokens,
316→              // Prompt caching: system prompt (GOLD Standard rules + phase instructions)
317→              // is stable across turns → cache for 60-70% token savings
318→              system: [
319→                {
320→                  type: "text" as const,
321→                  text: systemPrompt,
322→                  cache_control: { type: "ephemeral" as const },
323→                },
324→              ],
325→              messages: [
326→                { role: "user", content: userMessage },
327→              ],
328→            });
329→
330→            messageStream.on("text", (text) => {
331→              fullResponse += text;
332→              controller.enqueue(
333→                encoder.encode(`data: ${JSON.stringify({ type: "delta", text })}\n\n`)
334→              );
335→            });
336→
337→            return messageStream.finalMessage();
338→          }
339→        );
340→
341→        // Record token usage for budget tracking
342→        const totalTokens =
343→          (finalMessage.usage?.input_tokens ?? 0) +
344→          (finalMessage.usage?.output_tokens ?? 0);
345→        void recordTokenUsage(
346→          project.id,
347→          phaseNumber,
348→          totalTokens,
349→          model
350→        ).catch((err) =>
351→          console.error("Failed to record token usage:", err)
352→        );
353→
354→        // Convert LaTeX → Tiptap JSON for rich text editor
355→        const tiptapResult = latexToTiptap(fullResponse);
356→        const citationKeys = tiptapResult.citationKeys;
357→
358→        if (tiptapResult.warnings.length > 0) {
359→          console.warn(
360→            `LaTeX→Tiptap parse warnings (phase ${phaseNumber}):`,
361→            tiptapResult.warnings,
362→          );
363→        }
364→
365→        // Count words (rough: strip LaTeX commands)
366→        const plainText = fullResponse
367→          .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
368→          .replace(/\\[a-zA-Z]+/g, " ")
369→          .replace(/[{}\\]/g, " ");
370→        const wordCount = plainText.split(/\s+/).filter(Boolean).length;
371→
372→        // Update section with generated content + rich text JSON
373→        const { error: updateError } = await supabase
374→          .from("sections")
375→          .update({
376→            latex_content: fullResponse,
377→            rich_content_json: tiptapResult.json,
378→            ai_generated_latex: fullResponse,
379→            word_count: wordCount,
380→            citation_keys: citationKeys,
381→            status: "review",
382→            updated_at: new Date().toISOString(),
383→          })
384→          .eq("project_id", project.id)
385→          .eq("phase_number", phaseNumber);
386→
387→        // Fallback: if update failed (e.g. ai_generated_latex column missing),
388→        // retry without ai_generated_latex but keep rich_content_json
389→        if (updateError) {
390→          console.warn(
391→            `Section update failed (phase ${phaseNumber}), retrying without ai_generated_latex:`,
392→            updateError.message,
393→          );
394→          const { error: fallback1Error } = await supabase
395→            .from("sections")
396→            .update({
397→              latex_content: fullResponse,
398→              rich_content_json: tiptapResult.json,
399→              word_count: wordCount,
400→              citation_keys: citationKeys,
401→              status: "review",
402→              updated_at: new Date().toISOString(),
403→            })
404→            .eq("project_id", project.id)
405→            .eq("phase_number", phaseNumber);
406→
407→          // If that also fails (rich_content_json missing too), try core-only
408→          if (fallback1Error) {
409→            console.warn(
410→              `Section fallback also failed (phase ${phaseNumber}), retrying core-only:`,
411→              fallback1Error.message,
412→            );
413→            const { error: fallback2Error } = await supabase
414→              .from("sections")
415→              .update({
416→                latex_content: fullResponse,
417→                word_count: wordCount,
418→                citation_keys: citationKeys,
419→                status: "review",
420→                updated_at: new Date().toISOString(),
421→              })
422→              .eq("project_id", project.id)
423→              .eq("phase_number", phaseNumber);
424→
425→            if (fallback2Error) {
426→              console.error(
427→                `Section core-only update also failed (phase ${phaseNumber}):`,
428→                fallback2Error.message,
429→              );
430→            }
431→          }
432→        }
433→
434→        streamCompleted = true;
435→
436→        // Resolve citations from BibTeX trailer (15-second timeout)
437→        let citationSummary: CitationResolutionSummary | null = null;
438→        try {
439→          citationSummary = await Promise.race([
440→            resolveSectionCitations(project.id, fullResponse),
441→            new Promise<null>((resolve) => setTimeout(() => resolve(null), 15_000)),
442→          ]);
443→        } catch (err) {
444→          console.error("Citation resolution failed:", err);
445→        }
446→
447→        controller.enqueue(
448→          encoder.encode(
449→            `data: ${JSON.stringify({
450→              type: "complete",
451→              wordCount,
452→              citationKeys,
453→              parseWarnings: tiptapResult.warnings,
454→              citationSummary,
455→            })}\n\n`
456→          )
457→        );
458→
459→        controller.enqueue(encoder.encode("data: [DONE]\n\n"));
460→        controller.close();
461→      } catch (err) {
462→        // Reset section status on error
463→        await supabase
464→          .from("sections")
465→          .update({ status: "draft", updated_at: new Date().toISOString() })
466→          .eq("project_id", project.id)
467→          .eq("phase_number", phaseNumber);
468→
469→        streamCompleted = true; // Error handled, don't reset again in cancel
470→
471→        const errorMessage =
472→          err instanceof Error ? err.message : "AI generation failed";
473→        controller.enqueue(
474→          encoder.encode(
475→            `data: ${JSON.stringify({ type: "error", message: errorMessage })}\n\n`
476→          )
477→        );
478→        controller.close();
479→      }
480→    },
481→    cancel: async () => {
482→      // Client disconnected before stream completed — reset section status
483→      if (!streamCompleted) {
484→        await makeStreamCancelHandler(supabase, project.id, phaseNumber)();
485→      }
486→    },
487→  });
488→
489→  return new Response(stream, {
490→    headers: {
491→      "Content-Type": "text/event-stream",
492→      "Cache-Control": "no-cache",
493→      Connection: "keep-alive",
494→    },
495→  });
496→}
497→
498→/**
499→ * Phase 0: Synopsis parsing (returns structured JSON, not LaTeX).
500→ */
501→async function handlePhase0Generate(
502→  _request: NextRequest,
503→  project: Project,
504→  supabase: ReturnType<typeof createAdminSupabaseClient>
505→) {
506→  if (!project.synopsis_text) {
507→    return badRequest("Project has no synopsis text — upload a synopsis first");
508→  }
509→
510→  // Check section isn't already generating (with stale detection)
511→  const isActivelyGenerating = await checkAndResetStaleGeneration(
512→    supabase,
513→    project.id,
514→    0
515→  );
516→
517→  if (isActivelyGenerating) {
518→    return conflict("Generation already in progress for this section");
519→  }
520→
521→  // Redact PII before sending to AI
522→  const { redacted: redactedSynopsis } = redactPII(project.synopsis_text);
523→
524→  // Create/update section as "generating"
525→  const phaseDef = getPhase(0);
526→  await supabase.from("sections").upsert(
527→    {
528→      project_id: project.id,
529→      phase_number: 0,
530→      phase_name: phaseDef?.name ?? "orientation",
531→      latex_content: "",
532→      word_count: 0,
533→      citation_keys: [],
534→      status: "generating",
535→    },
536→    { onConflict: "project_id,phase_number" }
537→  );
538→
539→  let streamCompleted = false;
540→
541→  // Stream response via SSE
542→  const encoder = new TextEncoder();
543→  const stream = new ReadableStream({
544→    async start(controller) {
545→      try {
546→        const client = getAnthropicClient();
547→        let fullResponse = "";
548→
549→        const messageStream = client.messages.stream({
550→          model: "claude-sonnet-4-5-20250929",
551→          max_tokens: 2000,
552→          system: SYNOPSIS_PARSE_SYSTEM_PROMPT,
553→          messages: [
554→            {
555→              role: "user",
