 1→import { escapeLatexArg } from "./escape";
 2→import type { Project, ProjectMetadata } from "@/lib/types/database";
 3→
 4→/**
 5→ * Generate a populated main.tex from the template and project metadata.
 6→ * Replaces placeholder tokens in the template with escaped metadata values.
 7→ */
 8→
 9→interface TexGenerationResult {
10→  tex: string;
11→  warnings: string[];
12→}
13→
14→const FIELD_MAP: {
15→  command: string;
16→  metadataKey?: keyof ProjectMetadata;
17→  projectKey?: keyof Project;
18→  fallback: string;
19→}[] = [
20→  { command: "thesistitle", projectKey: "title", fallback: "[Thesis Title]" },
21→  { command: "candidatename", metadataKey: "candidate_name", fallback: "Dr. [Candidate Name]" },
22→  { command: "registrationno", metadataKey: "registration_no", fallback: "[Registration Number]" },
23→  { command: "supervisorname", metadataKey: "guide_name", fallback: "Prof (Dr.) [Guide Name]" },
24→  { command: "hodname", metadataKey: "hod_name", fallback: "Prof (Dr.) [HOD Name]" },
25→  { command: "departmentname", metadataKey: "department", fallback: "Department of [Specialty]" },
26→  { command: "degreename", metadataKey: "degree", fallback: "[M.D./M.S.]" },
27→  { command: "degreebranch", metadataKey: "speciality", fallback: "[Specialty]" },
28→  { command: "academicsession", metadataKey: "session", fallback: "[Start Year]--[End Year]" },
29→  { command: "submissionyear", metadataKey: "year", fallback: "[Year]" },
30→];
31→
32→export function generateTex(
33→  template: string,
34→  project: Project
35→): TexGenerationResult {
36→  let tex = template;
37→  const warnings: string[] = [];
38→  const metadata = project.metadata_json ?? {};
39→
40→  // Set university document class
41→  if (project.university_type === "ssuhs") {
42→    tex = tex
43→      .replace(
44→        /^\\documentclass\{sskm-thesis\}/m,
45→        "%\\documentclass{sskm-thesis}"
46→      )
47→      .replace(
48→        /^%\\documentclass\{ssuhs-thesis\}/m,
49→        "\\documentclass{ssuhs-thesis}"
50→      );
51→  }
52→
53→  // Set logo paths based on university type
54→  const logoMap: Record<string, { university: string; institute: string }> = {
55→    sskm: { university: "logo/wbuhs-logo", institute: "logo/sskm-logo" },
56→    ssuhs: { university: "logo/ssuhs-logo", institute: "logo/ssuhs-logo" },
57→  };
58→  const logos = logoMap[project.university_type ?? ""] ?? logoMap.sskm;
59→  // Insert logos and additional packages before \begin{document}
60→  // AI-generated content may use math symbols requiring these packages
61→  const logoCommands = `\\universitylogo{${logos.university}}\n\\institutelogo{${logos.institute}}`;
62→  const extraPackages = [
63→    "\\usepackage{mathrsfs}   % \\mathscr{} -- formal script math font",
64→    "\\usepackage{amssymb}    % Extended math symbols",
65→    "\\usepackage{graphicx}   % \\includegraphics for figures",
66→  ].join("\n");
67→  tex = tex.replace(
68→    /(\\begin\{document\})/,
69→    `${logoCommands}\n\n${extraPackages}\n\n$1`
70→  );
71→
72→  // Replace field values
73→  for (const field of FIELD_MAP) {
74→    let value: string | undefined;
75→
76→    if (field.metadataKey) {
77→      value = metadata[field.metadataKey] as string | undefined;
78→    }
79→    if (!value && field.projectKey) {
80→      value = project[field.projectKey] as string | undefined;
81→    }
82→
83→    if (value) {
84→      const escaped = escapeLatexArg(value);
85→      // Match the \command{...} pattern and replace the argument
86→      const regex = new RegExp(
87→        `(\\\\${field.command})\\{[^}]*\\}`,
88→        "m"
89→      );
90→      tex = tex.replace(regex, `$1{${escaped}}`);
91→    } else {
92→      warnings.push(`Missing metadata: ${field.command}`);
93→    }
94→  }
95→
96→  return { tex, warnings };
97→}
