  1→import { NextRequest, NextResponse } from "next/server";
  2→import { getAuthenticatedUser } from "@/lib/api/auth";
  3→import {
  4→  unauthorised,
  5→  notFound,
  6→  conflict,
  7→  licenceRequired,
  8→  internalError,
  9→  badRequest,
 10→} from "@/lib/api/errors";
 11→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 12→import { isValidPhase } from "@/lib/phases/transitions";
 13→import { canAdvancePhase } from "@/lib/phases/transitions";
 14→import { getPhase } from "@/lib/phases/constants";
 15→import { generateFrontMatterLatex } from "@/lib/latex/front-matter";
 16→import { auditCitations } from "@/lib/citations/audit";
 17→import type { Project, Section } from "@/lib/types/database";
 18→import { inngest } from "@/lib/inngest/client";
 19→
 20→export async function POST(
 21→  _request: NextRequest,
 22→  { params }: { params: Promise<{ id: string; phase: string }> }
 23→) {
 24→  try {
 25→    const authResult = await getAuthenticatedUser();
 26→    if (!authResult) return unauthorised();
 27→
 28→    const { id, phase } = await params;
 29→    const phaseNumber = parseInt(phase, 10);
 30→
 31→    if (!isValidPhase(phaseNumber)) {
 32→      return badRequest("Invalid phase number");
 33→    }
 34→
 35→    const supabase = createAdminSupabaseClient();
 36→
 37→    // Fetch project
 38→    const { data: project, error: projectError } = await supabase
 39→      .from("projects")
 40→      .select("*")
 41→      .eq("id", id)
 42→      .eq("user_id", authResult.user.id)
 43→      .single();
 44→
 45→    if (projectError || !project) {
 46→      return notFound("Project not found");
 47→    }
 48→
 49→    const typedProject = project as Project;
 50→
 51→    // Verify this is the current phase
 52→    if (typedProject.current_phase !== phaseNumber) {
 53→      return conflict(
 54→        `Cannot approve Phase ${phaseNumber} — project is currently at Phase ${typedProject.current_phase}`
 55→      );
 56→    }
 57→
 58→    // Fetch section
 59→    const { data: section, error: sectionError } = await supabase
 60→      .from("sections")
 61→      .select("*")
 62→      .eq("project_id", id)
 63→      .eq("phase_number", phaseNumber)
 64→      .single();
 65→
 66→    if (sectionError || !section) {
 67→      return notFound("Section not found — generate content first");
 68→    }
 69→
 70→    const typedSection = section as Section;
 71→
 72→    // Section must have approvable content
 73→    if (typedSection.status === "generating") {
 74→      return conflict("Section is still generating — wait for completion before approving");
 75→    }
 76→
 77→    if (typedSection.status === "approved") {
 78→      return conflict("Section is already approved");
 79→    }
 80→
 81→    if (typedSection.status === "draft") {
 82→      if (!typedSection.latex_content?.trim()) {
 83→        return badRequest("Cannot approve a draft section with no content — generate content first");
 84→      }
 85→      // Auto-promote draft with content to review, then approve below
 86→      await supabase
 87→        .from("sections")
 88→        .update({ status: "review", updated_at: new Date().toISOString() })
 89→        .eq("id", typedSection.id);
 90→    }
 91→
 92→    // At this point status is "review" (either originally or just promoted)
 93→
 94→    // Check if project can advance
 95→    const transitionCheck = canAdvancePhase(typedProject, "approved");
 96→
 97→    if (!transitionCheck.allowed) {
 98→      if (transitionCheck.code === "LICENCE_REQUIRED") {
 99→        return licenceRequired("Attach a licence to advance beyond Phase 1");
100→      }
101→      return conflict(transitionCheck.reason ?? "Cannot advance phase");
102→    }
103→
104→    // Approve section and advance phase
105→    const newPhase = phaseNumber + 1;
106→    const newPhasesCompleted = [
107→      ...typedProject.phases_completed,
108→      phaseNumber,
109→    ];
110→
111→    const [sectionResult, projectResult] = await Promise.all([
112→      supabase
113→        .from("sections")
114→        .update({ status: "approved", updated_at: new Date().toISOString() })
115→        .eq("id", typedSection.id)
116→        .select("*")
117→        .single(),
118→      supabase
119→        .from("projects")
120→        .update({
121→          current_phase: newPhase,
122→          phases_completed: newPhasesCompleted,
123→          updated_at: new Date().toISOString(),
124→        })
125→        .eq("id", id)
126→        .select("*")
127→        .single(),
128→    ]);
129→
130→    if (sectionResult.error || projectResult.error) {
131→      console.error("Failed to approve section:", sectionResult.error, projectResult.error);
132→      return internalError("Failed to approve section");
133→    }
134→
135→    // If Phase 0 was just approved, auto-create Phase 1 section with front matter
136→    if (phaseNumber === 0) {
137→      const phase1Def = getPhase(1);
138→      const updatedProject = projectResult.data as Project;
139→      const frontMatterLatex = generateFrontMatterLatex(updatedProject);
140→
141→      await supabase.from("sections").upsert(
142→        {
143→          project_id: id,
144→          phase_number: 1,
145→          phase_name: phase1Def?.name ?? "front_matter",
146→          latex_content: frontMatterLatex,
147→          word_count: frontMatterLatex.split(/\s+/).length,
148→          citation_keys: [],
149→          status: "review",
150→        },
151→        { onConflict: "project_id,phase_number" }
152→      );
153→    }
154→
155→    // Phase 8 approved → auto-create Phase 9 with citation audit results
156→    if (phaseNumber === 8) {
157→      const phase9Def = getPhase(9);
158→      try {
159→        // Fetch all sections and citations for audit
160→        const [{ data: allSections }, { data: allCitations }] = await Promise.all([
161→          supabase
162→            .from("sections")
163→            .select("*")
164→            .eq("project_id", id)
165→            .eq("status", "approved"),
166→          supabase
167→            .from("citations")
168→            .select("*")
169→            .eq("project_id", id),
170→        ]);
171→
172→        const auditResult = auditCitations(
173→          (allSections ?? []) as Section[],
174→          (allCitations ?? []) as unknown as import("@/lib/types/database").Citation[]
175→        );
176→
177→        const citations = allCitations ?? [];
178→        const auditLatex = [
179→          "\\section{Citation Audit Summary}",
180→          "",
181→          `Integrity Score: ${auditResult.integrityScore}\\%`,
182→          "",
183→          `Total citations: ${citations.length}`,
184→          `Tier A (verified): ${citations.filter((c) => c.provenance_tier === "A").length}`,
185→          `Tier B (confirmed): ${citations.filter((c) => c.provenance_tier === "B").length}`,
186→          `Tier C (attested): ${citations.filter((c) => c.provenance_tier === "C").length}`,
187→          `Tier D (unverified): ${citations.filter((c) => c.provenance_tier === "D").length}`,
188→          "",
189→          auditResult.missingCitations.length > 0
190→            ? `\\subsection{Missing Citations}\n${auditResult.missingCitations.map((m) => `\\texttt{${m.citeKey}}`).join(", ")}`
191→            : "No missing citations.",
192→          "",
193→          auditResult.orphanedCitations.length > 0
194→            ? `\\subsection{Orphaned Citations}\n${auditResult.orphanedCitations.map((o) => `\\texttt{${o.citeKey}}`).join(", ")}`
195→            : "No orphaned citations.",
196→          "",
197→          auditResult.tierDBlocking.length > 0
198→            ? `\\subsection{Tier D Citations (Blocking Final QC)}\n${auditResult.tierDBlocking.map((d) => `\\texttt{${d.citeKey}}`).join(", ")}`
199→            : "No Tier D citations blocking Final QC.",
200→        ].join("\n");
201→
202→        await supabase.from("sections").upsert(
203→          {
204→            project_id: id,
205→            phase_number: 9,
206→            phase_name: phase9Def?.name ?? "references",
207→            latex_content: auditLatex,
208→            word_count: auditLatex.split(/\s+/).length,
209→            citation_keys: [],
210→            status: "review",
211→          },
212→          { onConflict: "project_id,phase_number" }
213→        );
214→      } catch (auditError) {
215→        console.warn("Citation audit auto-trigger failed (non-blocking):", auditError);
216→      }
217→    }
218→
219→    // Phase 10 approved → auto-create Phase 11 with QC placeholder
220→    if (phaseNumber === 10) {
221→      const phase11Def = getPhase(11);
222→      await supabase.from("sections").upsert(
223→        {
224→          project_id: id,
225→          phase_number: 11,
226→          phase_name: phase11Def?.name ?? "final_qc",
227→          latex_content: "Run Final QC to generate the quality report.",
228→          word_count: 0,
229→          citation_keys: [],
230→          status: "review",
231→        },
232→        { onConflict: "project_id,phase_number" }
233→      );
234→    }
235→
236→    // Phase 11 approval: verify QC passed + set project to completed
237→    if (phaseNumber === 11) {
238→      await supabase
239→        .from("projects")
240→        .update({
241→          status: "completed",
242→          updated_at: new Date().toISOString(),
243→        })
244→        .eq("id", id);
245→    }
246→
247→    // Emit Inngest event for workflow orchestration (skip if not configured)
248→    if (process.env.INNGEST_EVENT_KEY) {
249→      try {
250→        await inngest.send({
251→          name: "thesis/phase.approved",
252→          data: {
253→            projectId: id,
254→            phaseNumber,
255→            userId: authResult.user.id,
256→          },
257→        });
258→      } catch (inngestError) {
259→        // Non-blocking — workflow will retry on next approval
260→        console.warn("Failed to emit Inngest event:", inngestError);
261→      }
262→    }
263→
264→    return NextResponse.json({
265→      data: {
266→        section: sectionResult.data,
267→        project: projectResult.data,
268→        advanced_to_phase: newPhase,
269→      },
270→    });
271→  } catch (err) {
272→    console.error("Unexpected error in POST /api/projects/[id]/sections/[phase]/approve:", err);
273→    return internalError();
274→  }
275→}
