  1→/**
  2→ * Global in-memory semaphore for compute resource admission control.
  3→ *
  4→ * Total capacity: 3 units
  5→ *   - compile job = 2 units
  6→ *   - analysis job = 1 unit
  7→ *
  8→ * Each job type has an independent queue with a max depth of 5.
  9→ *
 10→ * Per-user fairness: no single user can hold more than MAX_PER_USER
 11→ * concurrent analysis slots. This ensures round-robin fairness.
 12→ */
 13→
 14→import { randomUUID } from "crypto";
 15→
 16→export type JobType = "compile" | "analysis";
 17→
 18→export interface AcquireResult {
 19→  acquired: boolean;
 20→  position?: number;
 21→  estimatedWaitMs?: number;
 22→  jobId?: string;
 23→  /** If rejected due to per-user limit, this is set */
 24→  reason?: string;
 25→}
 26→
 27→interface ActiveJob {
 28→  type: JobType;
 29→  units: number;
 30→  projectId: string;
 31→  userId: string;
 32→}
 33→
 34→interface QueueEntry {
 35→  type: JobType;
 36→  projectId: string;
 37→  userId: string;
 38→  resolve: (jobId: string) => void;
 39→}
 40→
 41→const MAX_UNITS = 3;
 42→const MAX_QUEUE_DEPTH = 5;
 43→const MAX_PER_USER = 2; // No single user can hold >2 concurrent slots
 44→const MAX_ANALYSIS_CONCURRENT = 2; // R Plumber container limit
 45→const ESTIMATED_WAIT_PER_POSITION_MS = 30_000;
 46→
 47→const UNIT_COST: Record<JobType, number> = {
 48→  compile: 2,
 49→  analysis: 1,
 50→};
 51→
 52→const activeJobs = new Map<string, ActiveJob>();
 53→const queues: Record<JobType, QueueEntry[]> = {
 54→  compile: [],
 55→  analysis: [],
 56→};
 57→
 58→function usedUnits(): number {
 59→  let total = 0;
 60→  for (const job of activeJobs.values()) {
 61→    total += job.units;
 62→  }
 63→  return total;
 64→}
 65→
 66→/** Count active jobs for a specific user */
 67→function userActiveCount(userId: string): number {
 68→  let count = 0;
 69→  for (const job of activeJobs.values()) {
 70→    if (job.userId === userId) count++;
 71→  }
 72→  return count;
 73→}
 74→
 75→/** Count active analysis jobs across all users */
 76→function activeAnalysisCount(): number {
 77→  let count = 0;
 78→  for (const job of activeJobs.values()) {
 79→    if (job.type === "analysis") count++;
 80→  }
 81→  return count;
 82→}
 83→
 84→export function tryAcquire(
 85→  type: JobType,
 86→  projectId: string,
 87→  userId: string = "anonymous"
 88→): AcquireResult {
 89→  const cost = UNIT_COST[type];
 90→
 91→  // Per-user fairness: reject if user already at limit
 92→  if (userActiveCount(userId) >= MAX_PER_USER) {
 93→    // Try to queue instead of hard-rejecting
 94→    const queue = queues[type];
 95→    if (queue.length >= MAX_QUEUE_DEPTH) {
 96→      return {
 97→        acquired: false,
 98→        reason: "Per-user concurrency limit reached and queue full",
 99→      };
100→    }
101→
102→    const position = queue.length + 1;
103→    queue.push({ type, projectId, userId, resolve: () => {} });
104→
105→    return {
106→      acquired: false,
107→      position,
108→      estimatedWaitMs: position * ESTIMATED_WAIT_PER_POSITION_MS,
109→      reason: "Per-user concurrency limit reached — queued",
110→    };
111→  }
112→
113→  // Analysis concurrency limit: R Plumber can only handle 2 concurrent requests
114→  if (type === "analysis" && activeAnalysisCount() >= MAX_ANALYSIS_CONCURRENT) {
115→    const queue = queues[type];
116→    if (queue.length >= MAX_QUEUE_DEPTH) {
117→      return {
118→        acquired: false,
119→        reason: "Analysis concurrency limit reached and queue full",
120→      };
121→    }
122→    const position = queue.length + 1;
123→    queue.push({ type, projectId, userId, resolve: () => {} });
124→    return {
125→      acquired: false,
126→      position,
127→      estimatedWaitMs: position * ESTIMATED_WAIT_PER_POSITION_MS,
128→      reason: "Analysis concurrency limit reached — queued",
129→    };
130→  }
131→
132→  if (usedUnits() + cost <= MAX_UNITS) {
133→    const jobId = randomUUID();
134→    activeJobs.set(jobId, { type, units: cost, projectId, userId });
135→    return { acquired: true, jobId };
136→  }
137→
138→  // Not enough capacity — try to queue
139→  const queue = queues[type];
140→  if (queue.length >= MAX_QUEUE_DEPTH) {
141→    return { acquired: false };
142→  }
143→
144→  const position = queue.length + 1;
145→  queue.push({ type, projectId, userId, resolve: () => {} });
146→
147→  return {
148→    acquired: false,
149→    position,
150→    estimatedWaitMs: position * ESTIMATED_WAIT_PER_POSITION_MS,
151→  };
152→}
153→
154→export function release(jobId: string): void {
155→  const job = activeJobs.get(jobId);
156→  if (!job) return;
157→
158→  activeJobs.delete(jobId);
159→
160→  // Try to promote queued jobs, checking both queues
161→  for (const type of ["compile", "analysis"] as const) {
162→    const queue = queues[type];
163→    const cost = UNIT_COST[type];
164→
165→    while (queue.length > 0 && usedUnits() + cost <= MAX_UNITS) {
166→      // Respect R Plumber concurrency limit during promotion
167→      if (type === "analysis" && activeAnalysisCount() >= MAX_ANALYSIS_CONCURRENT) {
168→        break;
169→      }
170→
171→      const entry = queue[0]!;
172→
173→      // Check per-user limit before promoting
174→      if (userActiveCount(entry.userId) >= MAX_PER_USER) {
175→        // Skip this entry, try the next one with a different user
176→        const skippedIndex = findNextEligibleInQueue(queue);
177→        if (skippedIndex === -1) break; // No eligible entries
178→
179→        const eligible = queue.splice(skippedIndex, 1)[0]!;
180→        const newJobId = randomUUID();
181→        activeJobs.set(newJobId, {
182→          type,
183→          units: cost,
184→          projectId: eligible.projectId,
185→          userId: eligible.userId,
186→        });
187→        eligible.resolve(newJobId);
188→        continue;
189→      }
190→
191→      queue.shift();
192→      const newJobId = randomUUID();
193→      activeJobs.set(newJobId, {
194→        type,
195→        units: cost,
196→        projectId: entry.projectId,
197→        userId: entry.userId,
198→      });
199→      entry.resolve(newJobId);
200→    }
201→  }
202→}
203→
204→/** Find the first queue entry whose user is below the per-user limit */
205→function findNextEligibleInQueue(queue: QueueEntry[]): number {
206→  for (let i = 0; i < queue.length; i++) {
207→    if (userActiveCount(queue[i]!.userId) < MAX_PER_USER) {
208→      return i;
209→    }
210→  }
211→  return -1;
212→}
213→
214→export function getStatus(): {
215→  usedUnits: number;
216→  maxUnits: number;
217→  queueDepth: Record<JobType, number>;
218→} {
219→  return {
220→    usedUnits: usedUnits(),
221→    maxUnits: MAX_UNITS,
222→    queueDepth: {
223→      compile: queues.compile.length,
224→      analysis: queues.analysis.length,
225→    },
226→  };
227→}
