 20→// ── BibTeX splitter ─────────────────────────────────────────────────────────
 21→
 22→const BIBTEX_SEPARATOR = "---BIBTEX---";
 23→
 24→export interface SplitResult {
 25→  body: string;
 26→  bib: string;
 27→}
 28→
 29→/**
 30→ * Split section latex_content at the `---BIBTEX---` marker.
 31→ * Returns the body (before marker) and bib entries (after marker).
 32→ */
 33→export function splitBibtex(latexContent: string): SplitResult {
 34→  const idx = latexContent.indexOf(BIBTEX_SEPARATOR);
 35→  if (idx === -1) {
 36→    return { body: latexContent.trim(), bib: "" };
 37→  }
 38→  return {
 39→    body: latexContent.slice(0, idx).trim(),
 40→    bib: latexContent.slice(idx + BIBTEX_SEPARATOR.length).trim(),
 41→  };
 42→}
 43→
 44→// ── BibTeX deduplication ────────────────────────────────────────────────────
 45→
 46→/**
 47→ * Parse `@type{key,` entries from BibTeX content, deduplicate by cite key
 48→ * (last-write-wins so citations table entries override AI-generated ones).
 49→ */
 50→export function deduplicateBibEntries(bibContent: string): string {
 51→  if (!bibContent.trim()) return "";
 52→
 53→  const entries: { key: string; entry: string }[] = [];
 54→  const lines = bibContent.split("\n");
 55→  let currentEntry = "";
 56→  let currentKey = "";
 57→
 58→  for (const line of lines) {
 59→    const entryStart = line.match(/^@(\w+)\{([^,]+),/);
 60→    if (entryStart) {
 61→      if (currentEntry && currentKey) {
 62→        entries.push({ key: currentKey, entry: currentEntry.trim() });
 63→      }
 64→      currentKey = entryStart[2].trim();
 65→      currentEntry = line + "\n";
 66→    } else if (currentKey) {
 67→      currentEntry += line + "\n";
 68→    }
 69→  }
 70→
 71→  if (currentEntry && currentKey) {
 72→    entries.push({ key: currentKey, entry: currentEntry.trim() });
 73→  }
 74→
 75→  const seen = new Map<string, string>();
 76→  for (const { key, entry } of entries) {
 77→    seen.set(key, entry);
 78→  }
 79→
 80→  return Array.from(seen.values()).join("\n\n");
 81→}
 82→
 83→// ── Tier D citation stripping ────────────────────────────────────────────────
 84→
 85→/**
 86→ * Replace \cite{key} commands for Tier D citations with a LaTeX comment.
 87→ * Per PLAN.md §358-362: Tier D citations must NEVER appear as \cite{key}
 88→ * in compiled output — they are replaced with:
 89→ *   % UNRESOLVED: key — provide source or remove
 90→ */
 91→export function stripTierDCitations(
 92→  chapterBody: string,
 93→  tierDKeys: Set<string>
 94→): { stripped: string; replacedKeys: string[] } {
 95→  if (tierDKeys.size === 0) return { stripped: chapterBody, replacedKeys: [] };
 96→
 97→  const replacedKeys: string[] = [];
 98→
 99→  // Match \cite{key} and \cite{key1,key2,...} patterns
100→  const stripped = chapterBody.replace(
101→    /\\cite\{([^}]+)\}/g,
102→    (_match, keysStr: string) => {
103→      const keys = keysStr.split(",").map((k) => k.trim());
104→      const kept = keys.filter((k) => !tierDKeys.has(k));
105→      const removed = keys.filter((k) => tierDKeys.has(k));
106→
107→      for (const k of removed) {
108→        if (!replacedKeys.includes(k)) replacedKeys.push(k);
109→      }
110→
111→      if (kept.length === 0 && removed.length > 0) {
112→        // All keys are Tier D — replace entire \cite{} with comment
113→        return removed
114→          .map((k) => `% UNRESOLVED: ${k} --- provide source or remove`)
115→          .join("\n");
116→      }
117→
118→      if (removed.length > 0) {
119→        // Mixed — keep valid keys, comment out Tier D ones
120→        const comments = removed
121→          .map((k) => `% UNRESOLVED: ${k} --- provide source or remove`)
122→          .join("\n");
123→        return `\\cite{${kept.join(", ")}}\n${comments}`;
124→      }
125→
126→      // No Tier D keys in this cite command
127→      return _match;
128→    }
129→  );
130→
131→  return { stripped, replacedKeys };
132→}
133→
134→// ── Chapter body sanitisation ────────────────────────────────────────────────
135→
136→/**
137→ * Sanitise a chapter body after the tiptap round-trip:
138→ * 1. Strip bare markdown separators (--- / *** / ___)
139→ * 2. Repair unbalanced \begin{}/\end{} environments
140→ * 3. Repair unbalanced braces
141→ */
142→function sanitiseChapterLatex(latex: string): string {
143→  // 1. Strip markdown separators (lines that are just --- or *** or ___)
144→  let result = latex.replace(/^\s*[-*_]{3,}\s*$/gm, "");
145→
146→  // 2. Repair environment balance
147→  const beginRe = /\\begin\{(\w+)\}/g;
148→  const endRe = /\\end\{(\w+)\}/g;
149→  const envStack: string[] = [];
150→  const tokens: { type: "begin" | "end"; name: string; pos: number }[] = [];
151→
152→  for (const m of result.matchAll(beginRe)) {
153→    tokens.push({ type: "begin", name: m[1], pos: m.index! });
154→  }
155→  for (const m of result.matchAll(endRe)) {
156→    tokens.push({ type: "end", name: m[1], pos: m.index! });
157→  }
158→  tokens.sort((a, b) => a.pos - b.pos);
159→
160→  // Track unmatched \end{} positions to remove
161→  const unmatchedEndPositions: { pos: number; len: number }[] = [];
162→
163→  for (const token of tokens) {
164→    if (token.type === "begin") {
165→      envStack.push(token.name);
166→    } else {
167→      if (envStack.length > 0 && envStack[envStack.length - 1] === token.name) {
168→        envStack.pop();
169→      } else {
170→        // Unmatched \end{} — mark for removal
171→        const fullMatch = `\\end{${token.name}}`;
172→        unmatchedEndPositions.push({ pos: token.pos, len: fullMatch.length });
173→      }
174→    }
175→  }
176→
177→  // Remove unmatched \end{} from end to start (so positions stay valid)
178→  for (let i = unmatchedEndPositions.length - 1; i >= 0; i--) {
179→    const { pos, len } = unmatchedEndPositions[i];
180→    result = result.slice(0, pos) + result.slice(pos + len);
181→  }
182→
183→  // Close any remaining unclosed environments in LIFO order
184→  if (envStack.length > 0) {
185→    const closings = envStack.reverse().map((env) => `\\end{${env}}`).join("\n");
186→    result = result.trimEnd() + "\n" + closings + "\n";
187→  }
188→
189→  // 3. Repair unbalanced braces
190→  let braceDepth = 0;
191→  let inComment = false;
192→  for (let i = 0; i < result.length; i++) {
193→    const ch = result[i];
194→    if (ch === "%" && (i === 0 || result[i - 1] !== "\\")) {
195→      inComment = true;
196→      continue;
197→    }
198→    if (ch === "\n") {
199→      inComment = false;
200→      continue;
201→    }
202→    if (inComment) continue;
203→    if (ch === "{" && (i === 0 || result[i - 1] !== "\\")) braceDepth++;
204→    else if (ch === "}" && (i === 0 || result[i - 1] !== "\\")) braceDepth--;
205→  }
206→
207→  if (braceDepth > 0) {
208→    // More { than } — append closing braces
209→    result = result.trimEnd() + "}".repeat(braceDepth) + "\n";
210→  } else if (braceDepth < 0) {
211→    // More } than { — prepend opening braces
212→    result = "{".repeat(Math.abs(braceDepth)) + result;
213→  }
214→
215→  return result;
216→}
217→
218→// ── Bare ampersand sanitisation ──────────────────────────────────────────────
219→
220→/**
221→ * Escape bare `&` characters outside of tabular environments.
222→ *
223→ * In LaTeX, `&` is a column separator inside tabular/longtable/array — valid there.
224→ * Everywhere else it causes "Misplaced alignment tab character &".
225→ *
226→ * The tiptap round-trip escapes `&` in text nodes via `escapeLatex()`, but
227→ * content inside `codeBlock` nodes (non-tabular environments like `\begin{center}`)
228→ * passes through raw. AI may output bare `&` inside such environments.
229→ */
230→function escapeBareAmpersands(latex: string): string {
231→  const lines = latex.split("\n");
232→  let tabularDepth = 0;
233→
234→  return lines
235→    .map((line) => {
236→      const opens = (
237→        line.match(/\\begin\{(tabular|longtable|tabularx|array)\}/g) || []
238→      ).length;
239→      const closes = (
240→        line.match(/\\end\{(tabular|longtable|tabularx|array)\}/g) || []
241→      ).length;
242→
243→      // Inside a tabular env — & is a column separator, leave it
244→      if (tabularDepth > 0 || opens > 0) {
245→        tabularDepth += opens - closes;
246→        tabularDepth = Math.max(0, tabularDepth);
247→        return line;
248→      }
249→
250→      tabularDepth += opens - closes;
251→      tabularDepth = Math.max(0, tabularDepth);
252→
253→      // Skip comment lines
254→      if (line.trimStart().startsWith("%")) return line;
255→
256→      // Escape bare & (not preceded by \)
257→      return line.replace(/(?<!\\)&/g, "\\&");
258→    })
259→    .join("\n");
260→}
261→
262→// ── DOI underscore escaping ──────────────────────────────────────────────────
263→
264→/**
265→ * Escape bare `_` in DOI field values.
266→ * BibTeX passes DOI strings through to the .bbl file unchanged.
267→ * Bare `_` in DOIs like `10.4103/jgid.jgid_132_18` causes
268→ * "Missing $ inserted" errors in pdfLaTeX.
269→ */
270→function escapeDOIUnderscores(bibContent: string): string {
271→  // Match doi = {value} and doi = "value" patterns
272→  return bibContent.replace(
273→    /^(\s*doi\s*=\s*\{)([^}]+)(\})/gm,
274→    (_match, prefix: string, value: string, suffix: string) => {
275→      // Escape bare _ (not already escaped as \_)
276→      const escaped = value.replace(/(?<!\\)_/g, "\\_");
277→      return prefix + escaped + suffix;
278→    }
279→  );
280→}
281→
282→// ── Front matter injection ──────────────────────────────────────────────────
283→
284→/**
285→ * Extract Phase 1 content and inject acknowledgements + abstract into the
286→ * template's \begin{acknowledgements}...\end{acknowledgements} and
287→ * \begin{thesis_abstract}...\end{thesis_abstract} environments.
288→ */
289→function injectFrontMatter(
290→  tex: string,
291→  sections: Section[],
292→  warnings: string[]
293→): string {
294→  const phase1 = sections
295→    .filter((s) => s.phase_number === 1)
296→    .sort((a, b) => {
297→      const priority: Record<string, number> = { approved: 0, review: 1 };
298→      return (priority[a.status] ?? 2) - (priority[b.status] ?? 2);
299→    })[0];
300→
301→  if (!phase1) return tex;
302→
303→  // Get content via same pipeline as chapters
304→  let content = "";
305→  if (phase1.rich_content_json) {
306→    const r = tiptapToLatex(phase1.rich_content_json as unknown as TiptapNode);
307→    content = r.latex;
308→  } else if (phase1.latex_content) {
309→    const { body } = splitBibtex(phase1.latex_content);
310→    const tiptapJson = latexToTiptap(body);
311→    const r = tiptapToLatex(tiptapJson.json);
312→    content = r.latex;
313→  }
314→
315→  if (!content.trim()) return tex;
316→
317→  // Split at ABSTRACT heading — try \section{ABSTRACT} first, then markdown
318→  let ackText = content;
319→  let abstractText = "";
320→
321→  const abstractPatterns = [
322→    /\\section\*?\{[^}]*ABSTRACT[^}]*\}/i,
323→    /\\section\*?\{[^}]*Abstract[^}]*\}/,
324→  ];
325→
326→  for (const pattern of abstractPatterns) {
327→    const match = content.match(pattern);
328→    if (match?.index !== undefined) {
329→      ackText = content.slice(0, match.index).trim();
330→      abstractText = content.slice(match.index + match[0].length).trim();
331→      break;
332→    }
333→  }
334→
335→  // Clean acknowledgements — remove heading, keep body paragraphs
336→  ackText = ackText
337→    .replace(/\\section\*?\{[^}]*ACKNOWLEDGEMENTS[^}]*\}/i, "")
338→    .replace(/\\section\*?\{[^}]*Acknowledgements[^}]*\}/i, "")
339→    .trim();
340→
341→  // Inject acknowledgements (sanitise bare & in case AI missed escaping)
342→  if (ackText) {
343→    const safeAck = escapeBareAmpersands(ackText);
344→    tex = tex.replace(
345→      /\\begin\{acknowledgements\}[\s\S]*?\\end\{acknowledgements\}/,
346→      `\\begin{acknowledgements}\n\n${safeAck}\n\n\\end{acknowledgements}`
347→    );
348→  }
349→
350→  // Format abstract: convert \subsection{Label} → \noindent\textbf{Label:}
351→  if (abstractText) {
352→    const formatted = abstractText
353→      .replace(
354→        /\\subsection\*?\{([^}]+)\}/g,
355→        (_m, label: string) =>
356→          `\\vspace{0.3cm}\n\\noindent\\textbf{${label.trim()}:}`
357→      )
358→      // Remove leading \vspace before first label
359→      .replace(/^\s*\\vspace\{0\.3cm\}\s*\n/, "");
360→
361→    const safeAbstract = escapeBareAmpersands(formatted);
362→    tex = tex.replace(
363→      /\\begin\{thesis_abstract\}[\s\S]*?\\end\{thesis_abstract\}/,
364→      `\\begin{thesis_abstract}\n\n${safeAbstract}\n\n\\end{thesis_abstract}`
365→    );
366→  } else {
367→    warnings.push("Phase 1: could not split abstract from acknowledgements");
368→  }
369→
370→  return tex;
371→}
