373→// ── Main assembly function ──────────────────────────────────────────────────
374→
375→export interface AssembleResult {
376→  tex: string;
377→  bib: string;
378→  chapterFiles: Record<string, string>;
379→  warnings: string[];
380→}
381→
382→/**
383→ * Assemble the thesis .tex, .bib, and chapter files from template,
384→ * project metadata, approved/review sections, and citations.
385→ *
386→ * 1. Populate metadata via `generateTex()`
387→ * 2. For each phase 2–8: find the section, produce chapter body + extract BibTeX
388→ * 3. Chapter body source: `tiptapToLatex(rich_content_json)` if available,
389→ *    else `splitBibtex(latex_content).body` as fallback
390→ * 4. BibTeX source: `ai_generated_latex` (preserves ---BIBTEX--- trailer after user edits),
391→ *    else fallback to `latex_content`
392→ * 5. Each chapter body → `chapterFiles["chapters/xxx.tex"]`
393→ * 6. Missing chapters → empty file (LaTeX `\input{}` of empty file is fine)
394→ * 7. Collect BibTeX from all sections + citations table, deduplicate
395→ */
396→export function assembleThesisContent(
397→  template: string,
398→  project: Project,
399→  sections: Section[],
400→  citations: Citation[]
401→): AssembleResult {
402→  const warnings: string[] = [];
403→
404→  // Step 1: Populate metadata
405→  const { tex: metadataTex, warnings: metaWarnings } = generateTex(template, project);
406→  warnings.push(...metaWarnings);
407→
408→  // Step 1b: Inject Phase 1 front matter (acknowledgements + abstract)
409→  const tex = injectFrontMatter(metadataTex, sections, warnings);
410→  const bibParts: string[] = [];
411→  const chapterFiles: Record<string, string> = {};
412→
413→  // Build set of Tier D cite keys (to strip from compiled output)
414→  const tierDKeys = new Set<string>(
415→    citations
416→      .filter((c) => c.provenance_tier === "D")
417→      .map((c) => c.cite_key)
418→  );
419→
420→  // Step 2: For each chapter phase, produce chapter file content
421→  for (const [phaseStr, chapterPath] of Object.entries(PHASE_CHAPTER_MAP)) {
422→    const phaseNum = parseInt(phaseStr, 10);
423→
424→    // Find the best section: prefer approved, then review
425→    const section = sections
426→      .filter((s) => s.phase_number === phaseNum)
427→      .sort((a, b) => {
428→        const priority: Record<string, number> = { approved: 0, review: 1 };
429→        return (priority[a.status] ?? 2) - (priority[b.status] ?? 2);
430→      })[0];
431→
432→    if (!section) {
433→      warnings.push(`Phase ${phaseNum}: no approved/review content — chapter will be empty`);
434→      chapterFiles[chapterPath] = "";
435→      continue;
436→    }
437→
438→    // Chapter body: prefer tiptap round-trip (sanitised, escaped)
439→    let chapterBody = "";
440→
441→    if (section.rich_content_json) {
442→      const tiptapResult = tiptapToLatex(section.rich_content_json as unknown as TiptapNode);
443→      chapterBody = tiptapResult.latex;
444→      if (tiptapResult.warnings.length > 0) {
445→        warnings.push(
446→          `Phase ${phaseNum} tiptap warnings: ${tiptapResult.warnings.join(", ")}`
447→        );
448→      }
449→    } else if (section.latex_content) {
450→      // Fallback: sanitise raw latex_content via tiptap round-trip.
451→      // This strips markdown artifacts (# headings), \needspace, comments,
452→      // and escapes LaTeX special characters like bare #.
453→      const { body } = splitBibtex(section.latex_content);
454→      const tiptapJson = latexToTiptap(body);
455→      const roundTripped = tiptapToLatex(tiptapJson.json);
456→      chapterBody = roundTripped.latex;
457→      if (roundTripped.warnings.length > 0) {
458→        warnings.push(
459→          `Phase ${phaseNum} fallback round-trip warnings: ${roundTripped.warnings.join(", ")}`
460→        );
461→      }
462→    }
463→
464→    if (!chapterBody) {
465→      warnings.push(`Phase ${phaseNum}: section exists but body is empty`);
466→    }
467→
468→    // Strip Tier D \cite{key} → replace with % UNRESOLVED comment
469→    if (chapterBody && tierDKeys.size > 0) {
470→      const { stripped, replacedKeys } = stripTierDCitations(chapterBody, tierDKeys);
471→      chapterBody = stripped;
472→      if (replacedKeys.length > 0) {
473→        warnings.push(
474→          `Phase ${phaseNum}: stripped Tier D citations: ${replacedKeys.join(", ")}`
475→        );
476→      }
477→    }
478→
479→    // Sanitise bare & outside tabular environments (AI may output unescaped &)
480→    if (chapterBody) {
481→      chapterBody = escapeBareAmpersands(chapterBody);
482→    }
483→
484→    // Sanitise: strip markdown separators, repair environment/brace balance
485→    if (chapterBody) {
486→      chapterBody = sanitiseChapterLatex(chapterBody);
487→    }
488→
489→    chapterFiles[chapterPath] = chapterBody;
490→
491→    // BibTeX: prefer ai_generated_latex (preserves ---BIBTEX--- even after user edits)
492→    const bibSource = section.ai_generated_latex ?? section.latex_content;
493→    if (bibSource) {
494→      const { bib } = splitBibtex(bibSource);
495→      if (bib) {
496→        bibParts.push(bib);
497→      }
498→    }
499→  }
500→
501→  // Step 3: Collect BibTeX from citations table (exclude Tier D — they have no \cite{})
502→  for (const citation of citations) {
503→    if (citation.bibtex_entry?.trim() && citation.provenance_tier !== "D") {
504→      bibParts.push(citation.bibtex_entry.trim());
505→    }
506→  }
507→
508→  // Step 4: Deduplicate and sanitise BibTeX
509→  const rawBib = bibParts.join("\n\n");
510→  const dedupedBib = deduplicateBibEntries(rawBib);
511→  // Normalise Unicode (smart quotes, em-dashes) in BibTeX field values —
512→  // external APIs (CrossRef, PubMed) and AI often return these characters
513→  // which cause "Invalid UTF-8 byte sequence" during compilation.
514→  const normalisedBib = normaliseUnicode(dedupedBib);
515→  // Escape bare & in BibTeX field values — AI often generates journal names
516→  // like "Endocrinology & Metabolism" instead of "Endocrinology \& Metabolism".
517→  const ampEscapedBib = escapeBareAmpersands(normalisedBib);
518→  // Escape bare _ in doi fields — BibTeX passes DOI values through to .bbl
519→  // as-is, and bare _ causes "Missing $ inserted" in pdflatex.
520→  const bib = escapeDOIUnderscores(ampEscapedBib);
521→
522→  return { tex, bib, chapterFiles, warnings };
523→}
