  1→import * as Sentry from "@sentry/nextjs";
  2→import { mkdir, writeFile } from "fs/promises";
  3→import path from "path";
  4→import os from "os";
  5→import { callRPlumber, RPlumberError } from "./client";
  6→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
  7→import {
  8→  ANALYSIS_TIMEOUTS,
  9→  REQUIRED_PARAMS,
 10→  type AnalysisType,
 11→} from "@/lib/validation/analysis-schemas";
 12→import type { Analysis } from "@/lib/types/database";
 13→
 14→/** Base directory for figure file storage (dev: tmpdir, prod: R2) */
 15→const FIGURES_BASE_DIR = path.join(os.tmpdir(), "apollo-figures");
 16→
 17→/** R Plumber response shape (consistent across all endpoints) */
 18→export interface RAnalysisResponse {
 19→  summary: Record<string, unknown>;
 20→  table_latex: string;
 21→  figures: { filename: string; base64: string }[];
 22→  warnings: string[];
 23→  r_script: string;
 24→}
 25→
 26→/** Result of running an analysis */
 27→export interface AnalysisResult {
 28→  results_json: Record<string, unknown>;
 29→  figures_urls: string[];
 30→  r_script: string;
 31→  warnings: string[];
 32→}
 33→
 34→/** Map analysis type to R Plumber endpoint */
 35→const ENDPOINT_MAP: Record<AnalysisType, string> = {
 36→  descriptive: "/descriptive",
 37→  "chi-square": "/chi-square",
 38→  "t-test": "/t-test",
 39→  correlation: "/correlation",
 40→  survival: "/survival",
 41→  roc: "/roc",
 42→  logistic: "/logistic",
 43→  kruskal: "/kruskal",
 44→  "meta-analysis": "/meta-analysis",
 45→};
 46→
 47→/**
 48→ * Run a statistical analysis by dispatching to the correct R Plumber endpoint.
 49→ * Updates the analysis record in the database with results.
 50→ */
 51→export async function runAnalysis(
 52→  analysis: Analysis,
 53→  datasetRows: Record<string, unknown>[]
 54→): Promise<AnalysisResult> {
 55→  const analysisType = analysis.analysis_type as AnalysisType;
 56→  const endpoint = ENDPOINT_MAP[analysisType];
 57→  const timeout = ANALYSIS_TIMEOUTS[analysisType] ?? 30_000;
 58→  const params = analysis.parameters_json;
 59→
 60→  if (!endpoint) {
 61→    throw new Error(`Unknown analysis type: ${analysisType}`);
 62→  }
 63→
 64→  // Pre-flight: validate data is not empty
 65→  if (datasetRows.length === 0) {
 66→    throw new Error(
 67→      `Cannot run ${analysisType} analysis: dataset has no rows. ` +
 68→      "Please re-upload the dataset file."
 69→    );
 70→  }
 71→
 72→  // Pre-flight: validate required parameters are present
 73→  const requiredParams = REQUIRED_PARAMS[analysisType] ?? [];
 74→  const missingParams = requiredParams.filter(
 75→    (key) => !params[key] || (typeof params[key] === "string" && params[key].trim() === "")
 76→  );
 77→  if (missingParams.length > 0) {
 78→    throw new Error(
 79→      `Cannot run ${analysisType} analysis: missing required parameters: ${missingParams.join(", ")}. ` +
 80→      "Please specify the required column mappings."
 81→    );
 82→  }
 83→
 84→  // Pre-flight: validate that referenced columns exist in the dataset
 85→  const dataColumns = Object.keys(datasetRows[0] ?? {});
 86→  const columnParams = ["outcome", "predictor", "group", "time", "event"] as const;
 87→  for (const key of columnParams) {
 88→    const colName = params[key] as string | undefined;
 89→    if (colName && colName.trim() !== "" && !colName.includes(",")) {
 90→      // Single column reference — check it exists (skip comma-separated predictors)
 91→      if (!dataColumns.includes(colName)) {
 92→        throw new Error(
 93→          `Column "${colName}" (${key}) not found in dataset. ` +
 94→          `Available columns: ${dataColumns.join(", ")}`
 95→        );
 96→      }
 97→    }
 98→  }
 99→
100→  // Build the request body — only include defined parameters
101→  const requestBody: Record<string, unknown> = {
102→    data: datasetRows,
103→    confidence_level: params.confidence_level ?? 0.95,
104→  };
105→  // Only add non-empty string parameters to avoid sending undefined/null to R
106→  for (const key of columnParams) {
107→    const val = params[key] as string | undefined;
108→    if (val && val.trim() !== "") {
109→      requestBody[key] = val;
110→    }
111→  }
112→  // Pass figure preferences if present
113→  const figPrefs = params.figure_preferences as Record<string, unknown> | undefined;
114→  if (figPrefs) {
115→    if (figPrefs.chart_type && figPrefs.chart_type !== "auto") {
116→      requestBody.chart_type = figPrefs.chart_type;
117→    }
118→    if (figPrefs.colour_scheme && figPrefs.colour_scheme !== "default") {
119→      requestBody.colour_scheme = figPrefs.colour_scheme;
120→    }
121→  }
122→
123→  const { data: rResult } = await Sentry.startSpan(
124→    {
125→      name: `r-plumber.${analysisType}`,
126→      op: "r.analysis",
127→      attributes: {
128→        "analysis.type": analysisType,
129→        "analysis.endpoint": endpoint,
130→        "analysis.timeout_ms": timeout,
131→        "analysis.id": analysis.id,
132→        "project.id": analysis.project_id,
133→      },
134→    },
135→    () => callRPlumber<RAnalysisResponse>(endpoint, requestBody, timeout)
136→  );
137→
138→  // Process figures — decode base64 and write to disk + insert DB record
139→  const figureUrls: string[] = [];
140→  const supabase = createAdminSupabaseClient();
141→
142→  for (const fig of rResult.figures ?? []) {
143→    if (!fig || !fig.base64) continue;
144→
145→    const figureUrl = `figures/${analysis.project_id}/${analysis.id}/${fig.filename}`;
146→    figureUrls.push(figureUrl);
147→
148→    // Write decoded figure file to disk so LaTeX compile can find it
149→    try {
150→      const figDir = path.join(
151→        FIGURES_BASE_DIR,
152→        analysis.project_id,
153→        analysis.id
154→      );
155→      await mkdir(figDir, { recursive: true });
156→      const figPath = path.join(figDir, fig.filename);
157→      const buffer = Buffer.from(fig.base64, "base64");
158→      await writeFile(figPath, buffer);
159→    } catch (err) {
160→      console.warn(`Failed to write figure file ${figureUrl}:`, err);
161→    }
162→
163→    // Insert figure record
164→    await supabase.from("figures").insert({
165→      project_id: analysis.project_id,
166→      figure_type: analysisType,
167→      source_tool: "ggplot2",
168→      source_code: rResult.r_script,
169→      file_url: figureUrl,
170→      caption: `${analysisType} analysis — ${fig.filename}`,
171→      label: `fig:${analysisType}-${fig.filename.replace(/\.[^.]+$/, "").replace(/[^a-z0-9-]/g, "-")}`,
172→      width_pct: 100,
173→      dpi: 300,
174→      format: "pdf",
175→    });
176→  }
177→
178→  return {
179→    results_json: {
180→      summary: rResult.summary,
181→      table_latex: rResult.table_latex,
182→    },
183→    figures_urls: figureUrls,
184→    r_script: rResult.r_script ?? "",
185→    warnings: rResult.warnings ?? [],
186→  };
187→}
188→
189→/**
190→ * Execute analysis end-to-end: fetch data, run R, update DB status.
191→ */
192→export async function executeAnalysis(analysisId: string): Promise<void> {
193→  const supabase = createAdminSupabaseClient();
194→
195→  // Update status to running
196→  await supabase
197→    .from("analyses")
198→    .update({ status: "running" })
199→    .eq("id", analysisId);
200→
201→  try {
202→    // Fetch analysis + dataset
203→    const { data: analysis, error: fetchErr } = await supabase
204→      .from("analyses")
205→      .select("*")
206→      .eq("id", analysisId)
207→      .single();
208→
209→    if (fetchErr || !analysis) {
210→      throw new Error(`Analysis ${analysisId} not found`);
211→    }
212→
213→    const typedAnalysis = analysis as Analysis;
214→
215→    if (!typedAnalysis.dataset_id) {
216→      throw new Error("Analysis has no associated dataset");
217→    }
218→
219→    // Fetch dataset with stored rows
220→    const { data: dataset } = await supabase
221→      .from("datasets")
222→      .select("*")
223→      .eq("id", typedAnalysis.dataset_id)
224→      .single();
225→
226→    if (!dataset) {
227→      throw new Error(`Dataset ${typedAnalysis.dataset_id} not found`);
228→    }
229→
230→    // Use stored rows_json — fail if missing (dataset must be re-uploaded)
231→    const datasetRows = dataset.rows_json as Record<string, unknown>[] | null;
232→    if (!datasetRows || datasetRows.length === 0) {
233→      throw new Error(
234→        `Dataset ${typedAnalysis.dataset_id} has no stored data (rows_json is empty). ` +
235→        "The dataset file may need to be re-uploaded."
236→      );
237→    }
238→
239→    const result = await runAnalysis(typedAnalysis, datasetRows);
240→
241→    // Update analysis with results
242→    await supabase
243→      .from("analyses")
244→      .update({
245→        status: "completed",
246→        results_json: result.results_json,
247→        figures_urls: result.figures_urls,
248→        r_script: result.r_script,
249→      })
250→      .eq("id", analysisId);
251→  } catch (err) {
252→    const errorMessage =
253→      err instanceof RPlumberError
254→        ? err.message
255→        : err instanceof Error
256→          ? err.message
257→          : "Unknown error";
258→
259→    await supabase
260→      .from("analyses")
261→      .update({
262→        status: "failed",
263→        results_json: { error: errorMessage },
264→      })
265→      .eq("id", analysisId);
266→
267→    throw err;
268→  }
269→}
