  1→import { execFile } from "child_process";
  2→import { promisify } from "util";
  3→import { readFile, writeFile, mkdir, rm, copyFile } from "fs/promises";
  4→import path from "path";
  5→import os from "os";
  6→import { parseLatexLog } from "./parse-log";
  7→import type { ParsedLog } from "./parse-log";
  8→
  9→const execFileAsync = promisify(execFile);
 10→
 11→export type CompileMode = "docker" | "local" | "mock";
 12→
 13→export interface CompileResult {
 14→  success: boolean;
 15→  pdfPath: string | null;
 16→  log: ParsedLog;
 17→  rawLog: string;
 18→  compileTimeMs: number;
 19→}
 20→
 21→function getCompileMode(): CompileMode {
 22→  const mode = process.env.LATEX_COMPILE_MODE;
 23→  if (mode === "docker" || mode === "local" || mode === "mock") return mode;
 24→  return "mock";
 25→}
 26→
 27→/**
 28→ * Compile LaTeX source into a PDF.
 29→ */
 30→export async function compileTex(
 31→  texContent: string,
 32→  options: {
 33→    projectId: string;
 34→    watermark?: boolean;
 35→    clsFiles?: string[];
 36→    bstFile?: string;
 37→    bibContent?: string;
 38→    chapterFiles?: Record<string, string>;
 39→    /** Paths to figure files (relative path → absolute source path) */
 40→    figureFiles?: Record<string, string>;
 41→  }
 42→): Promise<CompileResult> {
 43→  const mode = getCompileMode();
 44→
 45→  if (mode === "mock") {
 46→    return mockCompile(texContent);
 47→  }
 48→
 49→  if (mode === "docker") {
 50→    return dockerCompile(texContent, options);
 51→  }
 52→
 53→  return localCompile(texContent, options);
 54→}
 55→
 56→async function mockCompile(texContent: string): Promise<CompileResult> {
 57→  // For testing: validate basic TeX structure
 58→  const hasDocumentClass = /\\documentclass/.test(texContent);
 59→  const hasBeginDocument = /\\begin\{document\}/.test(texContent);
 60→  const hasEndDocument = /\\end\{document\}/.test(texContent);
 61→
 62→  const errors: string[] = [];
 63→  if (!hasDocumentClass) errors.push("Missing \\documentclass");
 64→  if (!hasBeginDocument) errors.push("Missing \\begin{document}");
 65→  if (!hasEndDocument) errors.push("Missing \\end{document}");
 66→
 67→  const success = errors.length === 0;
 68→
 69→  // In mock mode, write a minimal placeholder PDF so the compile route sees a pdfPath
 70→  let pdfPath: string | null = null;
 71→  if (success) {
 72→    const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
 73→    await mkdir(persistDir, { recursive: true });
 74→    pdfPath = path.join(persistDir, "mock.pdf");
 75→    // Minimal valid PDF (1-page blank)
 76→    const minimalPdf = `%PDF-1.0\n1 0 obj<</Pages 2 0 R>>endobj\n2 0 obj<</Kids[3 0 R]/Count 1>>endobj\n3 0 obj<</MediaBox[0 0 612 792]>>endobj\ntrailer<</Root 1 0 R>>\n`;
 77→    await writeFile(pdfPath, minimalPdf);
 78→  }
 79→
 80→  return {
 81→    success,
 82→    pdfPath,
 83→    log: { errors, warnings: [], errorCount: errors.length, warningCount: 0 },
 84→    rawLog: success ? "Mock compilation successful" : errors.join("\n"),
 85→    compileTimeMs: 1,
 86→  };
 87→}
 88→
 89→async function dockerCompile(
 90→  texContent: string,
 91→  options: {
 92→    projectId: string;
 93→    watermark?: boolean;
 94→    clsFiles?: string[];
 95→    bstFile?: string;
 96→    bibContent?: string;
 97→    chapterFiles?: Record<string, string>;
 98→    figureFiles?: Record<string, string>;
 99→  }
100→): Promise<CompileResult> {
101→  const start = Date.now();
102→  const workDir = path.join(os.tmpdir(), `apollo-compile-${options.projectId}`);
103→
104→  try {
105→    await mkdir(workDir, { recursive: true });
106→
107→    // Write tex content
108→    await writeFile(path.join(workDir, "main.tex"), texContent);
109→
110→    // Copy CLS and BST files from templates
111→    const templatesDir = path.resolve(process.cwd(), "../../templates");
112→    const clsFiles = options.clsFiles ?? ["sskm-thesis.cls", "ssuhs-thesis.cls"];
113→
114→    for (const cls of clsFiles) {
115→      try {
116→        const content = await readFile(path.join(templatesDir, cls));
117→        await writeFile(path.join(workDir, cls), content);
118→      } catch {
119→        // CLS file may not exist locally in all environments
120→      }
121→    }
122→
123→    const bstFile = options.bstFile ?? "vancouver.bst";
124→    try {
125→      const bstContent = await readFile(path.join(templatesDir, bstFile));
126→      await writeFile(path.join(workDir, bstFile), bstContent);
127→    } catch {
128→      // BST may not exist locally
129→    }
130→
131→    // Copy logo directory
132→    try {
133→      const logoDir = path.join(templatesDir, "logo");
134→      await mkdir(path.join(workDir, "logo"), { recursive: true });
135→      const { stdout: logoFiles } = await execFileAsync("ls", [logoDir]);
136→      for (const file of logoFiles.trim().split("\n").filter(Boolean)) {
137→        const content = await readFile(path.join(logoDir, file));
138→        await writeFile(path.join(workDir, "logo", file), content);
139→      }
140→    } catch {
141→      // Logo dir may not exist
142→    }
143→
144→    // Write references.bib (populated or empty)
145→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
146→
147→    // Write chapter files
148→    if (options.chapterFiles) {
149→      await mkdir(path.join(workDir, "chapters"), { recursive: true });
150→      for (const [filename, content] of Object.entries(options.chapterFiles)) {
151→        await writeFile(path.join(workDir, filename), content);
152→      }
153→    }
154→
155→    // Copy figure files (relative path → absolute source) into workDir
156→    if (options.figureFiles) {
157→      for (const [relPath, srcPath] of Object.entries(options.figureFiles)) {
158→        try {
159→          const destPath = path.join(workDir, relPath);
160→          await mkdir(path.dirname(destPath), { recursive: true });
161→          await copyFile(srcPath, destPath);
162→        } catch {
163→          // Figure file may have been deleted — non-fatal
164→        }
165→      }
166→    }
167→
168→    // Create output directory
169→    const outputDir = path.join(workDir, "output");
170→    await mkdir(outputDir, { recursive: true });
171→
172→    // Run Docker compile
173→    const containerName = process.env.LATEX_CONTAINER_NAME ?? "apollo-latex";
174→    const args = [
175→      "run",
176→      "--rm",
177→      "--network=none",
178→      "--read-only",
179→      "--tmpfs", "/tmp:rw,size=512m",
180→      "--memory=1g",
181→      "-v", `${workDir}:/thesis`,
182→      containerName,
183→    ];
184→
185→    if (options.watermark) {
186→      args.push("--watermark");
187→    }
188→
189→    const { stdout, stderr } = await execFileAsync("docker", args, {
190→      timeout: 120_000,
191→    });
192→
193→    // Persist PDF to a stable directory (temp workDir is cleaned up in finally)
194→    let pdfPath: string | null = null;
195→    try {
196→      const pdfData = await readFile(path.join(outputDir, "main.pdf"));
197→      const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
198→      await mkdir(persistDir, { recursive: true });
199→      pdfPath = path.join(persistDir, `${options.projectId}.pdf`);
200→      await writeFile(pdfPath, pdfData);
201→    } catch {
202→      // PDF may not have been generated
203→    }
204→
205→    let rawLog = "";
206→    try {
207→      rawLog = await readFile(path.join(outputDir, "main.log"), "utf-8");
208→    } catch {
209→      rawLog = stdout + stderr;
210→    }
211→
212→    const log = parseLatexLog(rawLog);
213→
214→    return {
215→      success: log.errorCount === 0 && pdfPath !== null,
216→      pdfPath,
217→      log,
218→      rawLog,
219→      compileTimeMs: Date.now() - start,
220→    };
221→  } catch (err: unknown) {
222→    // execFileAsync includes stdout/stderr on the error object
223→    const execErr = err as { message?: string; stdout?: string; stderr?: string };
224→    const fullOutput = [execErr.stderr, execErr.stdout, execErr.message]
225→      .filter(Boolean)
226→      .join("\n");
227→    const log = parseLatexLog(fullOutput);
228→
229→    // If parseLatexLog found no errors, use the raw error message
230→    if (log.errorCount === 0) {
231→      log.errors.push(execErr.message ?? "Docker compilation failed");
232→      log.errorCount = 1;
233→    }
234→
235→    return {
236→      success: false,
237→      pdfPath: null,
238→      log,
239→      rawLog: fullOutput,
240→      compileTimeMs: Date.now() - start,
241→    };
242→  } finally {
243→    // Cleanup
244→    try {
245→      await rm(workDir, { recursive: true, force: true });
246→    } catch {
247→      // Best effort cleanup
248→    }
249→  }
250→}
251→
252→async function localCompile(
253→  texContent: string,
254→  options: {
255→    projectId: string;
256→    watermark?: boolean;
257→    bibContent?: string;
258→    chapterFiles?: Record<string, string>;
259→    figureFiles?: Record<string, string>;
260→  }
261→): Promise<CompileResult> {
262→  const start = Date.now();
263→  const workDir = path.join(os.tmpdir(), `apollo-compile-${options.projectId}`);
264→
265→  try {
266→    await mkdir(workDir, { recursive: true });
267→    await writeFile(path.join(workDir, "main.tex"), texContent);
268→
269→    // Copy templates
270→    const templatesDir = path.resolve(process.cwd(), "../../templates");
271→    for (const file of ["sskm-thesis.cls", "ssuhs-thesis.cls", "vancouver.bst"]) {
272→      try {
273→        const content = await readFile(path.join(templatesDir, file));
274→        await writeFile(path.join(workDir, file), content);
275→      } catch {
276→        // May not exist
277→      }
278→    }
279→
280→    // Copy logos
281→    try {
282→      await mkdir(path.join(workDir, "logo"), { recursive: true });
283→      const { stdout } = await execFileAsync("ls", [path.join(templatesDir, "logo")]);
284→      for (const file of stdout.trim().split("\n").filter(Boolean)) {
285→        const content = await readFile(path.join(templatesDir, "logo", file));
286→        await writeFile(path.join(workDir, "logo", file), content);
287→      }
288→    } catch {
289→      // May not exist
290→    }
291→
292→    // Write references.bib (populated or empty)
293→    await writeFile(path.join(workDir, "references.bib"), options.bibContent ?? "");
294→
295→    // Write chapter files
296→    if (options.chapterFiles) {
297→      await mkdir(path.join(workDir, "chapters"), { recursive: true });
298→      for (const [filename, content] of Object.entries(options.chapterFiles)) {
299→        await writeFile(path.join(workDir, filename), content);
300→      }
301→    }
302→
303→    // Copy figure files (relative path → absolute source) into workDir
304→    if (options.figureFiles) {
305→      for (const [relPath, srcPath] of Object.entries(options.figureFiles)) {
306→        try {
307→          const destPath = path.join(workDir, relPath);
308→          await mkdir(path.dirname(destPath), { recursive: true });
309→          await copyFile(srcPath, destPath);
310→        } catch {
311→          // Figure file may have been deleted — non-fatal
312→        }
313→      }
314→    }
315→
316→    // Run pdflatex → bibtex → pdflatex × 2
317→    const pdflatexArgs = ["-interaction=nonstopmode", "-output-directory", workDir, path.join(workDir, "main.tex")];
318→
319→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
320→    await execFileAsync("bibtex", [path.join(workDir, "main")], { timeout: 30_000, cwd: workDir }).catch(() => {});
321→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
322→    await execFileAsync("pdflatex", pdflatexArgs, { timeout: 120_000, cwd: workDir }).catch(() => {});
323→
324→    // Persist PDF to a stable directory
325→    let pdfPath: string | null = null;
326→    try {
327→      const pdfData = await readFile(path.join(workDir, "main.pdf"));
328→      const persistDir = path.join(os.tmpdir(), "apollo-pdfs");
329→      await mkdir(persistDir, { recursive: true });
330→      pdfPath = path.join(persistDir, `${options.projectId}.pdf`);
331→      await writeFile(pdfPath, pdfData);
332→    } catch {
333→      // PDF may not have been generated
334→    }
335→
336→    let rawLog = "";
337→    try {
338→      rawLog = await readFile(path.join(workDir, "main.log"), "utf-8");
339→    } catch {
340→      rawLog = "No log file generated";
341→    }
342→
343→    const log = parseLatexLog(rawLog);
344→
345→    return {
346→      success: log.errorCount === 0 && pdfPath !== null,
347→      pdfPath,
348→      log,
349→      rawLog,
350→      compileTimeMs: Date.now() - start,
351→    };
352→  } catch (err) {
353→    return {
354→      success: false,
355→      pdfPath: null,
356→      log: {
357→        errors: [err instanceof Error ? err.message : "Local compilation failed"],
358→        warnings: [],
359→        errorCount: 1,
360→        warningCount: 0,
361→      },
362→      rawLog: err instanceof Error ? err.message : "Unknown error",
363→      compileTimeMs: Date.now() - start,
364→    };
365→  } finally {
366→    // Cleanup work directory (PDF already persisted)
367→    try {
368→      await rm(workDir, { recursive: true, force: true });
369→    } catch {
370→      // Best effort cleanup
371→    }
372→  }
373→}
