 1→/**
 2→ * Word count targets per thesis phase (GOLD Standard methodology).
 3→ *
 4→ * Phases without a meaningful prose target (orientation, front matter,
 5→ * references, appendices, final QC) return `null`.
 6→ */
 7→
 8→export interface WordCountTarget {
 9→  min: number;
10→  max: number;
11→}
12→
13→export type WordCountStatus = "under" | "on-target" | "over" | "no-target";
14→
15→const WORD_COUNT_TARGETS: Record<number, WordCountTarget> = {
16→  2: { min: 750, max: 1200 },   // introduction
17→  3: { min: 200, max: 500 },    // aims
18→  4: { min: 3500, max: 4500 },  // review_of_literature
19→  5: { min: 1500, max: 3000 },  // materials_methods
20→  6: { min: 1500, max: 3000 },  // results
21→  7: { min: 2000, max: 3500 },  // discussion
22→  8: { min: 400, max: 800 },    // conclusion
23→};
24→
25→/**
26→ * Return the target word-count range for the given phase, or `null` if the
27→ * phase has no prose target.
28→ */
29→export function getWordCountTarget(phaseNumber: number): WordCountTarget | null {
30→  return WORD_COUNT_TARGETS[phaseNumber] ?? null;
31→}
32→
33→/**
34→ * Determine whether the current word count is under, on-target, or over the
35→ * expected range for the given phase.  Returns `"no-target"` for phases that
36→ * do not have a word-count target.
37→ */
38→export function getWordCountStatus(
39→  phaseNumber: number,
40→  wordCount: number,
41→): WordCountStatus {
42→  const target = getWordCountTarget(phaseNumber);
43→
44→  if (!target) {
45→    return "no-target";
46→  }
47→
48→  if (wordCount < target.min) {
49→    return "under";
50→  }
51→
52→  if (wordCount > target.max) {
53→    return "over";
54→  }
55→
56→  return "on-target";
57→}
