  1→/**
  2→ * Final Quality Control — Phase 11.
  3→ *
  4→ * Orchestrates all quality checks and returns a structured QCReport.
  5→ * Per PLAN.md §647-658 and §799-802.
  6→ */
  7→
  8→import type { Section, Citation } from "@/lib/types/database";
  9→import { auditCitations } from "@/lib/citations/audit";
 10→
 11→// ── Types ──────────────────────────────────────────────────────────────────
 12→
 13→export interface QCDetail {
 14→  item: string;
 15→  message: string;
 16→}
 17→
 18→export interface QCCheck {
 19→  name: string;
 20→  status: "pass" | "warn" | "fail";
 21→  blocking: boolean;
 22→  message: string;
 23→  details: QCDetail[];
 24→  fixAction?: string;
 25→}
 26→
 27→export interface QCReport {
 28→  checks: QCCheck[];
 29→  overallPass: boolean;
 30→  blockingCount: number;
 31→  warningCount: number;
 32→}
 33→
 34→// ── American → British spelling dictionary ─────────────────────────────────
 35→
 36→const AMERICAN_TO_BRITISH: [RegExp, string][] = [
 37→  [/\banalyze\b/gi, "analyse"],
 38→  [/\banalyzed\b/gi, "analysed"],
 39→  [/\banalyzing\b/gi, "analysing"],
 40→  [/\bbehavior\b/gi, "behaviour"],
 41→  [/\bcolor\b/gi, "colour"],
 42→  [/\bcenter\b/gi, "centre"],
 43→  [/\brandomized\b/gi, "randomised"],
 44→  [/\borganize\b/gi, "organise"],
 45→  [/\borganized\b/gi, "organised"],
 46→  [/\brecognize\b/gi, "recognise"],
 47→  [/\brecognized\b/gi, "recognised"],
 48→  [/\bspecialized\b/gi, "specialised"],
 49→  [/\bfavor\b/gi, "favour"],
 50→  [/\bhonor\b/gi, "honour"],
 51→  [/\blabor\b/gi, "labour"],
 52→  [/\btumor\b/gi, "tumour"],
 53→  [/\bfetus\b/gi, "foetus"],
 54→  [/\bpediatric\b/gi, "paediatric"],
 55→  [/\banesthesia\b/gi, "anaesthesia"],
 56→  [/\bhemoglobin\b/gi, "haemoglobin"],
 57→  [/\besophagus\b/gi, "oesophagus"],
 58→];
 59→
 60→// ── Word count targets per phase ───────────────────────────────────────────
 61→
 62→const WORD_COUNT_TARGETS: Record<number, { min: number; max: number }> = {
 63→  2: { min: 500, max: 750 },
 64→  3: { min: 150, max: 200 },
 65→  4: { min: 2500, max: 3500 },
 66→  5: { min: 1500, max: 2500 },
 67→  6: { min: 1500, max: 2500 },
 68→  7: { min: 2000, max: 2500 },
 69→  8: { min: 500, max: 750 },
 70→};
 71→
 72→// ── Helpers ────────────────────────────────────────────────────────────────
 73→
 74→function countWords(latex: string): number {
 75→  const plain = latex
 76→    .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
 77→    .replace(/\\[a-zA-Z]+/g, " ")
 78→    .replace(/[{}\\]/g, " ");
 79→  return plain.split(/\s+/).filter(Boolean).length;
 80→}
 81→
 82→function stripLatexCommands(latex: string): string {
 83→  return latex
 84→    .replace(/\\[a-zA-Z]+\{[^}]*\}/g, " ")
 85→    .replace(/\\[a-zA-Z]+/g, " ");
 86→}
 87→
 88→// ── Check implementations ──────────────────────────────────────────────────
 89→
 90→function checkCitationProvenance(
 91→  sections: Section[],
 92→  citations: Citation[]
 93→): QCCheck {
 94→  const audit = auditCitations(sections, citations);
 95→  const tierDCount = audit.tierDBlocking.length;
 96→
 97→  if (tierDCount === 0) {
 98→    return {
 99→      name: "citation-provenance",
100→      status: "pass",
101→      blocking: true,
102→      message: `All citations verified (integrity score: ${audit.integrityScore}%)`,
103→      details: [],
104→    };
105→  }
106→
107→  return {
108→    name: "citation-provenance",
109→    status: "fail",
110→    blocking: true,
111→    message: `${tierDCount} Tier D citation(s) remain unverified`,
112→    details: audit.tierDBlocking.map((d) => ({
113→      item: d.citeKey,
114→      message: `Used in phase(s): ${d.usedInPhases.join(", ")}`,
115→    })),
116→    fixAction: "re-resolve-citations",
117→  };
118→}
119→
120→function checkSectionCompleteness(sections: Section[]): QCCheck {
121→  const details: QCDetail[] = [];
122→  const contentPhases = [2, 3, 4, 5, 6, 7, 8]; // Phases with word count targets
123→
124→  for (const phase of contentPhases) {
125→    const section = sections.find((s) => s.phase_number === phase);
126→    if (!section || !section.latex_content) {
127→      details.push({
128→        item: `Phase ${phase}`,
129→        message: "Section missing or empty",
130→      });
131→      continue;
132→    }
133→
134→    const target = WORD_COUNT_TARGETS[phase];
135→    if (!target) continue;
136→
137→    const words = countWords(section.latex_content);
138→    if (words < target.min) {
139→      details.push({
140→        item: `Phase ${phase}`,
141→        message: `Word count (${words}) below minimum (${target.min})`,
142→      });
143→    }
144→  }
145→
146→  if (details.length === 0) {
147→    return {
148→      name: "section-completeness",
149→      status: "pass",
150→      blocking: false,
151→      message: "All sections meet word count targets",
152→      details: [],
153→    };
154→  }
155→
156→  return {
157→    name: "section-completeness",
158→    status: "warn",
159→    blocking: false,
160→    message: `${details.length} section(s) below word count target`,
161→    details,
162→    fixAction: "expand-section",
163→  };
164→}
165→
166→function checkBritishEnglish(sections: Section[]): QCCheck {
167→  const details: QCDetail[] = [];
168→
169→  for (const section of sections) {
170→    if (!section.latex_content || section.phase_number < 2 || section.phase_number > 8) continue;
171→    const prose = stripLatexCommands(section.latex_content);
172→
173→    for (const [pattern, british] of AMERICAN_TO_BRITISH) {
174→      const match = pattern.exec(prose);
175→      if (match) {
176→        details.push({
177→          item: `Phase ${section.phase_number}`,
178→          message: `"${match[0]}" → "${british}"`,
179→        });
180→        pattern.lastIndex = 0;
181→      }
182→    }
183→  }
184→
185→  if (details.length === 0) {
186→    return {
187→      name: "british-english",
188→      status: "pass",
189→      blocking: false,
190→      message: "No American English spellings detected",
191→      details: [],
192→    };
193→  }
194→
195→  return {
196→    name: "british-english",
197→    status: "warn",
198→    blocking: false,
199→    message: `${details.length} American English spelling(s) found`,
200→    details,
201→    fixAction: "auto-fix-spelling",
202→  };
203→}
204→
205→function checkNBEMSCompliance(sections: Section[]): QCCheck {
206→  const details: QCDetail[] = [];
207→
208→  // 80-page limit: phases 2-8 only (~250 words/page)
209→  let totalWords = 0;
210→  for (const section of sections) {
211→    if (section.phase_number >= 2 && section.phase_number <= 8 && section.latex_content) {
212→      totalWords += countWords(section.latex_content);
213→    }
214→  }
215→  const estimatedPages = Math.ceil(totalWords / 250);
216→  if (estimatedPages > 80) {
217→    details.push({
218→      item: "Page count",
219→      message: `Estimated ${estimatedPages} pages (phases 2-8) — exceeds 80-page NBEMS limit`,
220→    });
221→  }
222→
223→  // 300-word abstract limit
224→  const phase1 = sections.find((s) => s.phase_number === 1);
225→  if (phase1?.latex_content) {
226→    const abstractMatch = phase1.latex_content.match(
227→      /\\section\*?\{Abstract\}([\s\S]*?)(?=\\section|$)/i
228→    );
229→    if (abstractMatch) {
230→      const abstractWords = countWords(abstractMatch[1]);
231→      if (abstractWords > 300) {
232→        details.push({
233→          item: "Abstract",
234→          message: `Abstract is ${abstractWords} words (max 300)`,
235→        });
236→      }
237→    }
238→  }
239→
240→  // M&M must have ≥12 sections
241→  const phase5 = sections.find((s) => s.phase_number === 5);
242→  if (phase5?.latex_content) {
243→    const sectionCount = (
244→      phase5.latex_content.match(/\\(section|subsection|subsubsection)\*?\{/g) ?? []
245→    ).length;
246→    if (sectionCount < 12) {
247→      details.push({
248→        item: "Materials & Methods",
249→        message: `Only ${sectionCount} section headings (NBEMS requires 12)`,
250→      });
251→    }
252→  }
253→
254→  const blocking = details.some(
255→    (d) => d.item === "Page count" || d.item === "Materials & Methods"
256→  );
257→
258→  if (details.length === 0) {
259→    return {
260→      name: "nbems-compliance",
261→      status: "pass",
262→      blocking: true,
263→      message: "NBEMS compliance checks passed",
264→      details: [],
265→    };
266→  }
267→
268→  return {
269→    name: "nbems-compliance",
270→    status: blocking ? "fail" : "warn",
271→    blocking,
272→    message: `${details.length} NBEMS compliance issue(s)`,
273→    details,
274→  };
275→}
276→
277→function checkUndefinedReferences(
278→  compileLog: string | null
279→): QCCheck {
280→  if (!compileLog) {
281→    return {
282→      name: "undefined-references",
283→      status: "pass",
284→      blocking: true,
285→      message: "No compile log available — compile first",
286→      details: [],
287→    };
288→  }
289→
290→  const undefinedRefs = compileLog.match(/LaTeX Warning: Reference `([^']+)' on page/g) ?? [];
291→  const undefinedCites = compileLog.match(/LaTeX Warning: Citation `([^']+)' on page/g) ?? [];
292→
293→  const details: QCDetail[] = [
294→    ...undefinedRefs.map((m) => ({
295→      item: "Undefined ref",
296→      message: m,
297→    })),
298→    ...undefinedCites.map((m) => ({
299→      item: "Undefined cite",
300→      message: m,
301→    })),
302→  ];
303→
304→  if (details.length === 0) {
305→    return {
306→      name: "undefined-references",
307→      status: "pass",
308→      blocking: true,
309→      message: "No undefined references or citations",
310→      details: [],
311→    };
312→  }
313→
314→  return {
315→    name: "undefined-references",
316→    status: "fail",
317→    blocking: true,
318→    message: `${details.length} undefined reference(s) or citation(s)`,
319→    details,
320→  };
321→}
322→
323→// ── Public API ─────────────────────────────────────────────────────────────
324→
325→export function finalQC(
326→  sections: Section[],
327→  citations: Citation[],
328→  compileLog: string | null = null
329→): QCReport {
330→  const checks: QCCheck[] = [
331→    checkCitationProvenance(sections, citations),
332→    checkSectionCompleteness(sections),
333→    checkBritishEnglish(sections),
334→    checkNBEMSCompliance(sections),
335→    checkUndefinedReferences(compileLog),
336→  ];
337→
338→  const blockingCount = checks.filter(
339→    (c) => c.blocking && c.status === "fail"
340→  ).length;
341→  const warningCount = checks.filter((c) => c.status === "warn").length;
342→
343→  return {
344→    checks,
345→    overallPass: blockingCount === 0,
346→    blockingCount,
347→    warningCount,
348→  };
349→}
