  1→import { NextRequest, NextResponse } from "next/server";
  2→import { getAuthenticatedUser } from "@/lib/api/auth";
  3→import {
  4→  unauthorised,
  5→  notFound,
  6→  validationError,
  7→  internalError,
  8→} from "@/lib/api/errors";
  9→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 10→import { getAnthropicClient } from "@/lib/ai/client";
 11→import {
 12→  autoDetectSchema,
 13→  analysisTypes,
 14→  REQUIRED_PARAMS,
 15→} from "@/lib/validation/analysis-schemas";
 16→import type { AnalysisRecommendation } from "@/lib/validation/analysis-schemas";
 17→
 18→const PARAM_KEYS = ["outcome", "predictor", "group", "time", "event"] as const;
 19→
 20→/** Normalise AI-generated parameters: arrays → first element, non-strings → drop */
 21→function normaliseParameters(
 22→  raw: Record<string, unknown> | undefined
 23→): AnalysisRecommendation["parameters"] {
 24→  if (!raw || typeof raw !== "object") return {};
 25→  const result: Record<string, string> = {};
 26→  for (const key of PARAM_KEYS) {
 27→    const val = raw[key];
 28→    if (typeof val === "string" && val.length > 0) {
 29→      result[key] = val;
 30→    } else if (Array.isArray(val) && val.length > 0 && typeof val[0] === "string") {
 31→      result[key] = val[0];
 32→    }
 33→  }
 34→  return result;
 35→}
 36→
 37→// ── POST /api/projects/:id/analyses/auto-detect ─────────────────────────────
 38→// Accepts { dataset_id }, returns up to 5 AI-recommended analyses
 39→
 40→export async function POST(
 41→  request: NextRequest,
 42→  { params }: { params: Promise<{ id: string }> }
 43→) {
 44→  try {
 45→    const authResult = await getAuthenticatedUser();
 46→    if (!authResult) return unauthorised();
 47→
 48→    const { id } = await params;
 49→    const supabase = createAdminSupabaseClient();
 50→
 51→    // Verify project ownership
 52→    const { data: project } = await supabase
 53→      .from("projects")
 54→      .select("id, study_type, title")
 55→      .eq("id", id)
 56→      .eq("user_id", authResult.user.id)
 57→      .single();
 58→
 59→    if (!project) return notFound("Project not found");
 60→
 61→    // Validate body
 62→    const body = await request.json();
 63→    const parsed = autoDetectSchema.safeParse(body);
 64→    if (!parsed.success) {
 65→      return validationError("Invalid parameters", {
 66→        issues: parsed.error.issues,
 67→      });
 68→    }
 69→
 70→    // Fetch dataset — include rows_json to verify data is available
 71→    const { data: dataset } = await supabase
 72→      .from("datasets")
 73→      .select("id, columns_json, row_count, rows_json, file_url")
 74→      .eq("id", parsed.data.dataset_id)
 75→      .eq("project_id", id)
 76→      .single();
 77→
 78→    if (!dataset) return notFound("Dataset not found");
 79→
 80→    // Pre-flight: verify the dataset actually has stored row data
 81→    const hasRows = Array.isArray(dataset.rows_json) && (dataset.rows_json as unknown[]).length > 0;
 82→    if (!hasRows) {
 83→      return validationError(
 84→        "Dataset has no stored row data. Please delete this dataset and generate or upload a new one."
 85→      );
 86→    }
 87→
 88→    // Fetch Phase 0 synopsis section for context
 89→    const { data: synopsisSection } = await supabase
 90→      .from("sections")
 91→      .select("latex_content")
 92→      .eq("project_id", id)
 93→      .eq("phase_number", 0)
 94→      .maybeSingle();
 95→
 96→    const columns = (dataset.columns_json ?? []) as { name: string; type: string }[];
 97→    const columnsDescription = columns
 98→      .map((c) => `${c.name} (${c.type})`)
 99→      .join(", ");
100→
101→    const prompt = `You are a biostatistics expert helping a medical thesis student choose statistical analyses.
102→
103→Study type: ${project.study_type ?? "unknown"}
104→Dataset: ${dataset.row_count} rows, columns: ${columnsDescription}
105→${synopsisSection?.latex_content ? `Synopsis context: ${synopsisSection.latex_content.slice(0, 1500)}` : ""}
106→
107→Available analysis types: ${analysisTypes.join(", ")}
108→
109→Recommend up to 5 analyses from the available types. For each, provide:
110→- analysis_type: exactly one of the available types
111→- rationale: 1-2 sentences explaining why this analysis suits the data
112→- parameters: map dataset columns to roles (outcome, predictor, group, time, event) — use exact column names
113→- confidence: "high", "medium", or "low"
114→- suggested_figures: array of 1-3 objects with { chart_type, description } — recommend appropriate visualisations. Available chart_type values: bar, box, scatter, line, forest, kaplan-meier, heatmap, violin
115→
116→Order by confidence (highest first). Only recommend analyses that genuinely suit the data columns and study type.
117→
118→Respond with ONLY a JSON array of recommendations, no markdown fences or other text.`;
119→
120→    const anthropic = getAnthropicClient();
121→    const msg = await anthropic.messages.create({
122→      model: "claude-haiku-4-5-20251001",
123→      max_tokens: 1500,
124→      messages: [{ role: "user", content: prompt }],
125→    });
126→
127→    const text =
128→      msg.content[0].type === "text" ? msg.content[0].text.trim() : "";
129→
130→    let recommendations: AnalysisRecommendation[] = [];
131→    try {
132→      // Strip markdown fences if AI wrapped output in ```json ... ```
133→      const cleaned = text
134→        .replace(/```json?\s*\n?/gi, "")
135→        .replace(/```\s*$/gm, "")
136→        .trim();
137→
138→      let parsed: unknown;
139→      try {
140→        parsed = JSON.parse(cleaned);
141→      } catch {
142→        // Fallback: try extracting a JSON array from the text
143→        const arrayMatch = cleaned.match(/\[[\s\S]*\]/);
144→        if (arrayMatch) {
145→          parsed = JSON.parse(arrayMatch[0]);
146→        }
147→      }
148→
149→      if (Array.isArray(parsed)) {
150→        const columnNames = columns.map((c) => c.name);
151→
152→        recommendations = parsed
153→          .filter(
154→            (r: Record<string, unknown>) =>
155→              typeof r.analysis_type === "string" &&
156→              analysisTypes.includes(r.analysis_type as (typeof analysisTypes)[number])
157→          )
158→          .map((r: Record<string, unknown>) => ({
159→            analysis_type: r.analysis_type as AnalysisRecommendation["analysis_type"],
160→            rationale: typeof r.rationale === "string" ? r.rationale : "",
161→            confidence: (["high", "medium", "low"].includes(r.confidence as string)
162→              ? r.confidence
163→              : "medium") as AnalysisRecommendation["confidence"],
164→            // Normalise parameters: AI may return arrays — take first element
165→            parameters: normaliseParameters(
166→              r.parameters as Record<string, unknown> | undefined
167→            ),
168→          }))
169→          // Drop recommendations missing required parameters
170→          .filter((rec) => {
171→            const required = REQUIRED_PARAMS[rec.analysis_type] ?? [];
172→            const params = rec.parameters as Record<string, string | undefined>;
173→            for (const key of required) {
174→              const val = params[key];
175→              if (!val || val.trim() === "") return false;
176→              // Also verify the column actually exists in the dataset
177→              if (!columnNames.includes(val)) return false;
178→            }
179→            return true;
180→          })
181→          .slice(0, 5);
182→      }
183→    } catch {
184→      // All parsing failed — return empty recommendations instead of 500
185→      console.warn("Failed to parse AI recommendations, returning empty:", text.slice(0, 200));
186→    }
187→
188→    return NextResponse.json({ data: recommendations });
189→  } catch (err) {
190→    console.error("Auto-detect error:", err);
191→
192→    // Provide more specific error responses
193→    if (err instanceof Error) {
194→      if (err.message.includes("timeout") || err.message.includes("ETIMEDOUT")) {
195→        return NextResponse.json(
196→          { error: { code: "GATEWAY_TIMEOUT", message: "AI recommendation timed out — please try again" } },
197→          { status: 504 }
198→        );
199→      }
200→      if (err.message.includes("API") || err.message.includes("rate")) {
201→        return NextResponse.json(
202→          { error: { code: "BAD_GATEWAY", message: "AI service temporarily unavailable" } },
203→          { status: 502 }
204→        );
205→      }
206→    }
207→
208→    return internalError();
209→  }
210→}
