 1→import type { Project, SectionStatus } from "@/lib/types/database";
 2→import { getPhase, MAX_PHASE } from "./constants";
 3→
 4→export interface TransitionCheck {
 5→  allowed: boolean;
 6→  reason?: string;
 7→  code?: "LICENCE_REQUIRED" | "INVALID_TRANSITION" | "SECTION_NOT_APPROVED";
 8→}
 9→
10→/**
11→ * Check whether a project can advance from its current phase to the next.
12→ */
13→export function canAdvancePhase(
14→  project: Project,
15→  currentSectionStatus: SectionStatus | null
16→): TransitionCheck {
17→  const { current_phase, status } = project;
18→
19→  // Cannot go beyond the final phase (but final phase itself CAN be approved)
20→  if (current_phase > MAX_PHASE) {
21→    return {
22→      allowed: false,
23→      reason: "Already beyond the final phase",
24→      code: "INVALID_TRANSITION",
25→    };
26→  }
27→
28→  // Current section must be approved before advancing
29→  if (currentSectionStatus !== "approved") {
30→    return {
31→      allowed: false,
32→      reason: `Phase ${current_phase} section must be approved before advancing`,
33→      code: "SECTION_NOT_APPROVED",
34→    };
35→  }
36→
37→  // Final phase — no next phase to check licence for, just allow completion
38→  if (current_phase === MAX_PHASE) {
39→    return { allowed: true };
40→  }
41→
42→  // Check if next phase requires a licence
43→  const nextPhase = getPhase(current_phase + 1);
44→  const devBypass = process.env.NODE_ENV !== "production" && process.env.DEV_LICENCE_BYPASS === "true";
45→  if (
46→    nextPhase?.requiresLicence &&
47→    status !== "licensed" &&
48→    status !== "completed" &&
49→    !devBypass
50→  ) {
51→    return {
52→      allowed: false,
53→      reason: "Active licence required to advance beyond Phase 1",
54→      code: "LICENCE_REQUIRED",
55→    };
56→  }
57→
58→  return { allowed: true };
59→}
60→
61→/**
62→ * Check if a phase number is valid.
63→ */
64→export function isValidPhase(phase: number): boolean {
65→  return Number.isInteger(phase) && phase >= 0 && phase <= MAX_PHASE;
66→}
