 71→      // Create compilation record
 72→      const { data: compilation, error: compilationError } = await supabase
 73→        .from("compilations")
 74→        .insert({
 75→          project_id: id,
 76→          trigger: "manual",
 77→          status: "running",
 78→          warnings: [],
 79→          errors: [],
 80→        })
 81→        .select("*")
 82→        .single();
 83→
 84→      if (compilationError || !compilation) {
 85→        console.error("Failed to create compilation record:", compilationError);
 86→        return internalError("Failed to start compilation");
 87→      }
 88→
 89→      // Read the template
 90→      let template: string;
 91→      try {
 92→        const templatesDir = path.resolve(process.cwd(), "../../templates");
 93→        template = await readFile(path.join(templatesDir, "main.tex"), "utf-8");
 94→      } catch {
 95→        await updateCompilation(supabase, compilation.id, {
 96→          status: "failed",
 97→          errors: ["Template file not found"],
 98→        });
 99→        return internalError("LaTeX template not found");
100→      }
101→
102→      // Fetch sections, citations, and figures for assembly
103→      const [sectionsResult, citationsResult, figuresResult] = await Promise.all([
104→        supabase
105→          .from("sections")
106→          .select("*")
107→          .eq("project_id", id)
108→          .in("status", ["approved", "review"]),
109→        supabase
110→          .from("citations")
111→          .select("*")
112→          .eq("project_id", id),
113→        supabase
114→          .from("figures")
115→          .select("*")
116→          .eq("project_id", id),
117→      ]);
118→
119→      const sections = (sectionsResult.data ?? []) as Section[];
120→      const citations = (citationsResult.data ?? []) as Citation[];
121→      const figures = (figuresResult.data ?? []) as Figure[];
122→
123→      // Resolve figure files: map relative path → absolute disk path (only if file exists)
124→      const figureFiles: Record<string, string> = {};
125→      for (const fig of figures) {
126→        if (!fig.file_url) continue;
127→        // file_url is "figures/{project_id}/{analysis_id}/{filename}"
128→        // Disk path is FIGURES_BASE_DIR/{project_id}/{analysis_id}/{filename}
129→        const relAfterFigures = fig.file_url.replace(/^figures\//, "");
130→        const diskPath = path.join(FIGURES_BASE_DIR, relAfterFigures);
131→        try {
132→          await stat(diskPath);
133→          figureFiles[fig.file_url] = diskPath;
134→        } catch {
135→          // File doesn't exist on disk — will cause a non-fatal warning during compile
136→        }
137→      }
138→
139→      // Assemble thesis content (metadata + chapter files + BibTeX)
140→      const { tex, bib, chapterFiles, warnings: texWarnings } = assembleThesisContent(
141→        template,
142→        typedProject,
143→        sections,
144→        citations
145→      );
146→
147→      // Pre-flight validation on each chapter
148→      const preflightIssues = Object.entries(chapterFiles).flatMap(
149→        ([filename, content]) => preflightChapter(filename, content)
150→      );
151→
152→      const preflightErrors = preflightIssues.filter((i) => i.severity === "error");
153→      const preflightWarnings = preflightIssues.filter((i) => i.severity === "warning");
154→
155→      if (preflightErrors.length > 0) {
156→        await updateCompilation(supabase, compilation.id, {
157→          status: "failed",
158→          errors: preflightErrors.map(
159→            (i) => `${i.chapter}${i.line ? `:${i.line}` : ""}: ${i.message}`
160→          ),
161→          warnings: preflightWarnings.map(
162→            (i) => `${i.chapter}${i.line ? `:${i.line}` : ""}: ${i.message}`
163→          ),
164→        });
165→
166→        return NextResponse.json(
167→          {
168→            data: {
169→              compilation_id: compilation.id,
170→              status: "validation_failed",
171→              issues: preflightIssues,
172→              action: "Fix the issues in the section editor and retry",
173→            },
174→          },
175→          { status: 422 }
176→        );
177→      }
178→
179→      // AI validation (non-blocking — runs in parallel with compile setup)
180→      const aiValidationPromise = aiValidateChapters(chapterFiles);
181→
182→      // Compile
183→      const isWatermark = typedProject.status === "sandbox";
184→      const result = await Sentry.startSpan(
185→        {
186→          name: "latex.compile",
187→          op: "compile.latex",
188→          attributes: {
189→            "project.id": id,
190→            "compile.watermark": isWatermark,
191→            "compile.chapters": Object.keys(chapterFiles).length,
192→          },
193→        },
194→        () =>
195→          compileTex(tex, {
196→            projectId: id,
197→            watermark: isWatermark,
198→            bibContent: bib,
199→            chapterFiles,
200→            figureFiles,
201→          })
202→      );
203→
204→      // Collect all warnings
205→      const aiIssues = await aiValidationPromise;
206→      const aiWarnings = aiIssues.map(
207→        (i) => `[AI] ${i.chapter}: ${i.message}`
208→      );
209→      const allWarnings = [
210→        ...texWarnings,
211→        ...preflightWarnings.map(
212→          (i) => `${i.chapter}${i.line ? `:${i.line}` : ""}: ${i.message}`
213→        ),
214→        ...aiWarnings,
215→        ...result.log.warnings.map(String),
216→      ];
217→
218→      if (result.success && result.pdfPath) {
219→        await updateCompilation(supabase, compilation.id, {
220→          status: "completed",
221→          warnings: allWarnings,
222→          errors: [],
223→          pdf_url: result.pdfPath,
224→          log_text: result.rawLog.slice(0, 50000),
225→          compile_time_ms: result.compileTimeMs,
226→        });
227→
228→        return NextResponse.json({
229→          data: {
230→            compilation_id: compilation.id,
231→            status: "completed",
232→            warnings: allWarnings,
233→            errors: [],
234→            compile_time_ms: result.compileTimeMs,
235→          },
236→        });
237→      }
238→
239→      // Compilation failed
240→      await updateCompilation(supabase, compilation.id, {
241→        status: "failed",
242→        warnings: allWarnings,
243→        errors: result.log.errors,
244→        log_text: result.rawLog.slice(0, 50000),
245→        compile_time_ms: result.compileTimeMs,
246→      });
247→
248→      return NextResponse.json(
249→        {
250→          data: {
251→            compilation_id: compilation.id,
252→            status: "failed",
253→            warnings: allWarnings,
254→            errors: result.log.errors,
255→            compile_time_ms: result.compileTimeMs,
256→          },
257→        },
258→        { status: 422 }
259→      );
260→    } finally {
261→      release(slot.jobId!);
262→    }
263→  } catch (err) {
264→    console.error("Unexpected error in POST /api/projects/[id]/compile:", err);
265→    return internalError();
266→  }
267→}
