  1→import { describe, it, expect, beforeEach } from "vitest";
  2→import {
  3→  tryAcquire,
  4→  release,
  5→  getStatus,
  6→  _resetForTesting,
  7→} from "./semaphore";
  8→
  9→describe("compute semaphore", () => {
 10→  beforeEach(() => {
 11→    _resetForTesting();
 12→  });
 13→
 14→  it("can acquire a compile job (2 units)", () => {
 15→    const result = tryAcquire("compile", "project-1", "user-1");
 16→    expect(result.acquired).toBe(true);
 17→    expect(result.jobId).toBeDefined();
 18→    expect(getStatus().usedUnits).toBe(2);
 19→  });
 20→
 21→  it("can acquire an analysis job (1 unit)", () => {
 22→    const result = tryAcquire("analysis", "project-1", "user-1");
 23→    expect(result.acquired).toBe(true);
 24→    expect(result.jobId).toBeDefined();
 25→    expect(getStatus().usedUnits).toBe(1);
 26→  });
 27→
 28→  it("cannot exceed 3 units (compile + compile should fail on second)", () => {
 29→    const first = tryAcquire("compile", "project-1", "user-1");
 30→    expect(first.acquired).toBe(true);
 31→    expect(getStatus().usedUnits).toBe(2);
 32→
 33→    const second = tryAcquire("compile", "project-2", "user-2");
 34→    expect(second.acquired).toBe(false);
 35→    expect(second.position).toBe(1);
 36→  });
 37→
 38→  it("release frees units", () => {
 39→    const first = tryAcquire("compile", "project-1", "user-1");
 40→    expect(first.acquired).toBe(true);
 41→    expect(getStatus().usedUnits).toBe(2);
 42→
 43→    release(first.jobId!);
 44→    expect(getStatus().usedUnits).toBe(0);
 45→  });
 46→
 47→  it("queue works: acquire compile, try second compile -> queued at position 1", () => {
 48→    const first = tryAcquire("compile", "project-1", "user-1");
 49→    expect(first.acquired).toBe(true);
 50→
 51→    const second = tryAcquire("compile", "project-2", "user-2");
 52→    expect(second.acquired).toBe(false);
 53→    expect(second.position).toBe(1);
 54→    expect(second.estimatedWaitMs).toBe(30_000);
 55→  });
 56→
 57→  it("queue depth limit: fill queue to 5, next attempt has no position", () => {
 58→    const first = tryAcquire("compile", "project-0", "user-0");
 59→    expect(first.acquired).toBe(true);
 60→
 61→    for (let i = 1; i <= 5; i++) {
 62→      const queued = tryAcquire("compile", `project-${i}`, `user-${i}`);
 63→      expect(queued.acquired).toBe(false);
 64→      expect(queued.position).toBe(i);
 65→    }
 66→
 67→    const overflow = tryAcquire("compile", "project-overflow", "user-overflow");
 68→    expect(overflow.acquired).toBe(false);
 69→    expect(overflow.position).toBeUndefined();
 70→    expect(overflow.estimatedWaitMs).toBeUndefined();
 71→  });
 72→
 73→  it("getStatus returns correct state", () => {
 74→    expect(getStatus()).toEqual({
 75→      usedUnits: 0,
 76→      maxUnits: 3,
 77→      queueDepth: { compile: 0, analysis: 0 },
 78→    });
 79→
 80→    tryAcquire("compile", "project-1", "user-1");
 81→    expect(getStatus()).toEqual({
 82→      usedUnits: 2,
 83→      maxUnits: 3,
 84→      queueDepth: { compile: 0, analysis: 0 },
 85→    });
 86→
 87→    tryAcquire("analysis", "project-2", "user-2");
 88→    expect(getStatus()).toEqual({
 89→      usedUnits: 3,
 90→      maxUnits: 3,
 91→      queueDepth: { compile: 0, analysis: 0 },
 92→    });
 93→
 94→    tryAcquire("compile", "project-3", "user-3");
 95→    expect(getStatus()).toEqual({
 96→      usedUnits: 3,
 97→      maxUnits: 3,
 98→      queueDepth: { compile: 1, analysis: 0 },
 99→    });
100→  });
101→
102→  it("release promotes queued jobs", () => {
103→    const compile1 = tryAcquire("compile", "project-1", "user-1");
104→    const analysis1 = tryAcquire("analysis", "project-2", "user-2");
105→    expect(compile1.acquired).toBe(true);
106→    expect(analysis1.acquired).toBe(true);
107→    expect(getStatus().usedUnits).toBe(3);
108→
109→    const queued = tryAcquire("analysis", "project-3", "user-3");
110→    expect(queued.acquired).toBe(false);
111→    expect(queued.position).toBe(1);
112→
113→    release(analysis1.jobId!);
114→    expect(getStatus().usedUnits).toBe(3);
115→    expect(getStatus().queueDepth.analysis).toBe(0);
116→  });
117→
118→  // Per-user fairness tests
119→  it("enforces per-user limit: same user cannot hold >2 concurrent slots", () => {
120→    const a1 = tryAcquire("analysis", "project-1", "user-1");
121→    const a2 = tryAcquire("analysis", "project-2", "user-1");
122→    expect(a1.acquired).toBe(true);
123→    expect(a2.acquired).toBe(true);
124→
125→    // Third slot for same user should be queued
126→    const a3 = tryAcquire("analysis", "project-3", "user-1");
127→    expect(a3.acquired).toBe(false);
128→    expect(a3.reason).toContain("Per-user");
129→    expect(a3.position).toBe(1);
130→  });
131→
132→  it("different users can acquire slots independently up to analysis limit", () => {
133→    const a1 = tryAcquire("analysis", "project-1", "user-1");
134→    const a2 = tryAcquire("analysis", "project-2", "user-2");
135→    expect(a1.acquired).toBe(true);
136→    expect(a2.acquired).toBe(true);
137→    expect(getStatus().usedUnits).toBe(2);
138→
139→    // 3rd analysis queued — R Plumber can only handle 2 concurrent
140→    const a3 = tryAcquire("analysis", "project-3", "user-3");
141→    expect(a3.acquired).toBe(false);
142→    expect(a3.reason).toContain("Analysis concurrency limit");
143→    expect(a3.position).toBe(1);
144→  });
145→
146→  it("enforces max 2 concurrent analyses across all users", () => {
147→    // 2 analyses from different users — both succeed
148→    const a1 = tryAcquire("analysis", "p1", "user-1");
149→    const a2 = tryAcquire("analysis", "p2", "user-2");
150→    expect(a1.acquired).toBe(true);
151→    expect(a2.acquired).toBe(true);
152→
153→    // 3rd analysis queued even though global capacity has 1 unit left
154→    const a3 = tryAcquire("analysis", "p3", "user-3");
155→    expect(a3.acquired).toBe(false);
156→    expect(a3.position).toBe(1);
157→
158→    // But a compile job can still use the remaining unit — no, it needs 2 units
159→    // and only 1 is available, so it should also be queued
160→    const c1 = tryAcquire("compile", "p4", "user-4");
161→    expect(c1.acquired).toBe(false);
162→
163→    // Release one analysis → compile c1 promoted first (cost 2, 1+2=3 ≤ MAX)
164→    // a3 stays queued (capacity full)
165→    release(a1.jobId!);
166→    expect(getStatus().usedUnits).toBe(3); // a2(1) + promoted c1(2)
167→    expect(getStatus().queueDepth.compile).toBe(0);
168→    expect(getStatus().queueDepth.analysis).toBe(1); // a3 still waiting
169→
170→    // Release a2 → a3 can finally promote
171→    release(a2.jobId!);
172→    // usedUnits was 3, released 1 → 2 (c1). Analysis queue: a3 cost 1, 2+1=3 ≤ 3, analysis count=0 < 2
173→    expect(getStatus().usedUnits).toBe(3); // c1(2) + promoted a3(1)
174→    expect(getStatus().queueDepth.analysis).toBe(0);
175→  });
176→
177→  it("promotes different user from queue when per-user limit blocks first entry", () => {
178→    // Fill capacity: compile (2 units) + user-1 analysis (1 unit) = 3 units
179→    const c1 = tryAcquire("compile", "p0", "user-0");
180→    const a1 = tryAcquire("analysis", "p1", "user-1");
181→    expect(c1.acquired).toBe(true);
182→    expect(a1.acquired).toBe(true);
183→    expect(getStatus().usedUnits).toBe(3);
184→
185→    // Queue user-1 again (both per-user OK since they have 1, but global full)
186→    const a2 = tryAcquire("analysis", "p2", "user-1");
187→    expect(a2.acquired).toBe(false);
188→    expect(a2.position).toBe(1);
189→
190→    // Queue user-2
191→    const a3 = tryAcquire("analysis", "p3", "user-2");
192→    expect(a3.acquired).toBe(false);
193→    expect(a3.position).toBe(2);
194→
195→    // Release user-1's analysis → frees 1 unit → first in queue is user-1
196→    // user-1 is eligible (only had 1 slot, now 0), so they get promoted
197→    release(a1.jobId!);
198→    expect(getStatus().usedUnits).toBe(3); // compile(2) + promoted user-1(1)
199→    expect(getStatus().queueDepth.analysis).toBe(1); // user-2 still waiting
200→  });
201→
202→  it("defaults userId to anonymous when not provided", () => {
203→    const result = tryAcquire("analysis", "project-1");
204→    expect(result.acquired).toBe(true);
205→  });
206→});
