 1→import { NextRequest } from "next/server";
 2→import { readFile } from "fs/promises";
 3→import path from "path";
 4→import os from "os";
 5→import { getAuthenticatedUser } from "@/lib/api/auth";
 6→import { unauthorised, notFound, internalError } from "@/lib/api/errors";
 7→import { createAdminSupabaseClient } from "@/lib/supabase/admin";
 8→
 9→export async function GET(
10→  request: NextRequest,
11→  { params }: { params: Promise<{ id: string }> }
12→) {
13→  try {
14→    const authResult = await getAuthenticatedUser();
15→    if (!authResult) return unauthorised();
16→
17→    const { id } = await params;
18→    const supabase = createAdminSupabaseClient();
19→
20→    // Verify project belongs to user
21→    const { data: project, error: projectError } = await supabase
22→      .from("projects")
23→      .select("id")
24→      .eq("id", id)
25→      .eq("user_id", authResult.user.id)
26→      .single();
27→
28→    if (projectError || !project) {
29→      return notFound("Project not found");
30→    }
31→
32→    // Get latest successful compilation
33→    const { data: compilation, error } = await supabase
34→      .from("compilations")
35→      .select("pdf_url")
36→      .eq("project_id", id)
37→      .eq("status", "completed")
38→      .order("created_at", { ascending: false })
39→      .limit(1)
40→      .single();
41→
42→    if (error || !compilation?.pdf_url) {
43→      return notFound("No compiled PDF available — trigger a compilation first");
44→    }
45→
46→    const download = request.nextUrl.searchParams.get("download") === "1";
47→    const disposition = download
48→      ? `attachment; filename="thesis.pdf"`
49→      : "inline";
50→
51→    // In production, this would generate a signed R2 URL and redirect.
52→    // For local dev, read the PDF file from disk and stream it.
53→    // Try the stored path first, then deterministic fallback path.
54→    let pdfBytes: Uint8Array | null = null;
55→    for (const candidate of [
56→      compilation.pdf_url,
57→      path.join(os.tmpdir(), "apollo-pdfs", `${id}.pdf`),
58→    ]) {
59→      try {
60→        pdfBytes = await readFile(candidate);
61→        break;
62→      } catch {
63→        // Try next candidate
64→      }
65→    }
66→
67→    if (!pdfBytes) {
68→      return notFound("PDF file not found on disk — recompile to regenerate");
69→    }
70→
71→    return new Response(pdfBytes as unknown as BodyInit, {
72→      headers: {
73→        "Content-Type": "application/pdf",
74→        "Content-Disposition": disposition,
75→        "Cache-Control": "private, max-age=60",
76→      },
77→    });
78→  } catch (err) {
79→    console.error("Unexpected error in GET /api/projects/[id]/preview.pdf:", err);
80→    return internalError();
81→  }
82→}
